import React, { useState } from "react";
import {
    Wallet,
    isAddress,
    parseEther,
    formatEther,
    JsonRpcProvider,
    Contract,
    parseUnits,
    formatUnits,
    Interface,
} from "ethers";
import { QRCodeCanvas } from "qrcode.react";
import { Toaster, toast } from "react-hot-toast";
import { Wallet as WalletIcon, Search, CheckCircle, AlertCircle, Send, QrCode, Clipboard, Copy, History, Sparkles } from 'lucide-react';

// Setup the provider to connect to the Binance Smart Chain Testnet
const provider = new JsonRpcProvider("https://data-seed-prebsc-1-s1.binance.org:8545");

// ABI for standard ERC20 functions
const ERC20_ABI = [
    "function balanceOf(address owner) view returns (uint256)",
    "function transfer(address to, uint amount) returns (bool)",
];

// Configuration for supported tokens
const tokens = {
    BNB: { symbol: "BNB" },
    USDT: {
        symbol: "USDT",
        address: "0x787A697324dbA4AB965C58CD33c13ff5eeA6295F",
        decimals: 18,
    },
    USDC: {
        symbol: "USDC",
        address: "0x342e3aA1248AB77E319e3331C6fD3f1F2d4B36B1",
        decimals: 18,
    },
};

// A reusable Card component for consistent styling
const Card = ({ children, className, gradient = "from-white to-gray-50" }) => (
    <div className={`bg-gradient-to-br ${gradient} p-6 rounded-3xl shadow-xl border border-white/20 backdrop-blur-sm hover:shadow-2xl transition-all duration-300 ${className}`}>
        {children}
    </div>
);

// A reusable Button component
const Button = ({ onClick, children, className = '', disabled = false, variant = 'primary' }) => {
    const variants = {
        primary: 'bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600',
        secondary: 'bg-gradient-to-r from-blue-500 to-cyan-500 hover:from-blue-600 hover:to-cyan-600',
        success: 'bg-gradient-to-r from-green-500 to-emerald-500 hover:from-green-600 hover:to-emerald-600',
        warning: 'bg-gradient-to-r from-yellow-500 to-orange-500 hover:from-yellow-600 hover:to-orange-600',
        danger: 'bg-gradient-to-r from-red-500 to-rose-500 hover:from-red-600 hover:to-rose-600',
        info: 'bg-gradient-to-r from-indigo-500 to-purple-500 hover:from-indigo-600 hover:to-purple-600'
    };

    return (
        <button
            onClick={onClick}
            disabled={disabled}
            className={`w-full flex items-center justify-center gap-2 px-6 py-3 rounded-xl font-semibold text-white transition-all duration-300 transform ${variants[variant]} ${className} ${disabled ? 'opacity-50 cursor-not-allowed' : 'hover:scale-105 active:scale-95 shadow-lg hover:shadow-xl'}`}
        >
            {children}
        </button>
    );
};

export default function App() {
    const [walletName, setWalletName] = useState("");
    const [wallet, setWallet] = useState(null);
    const [searchName, setSearchName] = useState("");
    const [recipient, setRecipient] = useState("");
    const [amount, setAmount] = useState("");
    const [selectedToken, setSelectedToken] = useState("BNB");
    const [tokenBalance, setTokenBalance] = useState(null);
    const [txHash, setTxHash] = useState("");
    const [txInfo, setTxInfo] = useState(null);
    const [loading, setLoading] = useState(false);
    const [showQR, setShowQR] = useState(false);
    const [myTransactions, setMyTransactions] = useState([]);
    const [showHistory, setShowHistory] = useState(false);

    const fetchMyTransactions = async () => {
        try {
            const res = await fetch(`http://localhost:5000/api/transactions/${searchName || walletName}`);
            const data = await res.json();
            setMyTransactions(data);
        } catch (err) {
            console.error("Error fetching transaction history", err);
        }
    };

    const handleAction = async (action, successMessage) => {
        setLoading(true);
        try {
            await action();
            if (successMessage) toast.success(successMessage);
        } catch (err) {
            toast.error(err.message || "An error occurred.");
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    const generateWallet = () => handleAction(async () => {
        if (!walletName) throw new Error("Please enter a wallet name");
        const newWallet = Wallet.createRandom();
        setWallet(newWallet);
        const data = { name: walletName, address: newWallet.address, privateKey: newWallet.privateKey };
        await fetch("http://localhost:5000/api/wallet/save", {
            method: "POST", headers: { "Content-Type": "application/json" }, body: JSON.stringify(data)
        });
    }, `Wallet '${walletName}' created and saved!`);

    const fetchWallet = () => handleAction(async () => {
        if (!searchName) throw new Error("Enter wallet name to search");
        setWallet(null);
        const res = await fetch(`http://localhost:5000/api/wallet/${searchName}`);
        const data = await res.json();
        if (!data.privateKey) throw new Error("Wallet not found!");
        setWallet(new Wallet(data.privateKey, provider));
    }, "Wallet fetched successfully!");

    const getTokenBalance = () => handleAction(async () => {
        if (!wallet) return;
        let balance;
        if (selectedToken === "BNB") {
            balance = await provider.getBalance(wallet.address);
            setTokenBalance(formatEther(balance));
        } else {
            const token = tokens[selectedToken];
            const contract = new Contract(token.address, ERC20_ABI, provider);
            balance = await contract.balanceOf(wallet.address);
            setTokenBalance(formatUnits(balance, token.decimals));
        }
    }, "Balance updated!");

    const sendToken = () => handleAction(async () => {
        if (!wallet || !recipient || !amount) throw new Error("Missing input");
        if (!isAddress(recipient)) throw new Error("Invalid address");
        const signer = wallet.connect(provider);
        let tx;
        if (selectedToken === "BNB") {
            tx = await signer.sendTransaction({ to: recipient, value: parseEther(amount) });
        } else {
            const token = tokens[selectedToken];
            const contract = new Contract(token.address, ERC20_ABI, signer);
            tx = await contract.transfer(recipient, parseUnits(amount, token.decimals));
        }
        setTxHash(tx.hash);
        toast.success("Transaction sent! Verifying...");
        await tx.wait();
        await fetch("http://localhost:5000/api/transactions/save", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
                walletName: searchName || walletName,
                txHash: tx.hash,
                from: wallet.address,
                to: recipient,
                amount,
                tokenSymbol: selectedToken
            })
        });
        verifyTransaction(tx.hash);
    });

    const verifyTransaction = (hashToVerify = txHash) => handleAction(async () => {
        if (!hashToVerify) throw new Error("Enter transaction hash");
        const tx = await provider.getTransaction(hashToVerify);
        const receipt = await provider.getTransactionReceipt(hashToVerify);
        if (!tx || !receipt) throw new Error("Transaction not found or not yet mined.");
        const iface = new Interface(["event Transfer(address indexed from, address indexed to, uint256 value)"]);
        let decodedLogs = receipt.logs
            .map(log => {
                try {
                    const tokenDetails = Object.values(tokens).find(t => t.address && t.address.toLowerCase() === log.address.toLowerCase());
                    return tokenDetails ? { ...iface.parseLog(log), tokenDetails } : null;
                } catch { return null; }
            }).filter(log => log !== null);
        setTxInfo({ tx, receipt, decodedLogs });
    }, "Transaction verified!");

    const copyToClipboard = (text) => {
        navigator.clipboard.writeText(text);
        toast.success("Copied to clipboard!");
    };

    return (
        <div className="min-h-screen bg-gradient-to-br from-purple-400 via-pink-500 to-red-500 font-sans text-gray-800">
            <Toaster 
                position="top-center" 
                reverseOrder={false}
                toastOptions={{
                    style: {
                        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                        color: 'white',
                        borderRadius: '12px',
                        border: '1px solid rgba(255,255,255,0.2)'
                    }
                }}
            />

            <header className="bg-gradient-to-r from-indigo-600/80 via-purple-600/80 to-pink-600/80 backdrop-blur-md sticky top-0 z-10 border-b border-white/20">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6 flex items-center justify-center">
                    <h1 className="text-4xl font-bold text-white flex items-center gap-4">
                        <div className="p-2 bg-white/20 rounded-full">
                            <WalletIcon className="text-white" size={36} />
                        </div>
                        <span className="bg-gradient-to-r from-yellow-300 to-pink-300 bg-clip-text text-transparent">
                            Crypto Wallet Pro
                        </span>
                        <Sparkles className="text-yellow-300 animate-pulse" size={28} />
                    </h1>
                </div>
            </header>

            <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-10">
                {loading && (
                    <div className="fixed inset-0 bg-black/50 z-50 flex items-center justify-center backdrop-blur-sm">
                        <div className="bg-white/20 p-8 rounded-3xl">
                            <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-b-4 border-white mx-auto"></div>
                            <p className="text-white mt-4 font-semibold">Processing...</p>
                        </div>
                    </div>
                )}

                <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-10">
                    <Card gradient="from-blue-400 to-purple-500">
                        <h2 className="text-2xl font-bold mb-6 text-white flex items-center gap-2">
                            <WalletIcon size={24} />
                            Generate Wallet
                        </h2>
                        <input 
                            type="text" 
                            placeholder="Enter Wallet Name" 
                            className="w-full p-4 rounded-xl border-2 border-white/30 bg-white/20 backdrop-blur-sm focus:ring-4 focus:ring-white/50 focus:border-white/50 mb-6 text-white placeholder-white/70 font-medium" 
                            value={walletName} 
                            onChange={(e) => setWalletName(e.target.value)} 
                        />
                        <Button onClick={generateWallet} variant="info">
                            <WalletIcon size={20} /> Generate & Save
                        </Button>
                    </Card>

                    <Card gradient="from-green-400 to-blue-500">
                        <h2 className="text-2xl font-bold mb-6 text-white flex items-center gap-2">
                            <Search size={24} />
                            Fetch Wallet
                        </h2>
                        <input 
                            type="text" 
                            placeholder="Search Wallet Name" 
                            className="w-full p-4 rounded-xl border-2 border-white/30 bg-white/20 backdrop-blur-sm focus:ring-4 focus:ring-white/50 focus:border-white/50 mb-6 text-white placeholder-white/70 font-medium" 
                            value={searchName} 
                            onChange={(e) => setSearchName(e.target.value)} 
                        />
                        <Button onClick={fetchWallet} variant="success">
                            <Search size={20} /> Fetch Wallet
                        </Button>
                    </Card>

                    <Card gradient="from-yellow-400 to-red-500">
                        <h2 className="text-2xl font-bold mb-6 text-white flex items-center gap-2">
                            <CheckCircle size={24} />
                            Verify Transaction
                        </h2>
                        <input 
                            type="text" 
                            placeholder="Enter Tx Hash" 
                            className="w-full p-4 rounded-xl border-2 border-white/30 bg-white/20 backdrop-blur-sm focus:ring-4 focus:ring-white/50 focus:border-white/50 mb-6 text-white placeholder-white/70 font-medium" 
                            value={txHash} 
                            onChange={(e) => setTxHash(e.target.value)} 
                        />
                        <Button onClick={() => verifyTransaction()} variant="warning">
                            <CheckCircle size={20} /> Verify
                        </Button>
                    </Card>
                </div>

                {wallet && (
                    <Card className="mb-10" gradient="from-indigo-500 via-purple-500 to-pink-500">
                        <h2 className="text-3xl font-bold text-white mb-6 flex items-center gap-3">
                            <div className="p-2 bg-white/20 rounded-full">
                                <WalletIcon size={28} />
                            </div>
                            Wallet Details
                        </h2>

                        <div className="space-y-4 mb-8 text-white">
                            <div className="flex items-center gap-4 p-4 bg-white/10 rounded-xl backdrop-blur-sm">
                                <strong className="w-28">Address:</strong> 
                                <span className="font-mono bg-black/20 p-2 rounded-lg break-all flex-1">{wallet.address}</span>
                                <Copy 
                                    size={20} 
                                    className="cursor-pointer hover:text-yellow-300 transition-colors p-1 bg-white/20 rounded-lg" 
                                    onClick={() => copyToClipboard(wallet.address)} 
                                />
                            </div>
                            <div className="flex items-center gap-4 p-4 bg-white/10 rounded-xl backdrop-blur-sm">
                                <strong className="w-28">Private Key:</strong> 
                                <span className="font-mono bg-black/20 p-2 rounded-lg break-all flex-1">{wallet.privateKey.substring(0, 10)}...</span>
                                <Copy 
                                    size={20} 
                                    className="cursor-pointer hover:text-yellow-300 transition-colors p-1 bg-white/20 rounded-lg" 
                                    onClick={() => copyToClipboard(wallet.privateKey)} 
                                />
                            </div>
                        </div>

                        {showQR && (
                            <div className="flex justify-center my-8 p-6 bg-white rounded-2xl">
                                <QRCodeCanvas value={wallet.address} size={200} />
                            </div>
                        )}

                        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                            <Card gradient="from-cyan-400 to-blue-500" className="p-6">
                                <h3 className="text-xl font-bold mb-4 text-white flex items-center gap-2">
                                    <div className="p-1 bg-white/20 rounded-full">
                                        <WalletIcon size={20} />
                                    </div>
                                    Balance & History
                                </h3>
                                <div className="space-y-4">
                                    <div className="flex items-center gap-4">
                                        <select 
                                            className="border-2 border-white/30 p-3 rounded-xl bg-white/20 backdrop-blur-sm text-white font-medium flex-1"
                                            value={selectedToken} 
                                            onChange={(e) => {setSelectedToken(e.target.value); setTokenBalance(null);}}
                                        >
                                            {Object.keys(tokens).map(key => 
                                                <option key={key} value={key} className="text-gray-800">{tokens[key].symbol}</option>
                                            )}
                                        </select>
                                        <Button onClick={getTokenBalance} variant="secondary" className="w-auto px-6">
                                            Get Balance
                                        </Button>
                                    </div>

                                    <div className="p-4 bg-white/10 rounded-xl backdrop-blur-sm">
                                        <p className="text-2xl font-bold text-white">
                                            Balance: <span className="text-yellow-300">{tokenBalance ?? "--"} {selectedToken}</span>
                                        </p>
                                    </div>

                                    <div className="grid grid-cols-2 gap-4">
                                        <Button onClick={() => setShowQR(!showQR)} variant="warning" className="text-sm">
                                            <QrCode size={18} /> {showQR ? "Hide QR" : "Show QR"}
                                        </Button>
                                        <Button
                                            onClick={() => {
                                                setShowHistory(!showHistory);
                                                if (!showHistory) fetchMyTransactions();
                                            }}
                                            variant="info"
                                            className="text-sm"
                                        >
                                            <History size={18} /> {showHistory ? "Hide" : "Show"} History
                                        </Button>
                                    </div>
                                </div>
                            </Card>

                            <Card gradient="from-emerald-400 to-cyan-500" className="p-6">
                                <h3 className="text-xl font-bold mb-4 text-white flex items-center gap-2">
                                    <div className="p-1 bg-white/20 rounded-full">
                                        <Send size={20} />
                                    </div>
                                    Send Tokens
                                </h3>
                                <div className="space-y-4">
                                    <input 
                                        type="text" 
                                        placeholder="Recipient Address" 
                                        className="w-full p-4 rounded-xl border-2 border-white/30 bg-white/20 backdrop-blur-sm text-white placeholder-white/70 font-medium" 
                                        value={recipient} 
                                        onChange={(e) => setRecipient(e.target.value)} 
                                    />
                                    <input 
                                        type="text" 
                                        placeholder="Amount" 
                                        className="w-full p-4 rounded-xl border-2 border-white/30 bg-white/20 backdrop-blur-sm text-white placeholder-white/70 font-medium" 
                                        value={amount} 
                                        onChange={(e) => setAmount(e.target.value)} 
                                    />
                                    <Button onClick={sendToken} variant="success">
                                        <Send size={20} /> Send {selectedToken}
                                    </Button>
                                </div>
                            </Card>
                        </div>
                    </Card>
                )}

                {showHistory && (
                    <Card gradient="from-violet-500 to-purple-600">
                        <h2 className="text-2xl font-bold mb-6 text-white flex items-center gap-3">
                            <History size={24} />
                            Transaction History
                        </h2>
                        {myTransactions.length === 0 ? (
                            <div className="text-center py-12">
                                <div className="text-6xl mb-4">📊</div>
                                <p className="text-white/80 text-lg">No transactions found.</p>
                            </div>
                        ) : (
                            <div className="overflow-x-auto">
                                <table className="w-full text-sm text-left border border-white/20 rounded-xl overflow-hidden">
                                    <thead className="bg-white/20 backdrop-blur-sm">
                                        <tr className="text-white font-semibold">
                                            <th className="px-6 py-4">Type</th>
                                            <th className="px-6 py-4">Token</th>
                                            <th className="px-6 py-4">Amount</th>
                                            <th className="px-6 py-4">From</th>
                                            <th className="px-6 py-4">To</th>
                                            <th className="px-6 py-4">Time</th>
                                        </tr>
                                    </thead>
                                    <tbody className="text-white">
                                        {myTransactions.map((tx, index) => {
                                            const isSent = tx.from.toLowerCase() === wallet?.address?.toLowerCase();
                                            const time = new Date(tx.createdAt).toLocaleString();
                                            return (
                                                <tr key={tx.txHash} className={`${index % 2 === 0 ? 'bg-white/5' : 'bg-white/10'} hover:bg-white/20 transition-colors`}>
                                                    <td className={`px-6 py-4 font-semibold ${isSent ? "text-red-300" : "text-green-300"}`}>
                                                        {isSent ? "📤 Sent" : "📥 Received"}
                                                    </td>
                                                    <td className="px-6 py-4 font-mono">{tx.tokenSymbol}</td>
                                                    <td className="px-6 py-4 font-bold">{tx.amount}</td>
                                                    <td className="px-6 py-4 font-mono">{tx.from.slice(0, 8)}...</td>
                                                    <td className="px-6 py-4 font-mono">{tx.to.slice(0, 8)}...</td>
                                                    <td className="px-6 py-4">{time}</td>
                                                </tr>
                                            );
                                        })}
                                    </tbody>
                                </table>
                            </div>
                        )}
                    </Card>
                )}

                {txInfo && (
                    <Card gradient="from-teal-400 to-blue-500">
                        <h2 className="text-2xl font-bold mb-6 text-white flex items-center gap-3">
                            <CheckCircle size={24} />
                            Transaction Information
                        </h2>
                        <div className={`flex items-center gap-3 font-bold text-lg mb-6 p-4 rounded-xl ${txInfo.receipt.status === 1 ? 'bg-green-500/20 text-green-100' : 'bg-red-500/20 text-red-100'}`}>
                            {txInfo.receipt.status === 1 ? <CheckCircle size={24} /> : <AlertCircle size={24} />}
                            Status: {txInfo.receipt.status === 1 ? "✅ Success" : "❌ Failed"}
                        </div>
                        {txInfo.decodedLogs.length > 0 && (
                            <div>
                                <h3 className="text-xl font-semibold mb-4 text-white">Token Transfers:</h3>
                                <div className="space-y-4">
                                    {txInfo.decodedLogs.map((log, index) => (
                                        <div key={index} className="p-6 border border-white/20 rounded-xl bg-white/10 backdrop-blur-sm text-white">
                                            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                                <p><strong className="text-yellow-300">Token:</strong> {log.tokenDetails.symbol}</p>
                                                <p><strong className="text-yellow-300">Amount:</strong> {formatUnits(log.args.value, log.tokenDetails.decimals)}</p>
                                                <p className="font-mono break-all"><strong className="text-yellow-300">From:</strong> {log.args.from}</p>
                                                <p className="font-mono break-all"><strong className="text-yellow-300">To:</strong> {log.args.to}</p>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}
                    </Card>
                )}
            </main>
        </div>
    );
}
