import React, { useState } from "react";
import {
  Wallet,
  isAddress,
  JsonRpcProvider,
  Contract,
  formatEther,
  parseEther,
  formatUnits,
  parseUnits,
} from "ethers";
import { Interface } from "@ethersproject/abi";

const provider = new JsonRpcProvider("https://data-seed-prebsc-1-s1.binance.org:8545/");

const ERC20_ABI = [
  "function balanceOf(address) view returns (uint)",
  "function transfer(address to, uint amount) returns (bool)",
  "event Transfer(address indexed from, address indexed to, uint amount)",
];

const tokens = {
  BNB: { symbol: "BNB", decimals: 18 },
  USDT: {
    address: "", // üëà ADD YOUR USDT TESTNET ADDRESS
    symbol: "USDT",
    decimals: 18,
  },
  USDC: {
    address: "", // üëà ADD YOUR USDC TESTNET ADDRESS
    symbol: "USDC",
    decimals: 18,
  },
};

const ERC20_Interface = new Interface(ERC20_ABI);

function App() {
  const [walletName, setWalletName] = useState("");
  const [searchName, setSearchName] = useState("");
  const [wallet, setWallet] = useState(null);
  const [bnbBalance, setBnbBalance] = useState(null);
  const [txHash, setTxHash] = useState("");
  const [selectedToken, setSelectedToken] = useState("BNB");
  const [tokenBalance, setTokenBalance] = useState(null);
  const [recipient, setRecipient] = useState("");
  const [amount, setAmount] = useState("");

  const generateWallet = async () => {
    const wallet = Wallet.createRandom();
    const data = {
      name: walletName,
      address: wallet.address,
      privateKey: wallet.privateKey,
    };

    await fetch("http://localhost:5000/api/wallet", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(data),
    });

    alert("Wallet saved successfully!");
    setWallet(data);
  };

  const fetchWallet = async () => {
    const res = await fetch(`http://localhost:5000/api/wallet/${searchName}`);
    const data = await res.json();
    setWallet(data);

    const balance = await provider.getBalance(data.address);
    setBnbBalance(Number(formatEther(balance)).toFixed(6));
  };

  const verifyTransaction = async () => {
    try {
      const tx = await provider.getTransaction(txHash);
      const receipt = await provider.getTransactionReceipt(txHash);

      let tokenTransferValue = null;

      if (receipt && receipt.logs) {
        for (let log of receipt.logs) {
          try {
            const parsed = ERC20_Interface.parseLog(log);
            if (parsed.name === "Transfer") {
              tokenTransferValue = formatUnits(parsed.args.value, 6);
              break;
            }
          } catch (e) {}
        }
      }

      if (!tx) {
        alert("Transaction not found");
      } else {
        alert(`
From: ${tx.from}
To: ${tx.to}
Hash: ${tx.hash}
Amount: ${
          tokenTransferValue
            ? tokenTransferValue + " Tokens"
            : formatEther(tx.value) + " BNB"
        }`);
      }
    } catch (err) {
      alert("Invalid transaction hash");
    }
  };

  const getTokenBalance = async () => {
    if (!wallet) return alert("No wallet loaded");

    try {
      if (selectedToken === "BNB") {
        const balance = await provider.getBalance(wallet.address);
        setTokenBalance(Number(formatEther(balance)).toFixed(6));
      } else {
        const token = tokens[selectedToken];
        if (!token.address) throw new Error("Token address not set");
        const contract = new Contract(token.address, ERC20_ABI, provider);
        const balance = await contract.balanceOf(wallet.address);
        setTokenBalance(Number(formatUnits(balance, token.decimals)).toFixed(4));
      }
    } catch (err) {
      console.error(err);
      alert(`Error fetching ${selectedToken} balance`);
    }
  };

  const sendToken = async () => {
    if (!wallet) return alert("No wallet loaded");
    if (!isAddress(recipient)) return alert("Invalid recipient");

    try {
      const signer = new Wallet(wallet.privateKey, provider);
      const token = tokens[selectedToken];

      if (selectedToken === "BNB") {
        const tx = await signer.sendTransaction({
          to: recipient,
          value: parseEther(amount),
        });
        alert(`BNB sent!\nHash: ${tx.hash}`);
      } else {
        if (!token.address) throw new Error("Token address not set");
        const contract = new Contract(token.address, ERC20_ABI, signer);
        const tx = await contract.transfer(recipient, parseUnits(amount, token.decimals));
        alert(`${token.symbol} sent!\nHash: ${tx.hash}`);
      }
    } catch (err) {
      console.error(err);
      alert("Transaction failed");
    }
  };

  return (
    <div className="p-8 bg-gradient-to-br from-pink-100 via-yellow-100 to-blue-100 min-h-screen font-sans">
      <h1 className="text-5xl font-extrabold text-pink-700 mb-10 text-center drop-shadow-lg">
        üé® Goan Web3 Wallet üåü
      </h1>

      <div className="grid md:grid-cols-2 gap-6 mb-6">
        {/* Generate Wallet */}
        <div className="bg-white rounded-xl p-6 shadow-xl border-l-8 border-pink-400 hover:scale-[1.02] transition-all">
          <h2 className="font-bold text-xl mb-3 text-pink-600">üéÅ Generate Wallet</h2>
          <div className="flex gap-2">
            <input
              type="text"
              placeholder="Wallet Name"
              className="p-2 rounded border-2 border-pink-300 flex-1"
              value={walletName}
              onChange={(e) => setWalletName(e.target.value)}
            />
            <button
              onClick={generateWallet}
              className="bg-pink-600 text-white px-4 py-2 rounded hover:bg-pink-700"
            >
              Create
            </button>
          </div>
        </div>

        {/* Fetch Wallet */}
        <div className="bg-white rounded-xl p-6 shadow-xl border-l-8 border-yellow-400 hover:scale-[1.02] transition-all">
          <h2 className="font-bold text-xl mb-3 text-yellow-600">üì• Fetch Wallet</h2>
          <div className="flex gap-2">
            <input
              type="text"
              placeholder="Search by Name"
              className="p-2 rounded border-2 border-yellow-300 flex-1"
              value={searchName}
              onChange={(e) => setSearchName(e.target.value)}
            />
            <button
              onClick={fetchWallet}
              className="bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-600"
            >
              Fetch
            </button>
          </div>
        </div>
      </div>

      {/* Verify Tx */}
      <div className="bg-white rounded-xl p-6 shadow-xl border-l-8 border-indigo-400 mb-6 hover:scale-[1.01] transition-all">
        <h2 className="text-xl font-bold mb-4 text-indigo-700">üîç Verify Transaction</h2>
        <div className="flex gap-2">
          <input
            type="text"
            placeholder="Tx Hash"
            className="p-2 rounded border-2 border-indigo-300 flex-1"
            value={txHash}
            onChange={(e) => setTxHash(e.target.value)}
          />
          <button
            onClick={verifyTransaction}
            className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700"
          >
            Verify
          </button>
        </div>
      </div>

      {/* Wallet Info & Send */}
      {wallet && (
        <div className="bg-white rounded-xl p-6 shadow-xl border-l-8 border-green-400 mb-6 hover:scale-[1.01] transition-all">
          <p className="mb-1"><strong>Address:</strong> {wallet.address}</p>
          <p className="mb-1"><strong>Private Key:</strong> {wallet.privateKey}</p>
          <p className="mb-4"><strong>BNB:</strong> {bnbBalance}</p>

          <div className="flex gap-3 items-center mb-3">
            <label className="font-semibold text-gray-800">Token:</label>
            <select
              className="border-2 border-green-300 p-2 rounded"
              value={selectedToken}
              onChange={(e) => setSelectedToken(e.target.value)}
            >
              {Object.keys(tokens).map((key) => (
                <option key={key} value={key}>
                  {tokens[key].symbol}
                </option>
              ))}
            </select>
            <button
              onClick={getTokenBalance}
              className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
            >
              Check Balance
            </button>
          </div>

          <p className="mb-4"><strong>Token Balance:</strong> {tokenBalance ?? "--"}</p>

          <div className="flex gap-2">
            <input
              type="text"
              placeholder="Recipient"
              className="p-2 rounded border-2 border-green-300 flex-1"
              value={recipient}
              onChange={(e) => setRecipient(e.target.value)}
            />
            <input
              type="text"
              placeholder="Amount"
              className="p-2 rounded border-2 border-green-300 flex-1"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
            />
            <button
              onClick={sendToken}
              className={`px-4 py-2 rounded text-white ${
                selectedToken === "BNB"
                  ? "bg-purple-600 hover:bg-purple-700"
                  : "bg-teal-600 hover:bg-teal-700"
              }`}
            >
              Send {selectedToken}
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
