// ...[imports remain unchanged]
import React, { useState, useEffect, useRef } from "react";
import {
  Wallet,
  isAddress,
  parseEther,
  formatEther,
  JsonRpcProvider,
  Contract,
  parseUnits,
  formatUnits,
} from "ethers";
import { QRCodeCanvas } from "qrcode.react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const ERC20_ABI = [
  "function balanceOf(address) view returns (uint256)",
  "function transfer(address to, uint256 value) returns (bool)",
  "function decimals() view returns (uint8)",
  "function symbol() view returns (string)",
];

const provider = new JsonRpcProvider("https://bsc-testnet-dataseed.bnbchain.org");

const TOKEN_ADDRESSES = {
  USDT: "0x787A697324dbA4AB965C58CD33c13ff5eeA6295F",
  USDC: "0x342e3aA1248AB77E319e3331C6fD3f1F2d4B36B1",
};

function App() {
  // [All existing states remain unchanged]
  const [ledger, setLedger] = useState([]);
  // ...other state definitions

  // üì• Fetch ledger txs
  const fetchLedger = async () => {
    try {
      const res = await fetch("http://localhost:5001/api/transactions");
      const data = await res.json();
      setLedger(data);
    } catch {
      console.error("‚ùå Could not fetch ledger");
    }
  };

  useEffect(() => {
    fetchLedger();
  }, []);

  // üßæ Save tx to DB
  const saveTxToDB = async (txDetails) => {
    try {
      await fetch("http://localhost:5001/api/transaction", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(txDetails),
      });
      fetchLedger();
    } catch (err) {
      console.error("‚ùå Failed to save tx");
    }
  };

  // üöÄ Update: After tx.wait()
  const sendTokens = async () => {
    if (!walletData?.privateKey || !isAddress(recipient) || !amount || parseFloat(amount) <= 0)
      return notify("‚ùå Invalid inputs", "error");

    setLoader("sending", true);
    try {
      const wallet = new Wallet(walletData.privateKey, provider);
      const bnbBalance = await provider.getBalance(wallet.address);
      const bnbInEth = parseFloat(formatEther(bnbBalance));
      if (bnbInEth < 0.001) return notify("‚ùå Not enough BNB for gas", "error");

      let tx;
      if (tokenType === "BNB") {
        tx = await wallet.sendTransaction({ to: recipient, value: parseEther(amount) });
      } else {
        const token = new Contract(TOKEN_ADDRESSES[tokenType], ERC20_ABI, wallet);
        const decimals = await token.decimals();
        tx = await token.transfer(recipient, parseUnits(amount, decimals));
      }

      await tx.wait();
      const receipt = await provider.getTransactionReceipt(tx.hash);

      const txDetails = {
        hash: tx.hash,
        from: tx.from,
        to: tx.to,
        blockNumber: receipt.blockNumber,
        status: receipt.status,
        gasUsed: receipt.gasUsed.toString(),
        value: tx.value?.toString() || "0",
      };

      await saveTxToDB(txDetails);

      notify(`‚úÖ ${tokenType} Sent!\nTx: ${tx.hash}`, "success");
      navigator.clipboard.writeText(tx.hash);
      setAmount("");
      fetchAllBalances(walletData.privateKey, walletData.address);
    } catch (e) {
      console.error(e);
      notify("‚ùå Transaction failed", "error");
    } finally {
      setLoader("sending", false);
    }
  };

  // ...[rest of your UI and logic remains the same]

  return (
    <div style={styles.container}>
      <ToastContainer position="top-right" />
      <h1 style={styles.title}>ü¶ú Goan Wallet</h1>
      {/* Your existing UI code for wallet, balances, QR, etc. remains unchanged */}

      {/* üìò Transaction Ledger */}
      {ledger.length > 0 && (
        <div style={styles.walletBox}>
          <h3>üìò Transaction Ledger (Last {ledger.length})</h3>
          <ul style={{ fontSize: "13px", paddingLeft: "0", listStyle: "none" }}>
            {ledger.map((tx) => (
              <li key={tx.hash} style={{ marginBottom: "10px" }}>
                üîó <a href={`https://testnet.bscscan.com/tx/${tx.hash}`} target="_blank" rel="noreferrer" style={{ color: "#3b82f6" }}>{tx.hash.slice(0, 20)}...</a><br />
                From: {tx.from.slice(0, 10)}... ‚Üí To: {tx.to.slice(0, 10)}...<br />
                Value: {formatEther(tx.value)} BNB | Status: {tx.status === 1 ? "‚úÖ" : "‚ùå"}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

// ...[styles remain unchanged]
export default App;
