import React, { useState, useEffect, useRef } from "react";
import {
  Wallet,
  isAddress,
  parseEther,
  formatEther,
  JsonRpcProvider,
  Contract,
  parseUnits,
  formatUnits,
} from "ethers";
import { QRCodeCanvas } from "qrcode.react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const ERC20_ABI = [
  "function balanceOf(address) view returns (uint256)",
  "function transfer(address to, uint256 value) returns (bool)",
  "function decimals() view returns (uint8)",
  "function symbol() view returns (string)",
];

const provider = new JsonRpcProvider("https://bsc-testnet-dataseed.bnbchain.org");

const TOKEN_ADDRESSES = {
  USDT: "0x787A697324dbA4AB965C58CD33c13ff5eeA6295F",
  USDC: "0x342e3aA1248AB77E319e3331C6fD3f1F2d4B36B1",
};

function App() {
  const [walletData, setWalletData] = useState(null);
  const [walletName, setWalletName] = useState("");
  const [searchName, setSearchName] = useState("");
  const [recipient, setRecipient] = useState("");
  const [amount, setAmount] = useState("");
  const [tokenType, setTokenType] = useState("BNB");
  const [balances, setBalances] = useState({ BNB: "Loading...", USDT: "N/A", USDC: "N/A" });
  const [txHashInput, setTxHashInput] = useState("");
  const [txDetails, setTxDetails] = useState(null);
  const [ledger, setLedger] = useState([]); // <-- NEW: State for ledger
  const [loading, setLoading] = useState({});
  const [showPrivate, setShowPrivate] = useState(false);
  const [showQR, setShowQR] = useState(true);
  const qrRef = useRef();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [isLogin, setIsLogin] = useState(true);

  const setLoader = (key, value) => setLoading((prev) => ({ ...prev, [key]: value }));

  const notify = (msg, type = "info") => toast[type](msg);

  const handleAuth = async (e) => {
    e.preventDefault();
    const endpoint = isLogin ? "login" : "register";
    try {
      const res = await fetch(`http://localhost:5001/api/auth/${endpoint}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });
      const data = await res.json();
      if (res.ok) {
        if (isLogin) {
          localStorage.setItem("token", data.token);
          setToken(data.token);
        }
        notify(`‚úÖ ${isLogin ? 'Logged in' : 'Registered'} successfully!`, "success");
      } else {
        notify(`‚ùå ${data.error || 'Authentication failed'}`, "error");
      }
    } catch (error) {
      notify("‚ùå Authentication error", "error");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setToken(null);
    setWalletData(null);
    setLedger([]);
  };


  const generateAndSaveWallet = async () => {
    if (!walletName.trim()) return notify("Enter a wallet name", "error");
    setLoader("creating", true);
    const wallet = Wallet.createRandom();
    const newWallet = {
      name: walletName,
      address: wallet.address,
      privateKey: wallet.privateKey,
      mnemonic: wallet.mnemonic.phrase,
    };

    try {
      const res = await fetch("http://localhost:5001/api/wallet", {
        method: "POST",
        headers: { "Content-Type": "application/json", "Authorization": `Bearer ${token}` },
        body: JSON.stringify(newWallet),
      });

      const data = await res.json();
      if (res.ok) {
        notify("‚úÖ Wallet saved!", "success");
        setWalletName("");
      } else {
        notify(`‚ùå ${data.error || 'Save failed'}`, "error");
      }
    } catch {
      notify("‚ùå Save error", "error");
    } finally {
      setLoader("creating", false);
    }
  };

  const fetchWalletByName = async () => {
    if (!searchName.trim()) return notify("Enter wallet name to fetch", "error");
    setLoader("fetching", true);
    try {
      const res = await fetch(`http://localhost:5001/api/wallet/${searchName}`, {
        headers: { "Authorization": `Bearer ${token}` }
      });
      const data = await res.json();
      if (data.error) {
        notify("‚ùå Wallet not found", "error");
        setWalletData(null);
        setLedger([]); // Clear ledger if wallet not found
      } else {
        setWalletData(data);
        fetchAllBalances(data.privateKey, data.address);
        fetchLedger(data.address); // <-- NEW: Fetch ledger for this wallet
      }
    } catch {
      notify("‚ùå Error fetching wallet", "error");
    } finally {
      setLoader("fetching", false);
    }
  };

  const fetchAllBalances = async (privateKey, address) => {
    setLoader("balance", true);
    try {
      const wallet = new Wallet(privateKey, provider);
      const bnb = await provider.getBalance(wallet.address);

      const tokenBalances = await Promise.all(
        Object.entries(TOKEN_ADDRESSES).map(async ([symbol, addr]) => {
          const tokenContract = new Contract(addr, ERC20_ABI, wallet);
          const decimals = await tokenContract.decimals();
          const bal = await tokenContract.balanceOf(wallet.address);
          return [symbol, formatUnits(bal, decimals)];
        })
      );

      const all = Object.fromEntries(tokenBalances);
      setBalances({
        BNB: formatEther(bnb),
        ...all,
      });
    } catch (e) {
      console.error(e);
      setBalances({ BNB: "Error", USDT: "N/A", USDC: "N/A" });
    } finally {
      setLoader("balance", false);
    }
  };

  const sendTokens = async () => {
    if (!walletData?.privateKey || !isAddress(recipient) || !amount || parseFloat(amount) <= 0)
      return notify("‚ùå Invalid inputs", "error");

    setLoader("sending", true);
    try {
      const wallet = new Wallet(walletData.privateKey, provider);
      const bnbBalance = await provider.getBalance(wallet.address);
      if (parseFloat(formatEther(bnbBalance)) < 0.001) return notify("‚ùå Not enough BNB for gas", "error");

      let tx;
      if (tokenType === "BNB") {
        tx = await wallet.sendTransaction({ to: recipient, value: parseEther(amount) });
      } else {
        const tokenContract = new Contract(TOKEN_ADDRESSES[tokenType], ERC20_ABI, wallet);
        const decimals = await tokenContract.decimals();
        tx = await tokenContract.transfer(recipient, parseUnits(amount, decimals));
      }

      await tx.wait();
      notify(`‚úÖ ${tokenType} Sent!`, "success");
      navigator.clipboard.writeText(tx.hash);

      // --- NEW: Log transaction after it's confirmed ---
      logTransaction(tx.hash, walletData.address, recipient, amount, tokenType);

      setRecipient("");
      setAmount("");
      fetchAllBalances(walletData.privateKey, walletData.address);
    } catch (e) {
      console.error(e);
      notify("‚ùå Transaction failed", "error");
    } finally {
      setLoader("sending", false);
    }
  };

  // --- NEW: Function to save a transaction to the backend ---
  const logTransaction = async (hash, from, to, amount, token) => {
    try {
      await fetch("http://localhost:5001/api/ledger", {
        method: "POST",
        headers: { "Content-Type": "application/json", "Authorization": `Bearer ${localStorage.getItem("token")}` },
        body: JSON.stringify({
          walletAddress: from,
          hash,
          from,
          to,
          amount,
          token,
        }),
      });
      fetchLedger(from); // Refresh ledger UI after logging
    } catch (error) {
      console.error("Failed to log transaction:", error);
      notify("Could not save Tx to ledger", "warn");
    }
  };

  // --- NEW: Function to fetch transaction history for a wallet ---
  const fetchLedger = async (address) => {
    setLoader("ledger", true);
    try {
      const res = await fetch(`http://localhost:5001/api/ledger/${address}`, {
        headers: { "Authorization": `Bearer ${token}` }
      });
      const data = await res.json();
      setLedger(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error("Failed to fetch ledger:", error);
      setLedger([]);
    } finally {
      setLoader("ledger", false);
    }
  };

  const fetchTransactionDetails = async () => {
    if (!txHashInput) return;
    setLoader("tx", true);
    try {
      const tx = await provider.getTransaction(txHashInput);
      const receipt = await provider.getTransactionReceipt(txHashInput);
      setTxDetails({ ...tx, ...receipt });
    } catch (e) {
      notify("‚ùå Invalid or failed to fetch", "error");
    } finally {
      setLoader("tx", false);
    }
  };

  const copyAddress = () => {
    if (walletData?.address) {
      navigator.clipboard.writeText(walletData.address);
      notify("üìã Address copied", "info");
    }
  };

  const downloadQR = () => {
    const canvas = qrRef.current.querySelector("canvas");
    const pngUrl = canvas.toDataURL("image/png");
    const a = document.createElement("a");
    a.href = pngUrl;
    a.download = `${walletData.name}_qr.png`;
    a.click();
  };

  return (
    <div style={styles.container}>
      <ToastContainer position="top-right" autoClose={3000} hideProgressBar={false} />
      <h1 style={styles.title}>ü¶ú Goan Wallet</h1>

      {!token ? (
        <section style={styles.section}>
          <form onSubmit={handleAuth}>
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              style={styles.input}
              required
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={styles.input}
              required
            />
            <button type="submit" style={styles.buttonBlue}>
              {isLogin ? "Login" : "Register"}
            </button>
            <button type="button" onClick={() => setIsLogin(!isLogin)} style={styles.toggle}>
              {isLogin ? "Need to register?" : "Have an account?"}
            </button>
          </form>
        </section>
      ) : (
        <>
          <button onClick={handleLogout} style={styles.buttonOrange}>Logout</button>
          <section style={styles.section}>
            <input
              placeholder="üñä Create wallet name"
              value={walletName}
              onChange={(e) => setWalletName(e.target.value)}
              style={styles.input}
            />
            <button onClick={generateAndSaveWallet} style={styles.buttonGreen}>
              {loading.creating ? "‚è≥ Saving..." : "‚úÖ Generate & Save"}
            </button>
          </section>

          <section style={styles.section}>
            <input
              placeholder="üîç Load wallet by name"
              value={searchName}
              onChange={(e) => setSearchName(e.target.value)}
              style={styles.input}
            />
            <button onClick={fetchWalletByName} style={styles.buttonBlue}>
              {loading.fetching ? "‚è≥ Loading..." : "üì• Load Wallet"}
            </button>
          </section>

          {walletData && (
            <div style={styles.walletBox}>
              <h2>{walletData.name}</h2>
              <p onClick={copyAddress} style={{ cursor: "pointer" }}>
                <strong>üè¶ Address:</strong> {walletData.address} üìã
              </p>
              <p>
                <strong>üîë Private Key:</strong>{" "}
                {showPrivate ? walletData.privateKey : "‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"}
                <button onClick={() => setShowPrivate(!showPrivate)} style={styles.toggle}>
                  {showPrivate ? "üôà Hide" : "üëÅ Show"}
                </button>
              </p>

              <h3>üìä Balances</h3>
              <ul style={{ listStyle: "none", padding: 0 }}>
                {Object.entries(balances).map(([symbol, value]) => (
                  <li key={symbol}>
                    {symbol === "BNB" ? "üí∞" : symbol === "USDT" ? "üíµ" : "üí∏"} {symbol}:{" "}
                    <strong>{value}</strong>
                  </li>
                ))}
              </ul>

              <div style={{ marginTop: "20px" }}>
                <button onClick={() => setShowQR(!showQR)} style={styles.toggle}>
                  {showQR ? "üôà Hide QR" : "üì± Show QR"}
                </button>
                {showQR && (
                  <div ref={qrRef} style={{ marginTop: "10px" }}>
                    <QRCodeCanvas value={walletData.address} size={130} bgColor="#ffffff" fgColor="#000000" />
                    <p>üì± Scan to pay</p>
                    <button onClick={downloadQR} style={styles.buttonBlue}>‚¨áÔ∏è Download QR</button>
                  </div>
                )}
              </div>

              <h3>üöÄ Send Tokens</h3>
              <select value={tokenType} onChange={(e) => setTokenType(e.target.value)} style={styles.input}>
                <option value="BNB">BNB</option>
                <option value="USDT">USDT</option>
                <option value="USDC">USDC</option>
              </select>
              <input
                placeholder="üì§ Recipient"
                value={recipient}
                onChange={(e) => setRecipient(e.target.value)}
                style={styles.input}
              />
              <input
                placeholder="üí∞ Amount"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
                style={styles.input}
              />
              <button onClick={sendTokens} style={styles.buttonOrange}>
                {loading.sending ? "‚è≥ Sending..." : "üöÄ Send"}
              </button>

              {/* --- NEW: Transaction Ledger UI --- */}
              <h3 style={{ marginTop: "20px" }}>üìú Transaction Ledger</h3>
              {loading.ledger ? <p>Loading ledger...</p> : (
                <div style={styles.ledgerContainer}>
                  {ledger.length > 0 ? (
                    ledger.map((tx) => (
                      <div key={tx.hash} style={styles.ledgerTx}>
                        <p><strong>Hash:</strong> <a href={`https://testnet.bscscan.com/tx/${tx.hash}`} target="_blank" rel="noopener noreferrer">{`${tx.hash.substring(0, 10)}...`}</a></p>
                        <p><strong>To:</strong> {`${tx.to.substring(0, 10)}...`}</p>
                        <p><strong>Amount:</strong> {tx.amount} {tx.token}</p>
                        <p><strong>Date:</strong> {new Date(tx.createdAt).toLocaleString()}</p>
                      </div>
                    ))
                  ) : (
                    <p>No transactions recorded in the ledger.</p>
                  )}
                </div>
              )}

              <h4 style={{ marginTop: "20px" }}>üì¶ Transaction Info</h4>
              <input
                placeholder="üîç Tx Hash"
                value={txHashInput}
                onChange={(e) => setTxHashInput(e.target.value)}
                style={styles.input}
              />
              <button onClick={fetchTransactionDetails} style={styles.buttonPurple}>
                {loading.tx ? "‚è≥ Fetching..." : "üîç View Tx Info"}
              </button>

              {txDetails && (
                <div style={styles.txBox}>
                  <p><strong>From:</strong> {txDetails.from}</p>
                  <p><strong>To:</strong> {txDetails.to}</p>
                  <p><strong>Hash:</strong> <a href={`https://testnet.bscscan.com/tx/${txDetails.hash}`} target="_blank" rel="noopener noreferrer">{txDetails.hash}</a></p>
                  <p><strong>Block:</strong> {txDetails.blockNumber}</p>
                  <p><strong>Status:</strong> {txDetails.status === 1 ? "‚úÖ Success" : "‚ùå Failed"}</p>
                </div>
              )}
            </div>
          )}
        </>
      )}
    </div>
  );
}

const styles = {
