    padding: "12px 24px",
    backgroundColor: "#9333ea",
    borderRadius: "10px",
    color: "#fff",
    fontWeight: "bold",
    fontSize: "16px",
    cursor: "pointer",
  },
  walletBox: {
    backgroundColor: "#1e293b",
    padding: "30px",
    marginTop: "30px",
    borderRadius: "12px",
    maxWidth: "700px",
    marginInline: "auto",
    textAlign: "left",
