import React, { useState } from "react";
import {
  Wallet,
  isAddress,
  parseEther,
  JsonRpcProvider,
  formatEther,
} from "ethers";

function App() {
  const [walletData, setWalletData] = useState(null);
  const [walletName, setWalletName] = useState("");
  const [searchName, setSearchName] = useState("");
  const [recipient, setRecipient] = useState("");
  const [amount, setAmount] = useState("");
  const [balance, setBalance] = useState(null);

  const provider = new JsonRpcProvider("https://bsc-testnet-dataseed.bnbchain.org");

  const generateAndSaveWallet = async () => {
    if (!walletName.trim()) {
      alert("Please enter a name for the wallet.");
      return;
    }

    const wallet = Wallet.createRandom();
    const newWallet = {
      name: walletName,
      address: wallet.address,
      privateKey: wallet.privateKey,
      mnemonic: wallet.mnemonic.phrase,
    };

    try {
      const res = await fetch("http://localhost:5001/api/wallet", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newWallet),
      });

      if (res.ok) {
        alert("✅ Wallet generated and saved!");
        setWalletName("");
      } else {
        alert("❌ Failed to save wallet.");
      }
    } catch (error) {
      console.error("Error saving wallet:", error);
      alert("❌ Something went wrong.");
    }
  };

  const fetchWalletByName = async () => {
    if (!searchName.trim()) {
      alert("Please enter a wallet name to fetch.");
      return;
    }

    try {
      const res = await fetch(`http://localhost:5001/api/wallet/${searchName}`);
      const data = await res.json();

      if (data.error) {
        alert("❌ Wallet not found.");
        setWalletData(null);
        setBalance(null);
      } else {
        setWalletData(data);
        fetchBalance(data.privateKey);
      }
    } catch (error) {
      console.error("Error fetching wallet:", error);
      alert("❌ Error fetching wallet.");
    }
  };

  const fetchBalance = async (privateKey) => {
    try {
      const wallet = new Wallet(privateKey, provider);
      const bal = await provider.getBalance(wallet.address);
      setBalance(formatEther(bal));
    } catch (error) {
      console.error("Error fetching balance:", error);
      setBalance(null);
    }
  };

  const sendTokens = async () => {
    if (!walletData?.privateKey) {
      alert("Please load a wallet first.");
      return;
    }

    if (!isAddress(recipient)) {
      alert("Invalid recipient address.");
      return;
    }

    if (!amount || isNaN(amount) || parseFloat(amount) <= 0) {
      alert("Invalid amount.");
      return;
    }

    try {
      const wallet = new Wallet(walletData.privateKey, provider);
      const tx = await wallet.sendTransaction({
        to: recipient,
        value: parseEther(amount),
      });

      await tx.wait();
      alert(`✅ Transaction successful!\nHash: ${tx.hash}`);
      setAmount("");
      fetchBalance(walletData.privateKey); // Refresh balance
    } catch (error) {
      console.error("Transaction error:", error);
      alert("❌ Transaction failed.");
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>🌈 Goan Wallet <span style={{ fontSize: "1.2rem" }}>🤑💳</span></h1>

      <section style={styles.section}>
        <input
          type="text"
          placeholder="🎨 Name to create wallet"
          value={walletName}
          onChange={(e) => setWalletName(e.target.value)}
          style={styles.input}
        />
        <button onClick={generateAndSaveWallet} style={styles.buttonPrimary}>
          🚀 Generate & Save
        </button>
      </section>

      <section style={styles.section}>
        <input
          type="text"
          placeholder="🔍 Name to fetch wallet"
          value={searchName}
          onChange={(e) => setSearchName(e.target.value)}
          style={styles.input}
        />
        <button onClick={fetchWalletByName} style={styles.buttonSecondary}>
          🔓 Fetch Wallet
        </button>
      </section>

      {walletData && (
        <div style={styles.walletBox}>
          <h3>🔐 Wallet Details</h3>
          <p><strong>👤 Name:</strong> {walletData.name}</p>
          <p><strong>🏦 Address:</strong> {walletData.address}</p>
          <p><strong>🔑 Private Key:</strong> {walletData.privateKey}</p>
          <p><strong>📜 Mnemonic:</strong> {walletData.mnemonic}</p>
          <p><strong>💰 Balance:</strong> {balance !== null ? `${balance} BNB` : "Loading..."}</p>

          <div style={{ marginTop: "20px" }}>
            <h4>📤 Send BNB</h4>
            <input
              type="text"
              placeholder="➡️ Recipient Address"
              value={recipient}
              onChange={(e) => setRecipient(e.target.value)}
              style={styles.input}
            />
            <input
              type="text"
              placeholder="💸 Amount"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              style={styles.input}
            />
            <button onClick={sendTokens} style={styles.buttonTertiary}>
              ✈️ Send Tokens
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

const styles = {
  container: {
    minHeight: "100vh",
    background: "linear-gradient(135deg, #a855f7, #3b82f6)",
    padding: "40px 20px",
    fontFamily: "'Segoe UI', sans-serif",
    color: "#fff",
    textAlign: "center",
  },
  heading: {
    fontSize: "2.5rem",
    marginBottom: "30px",
    fontWeight: "bold",
  },
  section: {
    marginBottom: "25px",
  },
  input: {
    padding: "12px 18px",
    margin: "10px",
    width: "65%",
    maxWidth: "400px",
    borderRadius: "10px",
    border: "none",
    outline: "none",
    fontSize: "16px",
    backgroundColor: "#fef9c3",
    color: "#111",
    boxShadow: "0px 2px 8px rgba(0,0,0,0.1)",
  },
  buttonPrimary: {
    padding: "12px 24px",
    backgroundColor: "#22c55e",
    border: "none",
    borderRadius: "10px",
    color: "#fff",
    fontWeight: "bold",
    cursor: "pointer",
    fontSize: "16px",
  },
  buttonSecondary: {
    padding: "12px 24px",
    backgroundColor: "#ec4899",
    border: "none",
    borderRadius: "10px",
    color: "#fff",
    fontWeight: "bold",
    cursor: "pointer",
    fontSize: "16px",
  },
  buttonTertiary: {
    marginTop: "10px",
    padding: "12px 24px",
    backgroundColor: "#f59e0b",
    border: "none",
    borderRadius: "10px",
    color: "#fff",
    fontWeight: "bold",
    cursor: "pointer",
    fontSize: "16px",
  },
  walletBox: {
    marginTop: "40px",
    padding: "30px",
    borderRadius: "20px",
    background: "rgba(255,255,255,0.15)",
    backdropFilter: "blur(10px)",
    boxShadow: "0px 8px 32px rgba(0,0,0,0.2)",
    textAlign: "left",
    maxWidth: "600px",
    marginInline: "auto",
    lineHeight: "1.8",
    color: "#fff",
  },
};

export default App;
