import React, { useState } from "react";
import { ethers } from "ethers";
import erc20Abi from "./erc20Abi.json"; // Ensure this file has the ERC20 ABI

const App = () => {
  const [walletName, setWalletName] = useState("");
  const [wallets, setWallets] = useState({});
  const [currentWallet, setCurrentWallet] = useState(null);
  const [bnbBalance, setBnbBalance] = useState("0");
  const [tokenAddress, setTokenAddress] = useState("");
  const [tokenBalance, setTokenBalance] = useState("0");
  const [recipient, setRecipient] = useState("");
  const [amount, setAmount] = useState("");

  const provider = new ethers.JsonRpcProvider("https://data-seed-prebsc-1-s1.binance.org:8545/");

  const generateWallet = async () => {
    const wallet = ethers.Wallet.createRandom();
    const connectedWallet = wallet.connect(provider);
    const name = walletName || "wallet" + Object.keys(wallets).length;
    const updated = { ...wallets, [name]: connectedWallet };
    setWallets(updated);
    setCurrentWallet({ name, wallet: connectedWallet });
    alert("Wallet generated!");
  };

  const loadWallet = () => {
    const w = wallets[walletName];
    if (!w) return alert("Wallet not found!");
    setCurrentWallet({ name: walletName, wallet: w });
  };

  const refreshBNB = async () => {
    const bal = await provider.getBalance(currentWallet.wallet.address);
    setBnbBalance(ethers.formatEther(bal));
  };

  const fetchTokenBalance = async () => {
    const contract = new ethers.Contract(tokenAddress, erc20Abi, provider);
    const balance = await contract.balanceOf(currentWallet.wallet.address);
    setTokenBalance(ethers.formatUnits(balance, 18));
  };

  const sendTokens = async () => {
    try {
      const contract = new ethers.Contract(tokenAddress, erc20Abi, currentWallet.wallet);
      const gasLimit = await contract.transfer.estimateGas(recipient, ethers.parseUnits(amount, 18));
      const tx = await contract.transfer(recipient, ethers.parseUnits(amount, 18), {
        gasLimit,
      });
      await tx.wait();
      alert("Transaction successful!\nHash: " + tx.hash);
    } catch (error) {
      console.error(error);
      alert("Transaction failed:\n" + error.message);
    }
  };

  return (
    <div style={{ background: "#111", color: "#fff", padding: 20, fontFamily: "Arial" }}>
      <h2>ü¶ú Goan Wallet</h2>

      <div>
        <input
          value={walletName}
          onChange={(e) => setWalletName(e.target.value)}
          placeholder="Wallet name"
        />
        <button onClick={generateWallet}>Generate Wallet</button>
        <button onClick={loadWallet}>Load Wallet</button>
      </div>

      {currentWallet && (
        <>
          <h3>üîê {currentWallet.name}</h3>
          <p>üì¨ {currentWallet.wallet.address}</p>
          <p>ü™ô BNB: {bnbBalance}</p>
          <button onClick={refreshBNB}>Refresh BNB</button>

          <hr />

          <h3>üöÄ Send Tokens</h3>
          <select onChange={(e) => setTokenAddress(e.target.value)}>
            <option value="">Select ERC20 Token</option>
            <option value="0x787A697324db4AAB...">ERC20 Test Token</option>
            {/* Add other tokens here */}
          </select>
          <button onClick={fetchTokenBalance}>Check Balance</button>
          <p>Balance: {tokenBalance}</p>

          <input
            type="text"
            placeholder="Recipient address"
            value={recipient}
            onChange={(e) => setRecipient(e.target.value)}
          />
          <input
            type="number"
            placeholder="Amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
          <button onClick={sendTokens}>Send</button>
        </>
      )}
    </div>
  );
};

export default App;
