import React, { useState, useEffect, useRef } from "react";
import {
  Wallet,
  isAddress,
  parseEther,
  formatEther,
  JsonRpcProvider,
  Contract,
  parseUnits,
  formatUnits,
} from "ethers";
import { QRCodeCanvas } from "qrcode.react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const ERC20_ABI = [
  "function balanceOf(address) view returns (uint256)",
  "function transfer(address to, uint256 value) returns (bool)",
  "function decimals() view returns (uint8)",
  "function symbol() view returns (string)",
];

const provider = new JsonRpcProvider("https://bsc-testnet-dataseed.bnbchain.org");

const TOKEN_ADDRESSES = {
  USDT: "0x787A697324dbA4AB965C58CD33c13ff5eeA6295F",
  USDC: "0x342e3aA1248AB77E319e3331C6fD3f1F2d4B36B1",
};

function App() {
  const [walletData, setWalletData] = useState(null);
  const [walletName, setWalletName] = useState("");
  const [searchName, setSearchName] = useState("");
  const [recipient, setRecipient] = useState("");
  const [amount, setAmount] = useState("");
  const [tokenType, setTokenType] = useState("BNB");
  const [balances, setBalances] = useState({ BNB: "Loading...", USDT: "N/A", USDC: "N/A" });
  const [txHashInput, setTxHashInput] = useState("");
  const [txDetails, setTxDetails] = useState(null);
  const [ledger, setLedger] = useState([]);
  const [loading, setLoading] = useState({});
  const [showPrivate, setShowPrivate] = useState(false);
  const [showQR, setShowQR] = useState(true);
  const qrRef = useRef();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [isLogin, setIsLogin] = useState(true);

  const setLoader = (key, value) => setLoading((prev) => ({ ...prev, [key]: value }));
  const notify = (msg, type = "info") => toast[type](msg);

  const apiFetch = async (url, options = {}) => {
    const headers = {
      "Content-Type": "application/json",
      ...options.headers,
    };
    if (token) {
      headers["Authorization"] = `Bearer ${token}`;
    }
    const res = await fetch(url, { ...options, headers });
    const data = await res.json();
    if (!res.ok) {
      throw new Error(data.error || `Request failed with status ${res.status}`);
    }
    return data;
  };

  const handleAuth = async (e) => {
    e.preventDefault();
    const endpoint = isLogin ? "login" : "register";
    try {
      const res = await fetch(`http://localhost:5001/api/auth/${endpoint}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });
      const data = await res.json();
      if (res.ok) {
        if (isLogin) {
          localStorage.setItem("token", data.token);
          setToken(data.token);
        }
        notify(`✅ ${isLogin ? 'Logged in' : 'Registered'} successfully!`, "success");
      } else {
        notify(`❌ ${data.error || 'Authentication failed'}`, "error");
      }
    } catch (error) {
      notify("❌ Authentication error", "error");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setToken(null);
    setWalletData(null);
    setLedger([]);
  };

  const generateAndSaveWallet = async () => {
    if (!walletName.trim()) return notify("Enter a wallet name", "error");
    setLoader("creating", true);
    const wallet = Wallet.createRandom();
    const newWallet = {
      name: walletName,
      address: wallet.address,
      privateKey: wallet.privateKey,
      mnemonic: wallet.mnemonic.phrase,
    };
    try {
      await apiFetch("http://localhost:5001/api/wallet", {
        method: "POST",
        body: JSON.stringify(newWallet),
      });
      notify("✅ Wallet saved!", "success");
      setWalletName("");
    } catch (error) {
      notify(`❌ ${error.message}`, "error");
    } finally {
      setLoader("creating", false);
    }
  };

  const fetchWalletByName = async () => {
    if (!searchName.trim()) return notify("Enter wallet name to fetch", "error");
    setLoader("fetching", true);
    try {
      const data = await apiFetch(`http://localhost:5001/api/wallet/${searchName}`);
      setWalletData(data);
      fetchAllBalances(data.address);
      fetchLedger(data.address);
    } catch (error) {
      notify(`❌ ${error.message}`, "error");
      setWalletData(null);
      setLedger([]);
    } finally {
      setLoader("fetching", false);
    }
  };

  const fetchAllBalances = async (address) => {
    setLoader("balance", true);
    try {
      const bnb = await provider.getBalance(address);
      const tokenBalances = await Promise.all(
        Object.entries(TOKEN_ADDRESSES).map(async ([symbol, addr]) => {
          const token = new Contract(addr, ERC20_ABI, provider);
          const decimals = await token.decimals();
          const bal = await token.balanceOf(address);
          return [symbol, formatUnits(bal, decimals)];
        })
      );
      setBalances({ BNB: formatEther(bnb), ...Object.fromEntries(tokenBalances) });
    } catch (e) {
      setBalances({ BNB: "Error", USDT: "N/A", USDC: "N/A" });
    } finally {
      setLoader("balance", false);
    }
  };

  // UPDATED
  const sendTokens = async () => {
    if (!walletData?.privateKey || !isAddress(recipient) || !amount || parseFloat(amount) <= 0)
      return notify("❌ Invalid inputs", "error");
    setLoader("sending", true);
    try {
      const data = await apiFetch("http://localhost:5001/api/send", {
        method: "POST",
        body: JSON.stringify({
          privateKey: walletData.privateKey,
          to: recipient,
          amount,
          token: tokenType,
          fromAddress: walletData.address,
        }),
      });
      notify(`✅ Transaction sent! Hash: ${data.hash.substring(0, 10)}...`, "success");
      setRecipient("");
      setAmount("");
      // Add pending tx to UI immediately
      const pendingTx = {
        hash: data.hash,
        from: walletData.address,
        to: recipient,
        amount,
        token: tokenType,
        status: "Pending",
        createdAt: new Date().toISOString(),
      };
      setLedger(prev => [pendingTx, ...prev]);
    } catch (e) {
      notify(`❌ Transaction failed: ${e.message}`, "error");
    } finally {
      setLoader("sending", false);
    }
  };

  // UPDATED
  const fetchLedger = async (address) => {
    if (!address) return;
    setLoader("ledger", true);
    try {
      const data = await apiFetch(`http://localhost:5001/api/ledger/${address}`);
      setLedger(Array.isArray(data) ? data : []);
    } catch (error) {
      notify(`❌ ${error.message}`, "error");
      setLedger([]);
    } finally {
      setLoader("ledger", false);
    }
  };

  // NEW
  const verifyTransaction = async (hash) => {
      setLoader(hash, true); // Use hash as loader key
      try {
        const data = await apiFetch('http://localhost:5001/api/verify', {
            method: 'POST',
            body: JSON.stringify({ hash }),
        });
        // Update the specific transaction in the ledger
        setLedger(prev => prev.map(tx => tx.hash === hash ? { ...tx, status: data.status } : tx));
        notify(`✅ Tx ${hash.substring(0,6)}... status: ${data.status}`, "success");
      } catch (error) {
        notify(`❌ Verification failed: ${error.message}`, "error");
      } finally {
        setLoader(hash, false);
      }
  }


  const fetchTransactionDetails = async () => {
    if (!txHashInput) return;
    setLoader("tx", true);
    try {
      const tx = await provider.getTransaction(txHashInput);
      const receipt = await provider.getTransactionReceipt(txHashInput);
      setTxDetails({ ...tx, ...receipt });
    } catch (e) {
      notify("❌ Invalid or failed to fetch", "error");
    } finally {
      setLoader("tx", false);
    }
  };

  const copyAddress = () => {
    if (walletData?.address) {
      navigator.clipboard.writeText(walletData.address);
      notify("📋 Address copied", "info");
    }
  };

  const downloadQR = () => {
    const canvas = qrRef.current.querySelector("canvas");
    const pngUrl = canvas.toDataURL("image/png");
    const a = document.createElement("a");
    a.href = pngUrl;
    a.download = `${walletData.name}_qr.png`;
    a.click();
  };

  return (
    <div style={styles.container}>
      <ToastContainer position="top-right" autoClose={3000} hideProgressBar={false} />
      <h1 style={styles.title}>🦜 Goan Wallet</h1>

      {!token ? (
        <section style={styles.section}>
          <form onSubmit={handleAuth}>
            <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} style={styles.input} required />
            <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} style={styles.input} required />
            <button type="submit" style={styles.buttonBlue}>{isLogin ? "Login" : "Register"}</button>
            <button type="button" onClick={() => setIsLogin(!isLogin)} style={styles.toggle}>{isLogin ? "Need to register?" : "Have an account?"}</button>
          </form>
        </section>
      ) : (
        <>
          <button onClick={handleLogout} style={styles.buttonOrange}>Logout</button>
          <section style={styles.section}>
            <input placeholder="🖊 Create wallet name" value={walletName} onChange={(e) => setWalletName(e.target.value)} style={styles.input}/>
            <button onClick={generateAndSaveWallet} style={styles.buttonGreen}>{loading.creating ? "⏳ Saving..." : "✅ Generate & Save"}</button>
          </section>

          <section style={styles.section}>
            <input placeholder="🔍 Load wallet by name" value={searchName} onChange={(e) => setSearchName(e.target.value)} style={styles.input} />
            <button onClick={fetchWalletByName} style={styles.buttonBlue}>{loading.fetching ? "⏳ Loading..." : "📥 Load Wallet"}</button>
          </section>

          {walletData && (
            <div style={styles.walletBox}>
              <h2>{walletData.name}</h2>
              <p onClick={copyAddress} style={{ cursor: "pointer" }}><strong>🏦 Address:</strong> {walletData.address} 📋</p>
              <p><strong>🔑 Private Key:</strong> {showPrivate ? walletData.privateKey : "••••••••••••••••••••"}<button onClick={() => setShowPrivate(!showPrivate)} style={styles.toggle}>{showPrivate ? "🙈 Hide" : "👁 Show"}</button></p>
              <h3>📊 Balances</h3>
              <ul style={{ listStyle: "none", padding: 0 }}>
                {Object.entries(balances).map(([symbol, value]) => (<li key={symbol}> {symbol === "BNB" ? "💰" : symbol === "USDT" ? "💵" : "💸"} {symbol}: <strong>{value}</strong></li>))}
              </ul>
              <div style={{ marginTop: "20px" }}>
                <button onClick={() => setShowQR(!showQR)} style={styles.toggle}>{showQR ? "🙈 Hide QR" : "📱 Show QR"}</button>
                {showQR && (<div ref={qrRef} style={{ marginTop: "10px" }}><QRCodeCanvas value={walletData.address} size={130} bgColor="#ffffff" fgColor="#000000" /><p>📱 Scan to pay</p><button onClick={downloadQR} style={styles.buttonBlue}>⬇️ Download QR</button></div>)}
              </div>
              <h3>🚀 Send Tokens</h3>
              <select value={tokenType} onChange={(e) => setTokenType(e.target.value)} style={styles.input}><option value="BNB">BNB</option><option value="USDT">USDT</option><option value="USDC">USDC</option></select>
              <input placeholder="📤 Recipient" value={recipient} onChange={(e) => setRecipient(e.target.value)} style={styles.input} />
              <input placeholder="💰 Amount" value={amount} onChange={(e) => setAmount(e.target.value)} style={styles.input} />
              <button onClick={sendTokens} style={styles.buttonOrange}>{loading.sending ? "⏳ Sending..." : "🚀 Send"}</button>

              <h3 style={{ marginTop: "20px" }}>📜 Transaction Ledger <button onClick={() => fetchLedger(walletData.address)} style={styles.toggle}>🔄</button></h3>
              {loading.ledger ? <p>Loading ledger...</p> : (
                <div style={styles.ledgerContainer}>
                  {ledger.length > 0 ? (
                    ledger.map((tx) => (
                      <div key={tx.hash} style={styles.ledgerTx}>
                        <p><strong>Hash:</strong> <a href={`https://testnet.bscscan.com/tx/${tx.hash}`} target="_blank" rel="noopener noreferrer">{`${tx.hash.substring(0, 10)}...`}</a></p>
                        <p><strong>From:</strong> {`${tx.from.substring(0, 10)}...`}</p>
                        <p><strong>To:</strong> {`${tx.to.substring(0, 10)}...`}</p>
                        <p><strong>Amount:</strong> {tx.amount} {tx.token}</p>
                        <p><strong>Status:</strong> {tx.status} 
                           {tx.status === 'Pending' && <button onClick={() => verifyTransaction(tx.hash)} style={{...styles.toggle, marginLeft: '8px'}}>{loading[tx.hash] ? '...': 'Verify'}</button>}
                        </p>
                        <p><strong>Date:</strong> {new Date(tx.createdAt).toLocaleString()}</p>
                      </div>
                    ))
                  ) : (<p>No transactions recorded in the ledger.</p>)}
                </div>
              )}

              <h4 style={{ marginTop: "20px" }}>📦 Transaction Info</h4>
              <input placeholder="🔍 Tx Hash" value={txHashInput} onChange={(e) => setTxHashInput(e.target.value)} style={styles.input} />
              <button onClick={fetchTransactionDetails} style={styles.buttonPurple}>{loading.tx ? "⏳ Fetching..." : "🔍 View Tx Info"}</button>
              {txDetails && (<div style={styles.txBox}><p><strong>From:</strong> {txDetails.from}</p><p><strong>To:</strong> {txDetails.to}</p><p><strong>Hash:</strong> <a href={`https://testnet.bscscan.com/tx/${txDetails.hash}`} target="_blank" rel="noopener noreferrer">{txDetails.hash}</a></p><p><strong>Block:</strong> {txDetails.blockNumber}</p><p><strong>Status:</strong> {txDetails.status === 1 ? "✅ Success" : "❌ Failed"}</p></div>)}
            </div>
          )}
        </>
      )}
    </div>
  );
}

const styles = {
  container: { minHeight: "100vh", background: "linear-gradient(to right, #3b82f6, #9333ea)", padding: "40px 20px", fontFamily: "Segoe UI, sans-serif", color: "#fff", textAlign: "center", },
  title: { fontSize: "2.5rem", marginBottom: "30px", },
  section: { marginBottom: "25px", },
  input: { padding: "12px", margin: "10px", width: "60%", maxWidth: "400px", borderRadius: "10px", border: "none", fontSize: "16px", backgroundColor: "#f0f0f0", color: "#111", },
  buttonGreen: { padding: "12px 24px", backgroundColor: "#16a34a", borderRadius: "10px", color: "#fff", fontWeight: "bold", fontSize: "16px", cursor: "pointer", },
  buttonBlue: { padding: "12px 24px", backgroundColor: "#2563eb", borderRadius: "10px", color: "#fff", fontWeight: "bold", fontSize: "16px", marginTop: "10px", cursor: "pointer", },
  buttonOrange: { padding: "12px 24px", backgroundColor: "#f97316", borderRadius: "10px", color: "#fff", fontWeight: "bold", fontSize: "16px", marginTop: "10px", cursor: "pointer", },
  buttonPurple: { padding: "12px 24px", backgroundColor: "#9333ea", borderRadius: "10px", color: "#fff", fontWeight: "bold", fontSize: "16px", cursor: "pointer", },
  walletBox: { backgroundColor: "#1e293b", padding: "30px", marginTop: "30px", borderRadius: "12px", maxWidth: "700px", marginInline: "auto", textAlign: "left", boxShadow: "0 0 20px rgba(0,0,0,0.5)", },
  txBox: { marginTop: "15px", fontSize: "14px", background: "#111827", padding: "15px", borderRadius: "8px", },
  toggle: { marginLeft: "10px", padding: "5px 10px", backgroundColor: "#2563eb", color: "#fff", border: "none", borderRadius: "6px", cursor: "pointer", },
  ledgerContainer: { maxHeight: '300px', overflowY: 'auto', backgroundColor: '#111827', padding: '15px', borderRadius: '8px', marginTop: '10px', },
  ledgerTx: { borderBottom: '1px solid #374151', paddingBottom: '10px', marginBottom: '10px', fontSize: '14px', },
};

export default App;
