import React, { useState, useEffect, useRef } from "react";
import {
  Wallet,
  isAddress,
  parseEther,
  formatEther,
  JsonRpcProvider,
  Contract,
  parseUnits,
  formatUnits,
  Interface,
} from "ethers";
import { QRCodeCanvas } from "qrcode.react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const ERC20_ABI = [
  "function balanceOf(address) view returns (uint256)",
  "function transfer(address to, uint256 value) returns (bool)",
  "function decimals() view returns (uint8)",
  "function symbol() view returns (string)",
  "event Transfer(address indexed from, address indexed to, uint256 value)",
];
const provider = new JsonRpcProvider("https://bsc-testnet-dataseed.bnbchain.org");
const TOKEN_ADDRESSES = {
  USDT: "0x787A697324dbA4AB965C58CD33c13ff5eeA6295F",
  USDC: "0x342e3aA1248AB77E319e3331C6fD3f1F2d4B36B1",
};

function App() {
  const [walletData, setWalletData] = useState(null);
  const [walletName, setWalletName] = useState("");
  const [searchName, setSearchName] = useState("");
  const [recipient, setRecipient] = useState("");
  const [amount, setAmount] = useState("");
  const [tokenType, setTokenType] = useState("BNB");
  const [balances, setBalances] = useState({ BNB: "Loading...", USDT: "N/A", USDC: "N/A" });
  const [txHashInput, setTxHashInput] = useState("");
  const [txDetails, setTxDetails] = useState(null);
  const [ledger, setLedger] = useState([]);
  const [loading, setLoading] = useState({});
  const [showPrivate, setShowPrivate] = useState(false);
  const [showQR, setShowQR] = useState(true);
  const qrRef = useRef();

  const setLoader = (key, value) => setLoading(prev => ({ ...prev, [key]: value }));
  const notify = (msg, type = "info") => toast[type](msg);

  const generateAndSaveWallet = async () => {
    if (!walletName.trim()) return notify("Enter a wallet name", "error");
    setLoader("creating", true);
    const wallet = Wallet.createRandom();
    const newWallet = {
      name: walletName,
      address: wallet.address,
      privateKey: wallet.privateKey,
      mnemonic: wallet.mnemonic.phrase
    };
    try {
      const res = await fetch("http://localhost:5001/api/wallet", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newWallet),
      });
      const data = await res.json();
      if (res.ok) {
        notify("✅ Wallet saved!", "success");
        setWalletName("");
      } else {
        notify(`❌ ${data.error || "Save failed"}`, "error");
      }
    } catch {
      notify("❌ Save error", "error");
    } finally {
      setLoader("creating", false);
    }
  };

  const fetchWalletByName = async () => {
    if (!searchName.trim()) return notify("Enter wallet name to fetch", "error");
    setLoader("fetching", true);
    setTxDetails(null);
    try {
      const res = await fetch(`http://localhost:5001/api/wallet/${searchName}`);
      const data = await res.json();
      if (data.error) {
        notify("❌ Wallet not found", "error");
        setWalletData(null);
        setLedger([]);
      } else {
        setWalletData(data);
        fetchAllBalances(data.privateKey, data.address);
        fetchLedger(data.address);
      }
    } catch {
      notify("❌ Error fetching wallet", "error");
    } finally {
      setLoader("fetching", false);
    }
  };

  const fetchAllBalances = async (privateKey, address) => {
    setLoader("balance", true);
    try {
      const wallet = new Wallet(privateKey, provider);
      const bnb = await provider.getBalance(wallet.address);
      const tokenBalances = await Promise.all(
        Object.entries(TOKEN_ADDRESSES).map(async ([symbol, addr]) => {
          const tokenContract = new Contract(addr, ERC20_ABI, wallet);
          const decimals = await tokenContract.decimals();
          const bal = await tokenContract.balanceOf(wallet.address);
          return [symbol, formatUnits(bal, decimals)];
        })
      );
      const all = Object.fromEntries(tokenBalances);
      setBalances({ BNB: formatEther(bnb), ...all });
    } catch (e) {
      console.error(e);
      setBalances({ BNB: "Error", USDT: "N/A", USDC: "N/A" });
    } finally {
      setLoader("balance", false);
    }
  };

  const sendTokens = async () => {
    if (!walletData?.privateKey || !isAddress(recipient) || !amount || parseFloat(amount) <= 0)
      return notify("❌ Invalid inputs", "error");
    setLoader("sending", true);
    try {
      const wallet = new Wallet(walletData.privateKey, provider);
      const bnbBalance = await provider.getBalance(wallet.address);
      if (parseFloat(formatEther(bnbBalance)) < 0.001)
        return notify("❌ Not enough BNB for gas", "error");
      let tx;
      if (tokenType === "BNB") {
        tx = await wallet.sendTransaction({ to: recipient, value: parseEther(amount) });
      } else {
        const tokenContract = new Contract(TOKEN_ADDRESSES[tokenType], ERC20_ABI, wallet);
        const decimals = await tokenContract.decimals();
        tx = await tokenContract.transfer(recipient, parseUnits(amount, decimals));
      }
      await tx.wait();
      notify(`✅ ${tokenType} Sent!`, "success");
      navigator.clipboard.writeText(tx.hash);
      logTransaction(tx.hash, walletData.address, recipient, amount, tokenType);
      setRecipient("");
      setAmount("");
      fetchAllBalances(walletData.privateKey, walletData.address);
    } catch (e) {
      console.error(e);
      notify("❌ Transaction failed", "error");
    } finally {
      setLoader("sending", false);
    }
  };

  const logTransaction = async (hash, from, to, amount, token) => {
    try {
      await fetch("http://localhost:5001/api/ledger", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ walletAddress: from, hash, from, to, amount, token }),
      });
      fetchLedger(from);
    } catch (error) {
      console.error("Failed to log transaction:", error);
      notify("Could not save Tx to ledger", "warn");
    }
  };

  const fetchLedger = async (address) => {
    setLoader("ledger", true);
    try {
      const res = await fetch(`http://localhost:5001/api/ledger/${address}`);
      const data = await res.json();
      setLedger(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error("Failed to fetch ledger:", error);
      setLedger([]);
    } finally {
      setLoader("ledger", false);
    }
  };

  const fetchTransactionDetails = async () => {
    if (!txHashInput) return;
    setLoader("tx", true);
    setTxDetails(null);
    try {
      const tx = await provider.getTransaction(txHashInput);
      const receipt = await provider.getTransactionReceipt(txHashInput);
      if (!tx || !receipt) throw new Error("Transaction not found or not yet mined.");

      const iface = new Interface(ERC20_ABI);
      const decodedLogs = receipt.logs.map(log => {
        try {
          const tokenSymbol = Object.keys(TOKEN_ADDRESSES).find(
            key => TOKEN_ADDRESSES[key].toLowerCase() === log.address.toLowerCase()
          );
          if (!tokenSymbol) return null;
          const parsedLog = iface.parseLog(log);
          return {
            ...parsedLog,
            tokenSymbol,
            formattedAmount: formatUnits(parsedLog.args.value, 18),
          };
        } catch {
          return null;
        }
      }).filter(log => log !== null);

      setTxDetails({ ...tx, ...receipt, decodedLogs });
    } catch (e) {
      console.error(e);
      notify(`❌ ${e.message || "Invalid or failed to fetch"}`, "error");
    } finally {
      setLoader("tx", false);
    }
  };

  const copyAddress = () => {
    if (walletData?.address) {
      navigator.clipboard.writeText(walletData.address);
      notify("📋 Address copied", "info");
    }
  };

  const downloadQR = () => {
    const canvas = qrRef.current.querySelector("canvas");
    const pngUrl = canvas.toDataURL("image/png");
    const a = document.createElement("a");
    a.href = pngUrl;
    a.download = `${walletData.name}_qr.png`;
    a.click();
  };

  return (
    <div style={styles.container}>
      <ToastContainer position="top-right" autoClose={3000} hideProgressBar={false} />
      <h1 style={styles.title}>🦜 Goan Wallet</h1>
      {/* Create and Load Wallet sections */}
      <section style={styles.section}>
        <input placeholder="🖊 Create wallet name" value={walletName} onChange={e => setWalletName(e.target.value)} style={styles.input} />
        <button onClick={generateAndSaveWallet} style={styles.buttonGreen}>
          {loading.creating ? "⏳ Saving..." : "✅ Generate & Save"}
        </button>
      </section>
      <section style={styles.section}>
        <input placeholder="🔍 Load wallet by name" value={searchName} onChange={e => setSearchName(e.target.value)} style={styles.input} />
        <button onClick={fetchWalletByName} style={styles.buttonBlue}>
          {loading.fetching ? "⏳ Loading..." : "📥 Load Wallet"}
        </button>
      </section>
      {/* Wallet details, QR, send, ledger and tx info... */}
      {walletData && (
        <div style={styles.walletBox}>
          <h2>{walletData.name}</h2>
          <p onClick={copyAddress} style={{ cursor: "pointer" }}>
            <strong>🏦 Address:</strong> {walletData.address} 📋
          </p>
          <p>
            <strong>🔑 Private Key:</strong>{" "}
            {showPrivate ? walletData.privateKey : "••••••••••••••••••••"}
            <button onClick={() => setShowPrivate(!showPrivate)} style={styles.toggle}>
              {showPrivate ? "🙈 Hide" : "👁 Show"}
            </button>
          </p>
          <h3>📊 Balances</h3>
          <ul style={{ listStyle: "none", padding: 0 }}>
            {Object.entries(balances).map(([symbol, value]) => (
              <li key={symbol}>
                {symbol === "BNB" ? "💰" : symbol === "USDT" ? "💵" : "💸"} {symbol}: <strong>{value}</strong>
              </li>
            ))}
          </ul>
          <div style={{ marginTop: "20px" }}>
            <button onClick={() => setShowQR(!showQR)} style={styles.toggle}>
              {showQR ? "🙈 Hide QR" : "📱 Show QR"}
            </button>
            {showQR && (
              <div ref={qrRef} style={{ marginTop: "10px" }}>
                <QRCodeCanvas value={walletData.address} size={130} bgColor="#ffffff" fgColor="#000000" />
                <p>📱 Scan to pay</p>
                <button onClick={downloadQR} style={styles.buttonBlue}>⬇️ Download QR</button>
              </div>
            )}
          </div>
          <h3>🚀 Send Tokens</h3>
          <select value={tokenType} onChange={e => setTokenType(e.target.value)} style={styles.input}>
            <option value="BNB">BNB</option>
            <option value="USDT">USDT</option>
            <option value="USDC">USDC</option>
          </select>
          <input placeholder="📤 Recipient" value={recipient} onChange={e => setRecipient(e.target.value)} style={styles.input} />
          <input placeholder="💰 Amount" value={amount} onChange={e => setAmount(e.target.value)} style={styles.input} />
          <button onClick={sendTokens} style={styles.buttonOrange}>
            {loading.sending ? "⏳ Sending..." : "🚀 Send"}
          </button>
          <div style={{ display: 'flex', alignItems: 'center', marginTop: "20px" }}>
            <h3>📜 Transaction Ledger</h3>
            <button onClick={() => setShowHistory(!showHistory)} style={styles.toggle}>
              {showHistory ? 'Hide History' : 'Show History'}
            </button>
          </div>
          {showHistory && (
            loading.ledger ? <p>Loading ledger...</p> : (
              <div style={styles.ledgerContainer}>
                {ledger.length > 0 ? ledger.map(tx => {
                  const isSent = tx.from.toLowerCase() === walletData.address.toLowerCase();
                  return (
                    <div key={tx.hash} style={styles.ledgerTx}>
                      <p>
                        <strong>{isSent ? <span style={{ color: '#f97316' }}>[OUT]</span> : <span style={{ color: '#16a34a' }}>[IN]</span>}</strong>
                        <a href={`https://testnet.bscscan.com/tx/${tx.hash}`} target="_blank" rel="noopener noreferrer">
                          {`${tx.hash.substring(0, 10)}...`}
                        </a>
                      </p>
                      <p>{isSent ? `To: ${tx.to.substring(0, 10)}...` : `From: ${tx.from.substring(0, 10)}...`}</p>
                      <p><strong>Amount:</strong> {tx.amount} {tx.token}</p>
                      <p><strong>Date:</strong> {new Date(tx.createdAt).toLocaleString()}</p>
                    </div>
                  );
                }) : <p>No transactions recorded for this wallet.</p>}
              </div>
            )
          )}

          <h4 style={{ marginTop: "20px" }}>📦 Transaction Info</h4>
          <input placeholder="🔍 Tx Hash" value={txHashInput} onChange={e => setTxHashInput(e.target.value)} style={styles.input} />
          <button onClick={fetchTransactionDetails} style={styles.buttonPurple}>
            {loading.tx ? "⏳ Fetching..." : "🔍 View Tx Info"}
          </button>
          {txDetails && (
            <div style={styles.txBox}>
              <p><strong>Hash:</strong> <a href={`https://testnet.bscscan.com/tx/${txDetails.hash}`} target="_blank" rel="noopener noreferrer">{txDetails.hash}</a></p>
              <p><strong>Status:</strong> {txDetails.status === 1 ? "✅ Success" : "❌ Failed"}</p>
              <p><strong>Block:</strong> {txDetails.blockNumber}</p>
              <p><strong>From:</strong> {txDetails.from}</p>
              <p><strong>To:</strong> {txDetails.to}</p>
              {txDetails.decodedLogs?.length > 0 && (
                <div style={{ marginTop: '15px', borderTop: '1px solid #374151', paddingTop: '10px' }}>
                  <h5 style={{ margin: '0 0 10px 0' }}><strong>Token Transfers Detected:</strong></h5>
                  {txDetails.decodedLogs.map((log, idx) => (
                    <div key={idx} style={{ background: '#000', padding: '10px', borderRadius: '5px', marginBottom: '5px' }}>
                      <p><strong>From:</strong> {log.args.from}</p>
                      <p><strong>To:</strong> {log.args.to}</p>
                      <p><strong>Amount:</strong> {log.formattedAmount} {log.tokenSymbol}</p>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

const styles = {
  container: { minHeight: "100vh", background: "linear-gradient(to right, #3b82f6, #9333ea)", padding: "40px 20px", fontFamily: "Segoe UI, sans-serif", color: "#fff", textAlign: "center" },
  title: { fontSize: "2.5rem", marginBottom: "30px" },
  section: { marginBottom: "25px" },
  input: { padding: "12px", margin: "10px", width: "60%", maxWidth: "400px", borderRadius: "10px", border: "none", fontSize: "16px", backgroundColor: "#f0f0f0", color: "#111" },
  buttonGreen: { padding: "12px 24px", backgroundColor: "#16a34a", borderRadius: "10px", color: "#fff", fontWeight: "bold", fontSize: "16px", cursor: "pointer", border: 'none' },
  buttonBlue: { padding: "12px 24px", backgroundColor: "#2563eb", borderRadius: "10px", color: "#fff", fontWeight: "bold", fontSize: "16px", marginTop: "10px", cursor: "pointer", border: 'none' },
  buttonOrange: { padding: "12px 24px", backgroundColor: "#f97316", borderRadius: "10px", color: "#fff", fontWeight: "bold", fontSize: "16px", marginTop: "10px", cursor: "pointer", border: 'none' },
  buttonPurple: { padding: "12px 24px", backgroundColor: "#9333ea", borderRadius: "10px", color: "#fff", fontWeight: "bold", fontSize: "16px", cursor: "pointer", border: 'none' },
  walletBox: { backgroundColor: "#1e293b", padding: "30px", marginTop: "30px", borderRadius: "12px", maxWidth: "700px", marginInline: "auto", textAlign: "left", boxShadow: "0 0 20px rgba(0,0,0,0.5)" },
  txBox: { marginTop: "15px", fontSize: "14px", background: "#111827", padding: "15px", borderRadius: "8px" },
  toggle: { marginLeft: "10px", padding: "5px 10px", backgroundColor: "#2563eb", color: "#fff", border: "none", borderRadius: "6px", cursor: "pointer" },
  ledgerContainer: { maxHeight: '300px', overflowY: 'auto', backgroundColor: '#111827', padding: '15px', borderRadius: '8px', marginTop: '10px' },
  ledgerTx: { borderBottom: '1px solid #374151', paddingBottom: '10px', marginBottom: '10px', fontSize: '14px' },
};

export default App;
