            <option value="BNB">BNB</option><option value="USDT">USDT</option><option value="USDC">USDC</option>
          </select>
          <input placeholder="ðŸ“¤ Recipient" value={recipient} onChange={(e) => setRecipient(e.target.value)} style={styles.input} className="interactive-input" />
          <input placeholder="ðŸ’° Amount" value={amount} onChange={(e) => setAmount(e.target.value)} style={styles.input} className="interactive-input" />
