          <div style={{ display: 'flex', alignItems: 'center', marginTop: "20px" }}>
            <h3>ðŸ“œ Transaction Ledger</h3>
            <button onClick={() => setShowHistory(!showHistory)} style={styles.toggle} className="interactive-toggle">{showHistory ? 'Hide History' : 'Show History'}</button>
          </div>

          {showHistory && (
            // 3. Animate this section too
            <div className="animated-slide-down">
              {loading.ledger ? <p>Loading ledger...</p> : (
                <div style={styles.ledgerContainer}>
                  {ledger.length > 0 ? (
                    // 4. Stagger animation for each transaction item
                    ledger.map((tx, index) => {
                      const isSent = tx.from.toLowerCase() === walletData.address.toLowerCase();
                      return (
                        <div key={tx.hash} style={{...styles.ledgerTx, animationDelay: `${index * 80}ms`}} className="animated-item">
                          <p><strong>{isSent ? <span style={{ color: '#f97316' }}>[OUT]</span> : <span style={{ color: '#16a34a' }}>[IN]</span>}</strong>
