          )}

          <h4 style={{ marginTop: "20px" }}>ðŸ“¦ Transaction Info</h4>
          <input placeholder="ðŸ” Tx Hash" value={txHashInput} onChange={(e) => setTxHashInput(e.target.value)} style={styles.input} className="interactive-input" />
          <button onClick={fetchTransactionDetails} style={styles.buttonPurple} className="interactive-button">{loading.tx ? "â³ Fetching..." : "ðŸ” View Tx Info"}</button>

          {/* 3. Animate the final conditional section */}
          {txDetails && (
            <div style={styles.txBox} className="animated-slide-down">
