              {txDetails.decodedLogs?.length > 0 && (
                <div style={{ marginTop: '15px', borderTop: '1px solid #374151', paddingTop: '10px' }}>
                  <h5 style={{ margin: '0 0 10px 0' }}><strong>Token Transfers Detected:</strong></h5>
                  {txDetails.decodedLogs.map((log, index) => (
                    <div key={index} style={{ background: '#000', padding: '10px', borderRadius: '5px', marginBottom: '5px' }}>
                      <p><strong>From:</strong> {log.args.from}</p>
                      <p><strong>To:</strong> {log.args.to}</p>
                      <p><strong>Amount:</strong> {log.formattedAmount} {log.tokenSymbol}</p>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}
        </div>
      )}

      {/* 2. Apply animation class to the global ledger box */}
      <div style={{ ...styles.walletBox, marginTop: "30px" }} className="animated-section">
        <h3>ðŸŒ Global Transaction Ledger</h3>
        <p>Showing all transactions from all wallets.</p>
        {loading.globalLedger ? <p>Loading global ledger...</p> : (
          <div style={styles.ledgerContainer}>
            {globalLedger.length > 0 ? (
              // 4. Stagger animation for these items as well
              globalLedger.map((tx, index) => (
                <div key={tx.hash} style={{...styles.ledgerTx, animationDelay: `${index * 50}ms`}} className="animated-item">
                  <p><strong>Hash: </strong><a href={`https://testnet.bscscan.com/tx/${tx.hash}`} target="_blank" rel="noopener noreferrer">{`${tx.hash.substring(0, 10)}...`}</a></p>
                  <p><strong>From:</strong> {`${tx.from.substring(0, 12)}...`}</p>
                  <p><strong>To:</strong> {`${tx.to.substring(0, 12)}...`}</p>
                  <p><strong>Amount:</strong> {tx.amount} {tx.token}</p>
                  <p><strong>Date:</strong> {new Date(tx.createdAt).toLocaleString()}</p>
                </div>
              ))
            ) : (<p>No transactions recorded in the global ledger yet.</p>)}
          </div>
        )}
      </div>
    </div>
  );
}

