import React, { useState } from "react";
import {
  Wallet,
  isAddress,
  parseEther,
  formatEther,
  JsonRpcProvider,
  Contract,
  parseUnits,
  formatUnits
} from "ethers";

const ERC20_ABI = [
  "function balanceOf(address) view returns (uint256)",
  "function transfer(address to, uint256 value) returns (bool)",
  "function decimals() view returns (uint8)"
];

const provider = new JsonRpcProvider("https://bsc-testnet-dataseed.bnbchain.org");

// 👇 Define your admin wallet address here
const ADMIN_WALLET_ADDRESS = "0xYourAdminWalletAddressHere".toLowerCase();

function App() {
  const [walletData, setWalletData] = useState(null);
  const [walletName, setWalletName] = useState("");
  const [searchName, setSearchName] = useState("");
  const [recipient, setRecipient] = useState("");
  const [amount, setAmount] = useState("");
  const [balance, setBalance] = useState(null);

  // ERC20 state
  const [tokenAddress, setTokenAddress] = useState("");
  const [tokenAmount, setTokenAmount] = useState("");
  const [tokenRecipient, setTokenRecipient] = useState("");
  const [tokenBalance, setTokenBalance] = useState("N/A");
  const [tokenDecimals, setTokenDecimals] = useState(18);

  // 🧾 Tx Logs
  const [txLogs, setTxLogs] = useState([]);

  const generateAndSaveWallet = async () => {
    if (!walletName.trim()) return alert("Enter a wallet name");

    const wallet = Wallet.createRandom();
    const newWallet = {
      name: walletName,
      address: wallet.address,
      privateKey: wallet.privateKey,
      mnemonic: wallet.mnemonic.phrase,
    };

    try {
      const res = await fetch("http://localhost:5001/api/wallet", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newWallet),
      });

      if (res.ok) {
        alert("✅ Wallet saved!");
        setWalletName("");
      } else {
        alert("❌ Save failed");
      }
    } catch (err) {
      console.error("Save error", err);
      alert("❌ Save error");
    }
  };

  const fetchWalletByName = async () => {
    if (!searchName.trim()) return alert("Enter wallet name to fetch");

    try {
      const res = await fetch(`http://localhost:5001/api/wallet/${searchName}`);
      const data = await res.json();

      if (data.error) {
        alert("❌ Wallet not found");
        setWalletData(null);
        setBalance(null);
      } else {
        setWalletData(data);
        fetchBalance(data.privateKey);
      }
    } catch (err) {
      console.error("Fetch error:", err);
      alert("❌ Error fetching wallet");
    }
  };

  const fetchBalance = async (privateKey) => {
    try {
      const wallet = new Wallet(privateKey, provider);
      const bal = await provider.getBalance(wallet.address);
      setBalance(formatEther(bal));
    } catch (err) {
      console.error("Balance error", err);
      setBalance(null);
    }
  };

  const sendTokens = async () => {
    if (!walletData?.privateKey || !isAddress(recipient) || !amount || parseFloat(amount) <= 0) {
      return alert("❌ Invalid inputs");
    }

    try {
      const wallet = new Wallet(walletData.privateKey, provider);
      const tx = await wallet.sendTransaction({
        to: recipient,
        value: parseEther(amount),
      });

      await tx.wait();
      alert(`✅ BNB Sent!\nTx: ${tx.hash}`);
      setAmount("");
      fetchBalance(walletData.privateKey);

      // 🧾 Log the transaction
      setTxLogs((prev) => [
        {
          hash: tx.hash,
          type: "BNB",
          to: recipient,
          from: wallet.address,
          validated: recipient.toLowerCase() === ADMIN_WALLET_ADDRESS,
        },
        ...prev
      ]);
    } catch (err) {
      console.error("BNB Tx error", err);
      alert("❌ BNB Transaction failed");
    }
  };

  // ERC20 Token Functions
  const fetchTokenBalance = async () => {
    if (!isAddress(tokenAddress) || !walletData) return;

    try {
      const wallet = new Wallet(walletData.privateKey, provider);
      const token = new Contract(tokenAddress, ERC20_ABI, wallet);

      const decimals = await token.decimals();
      const balance = await token.balanceOf(wallet.address);

      setTokenDecimals(decimals);
      setTokenBalance(formatUnits(balance, decimals));
    } catch (err) {
      console.error("ERC20 balance error:", err);
      setTokenBalance("N/A");
    }
  };

  const sendERC20 = async () => {
    if (!isAddress(tokenRecipient) || !tokenAmount || parseFloat(tokenAmount) <= 0 || !walletData) {
      return alert("❌ Invalid ERC20 inputs");
    }

    try {
      const wallet = new Wallet(walletData.privateKey, provider);
      const token = new Contract(tokenAddress, ERC20_ABI, wallet);

      const amountInUnits = parseUnits(tokenAmount, tokenDecimals);
      console.log("Attempting transfer of", tokenAmount, "tokens");

      const tx = await token.transfer(tokenRecipient, amountInUnits);
      const receipt = await tx.wait();

      alert(`✅ ERC20 Sent!\nTx: ${tx.hash}`);
      setTokenAmount("");
      fetchTokenBalance();

      // 🧾 Log the transaction
      setTxLogs((prev) => [
        {
          hash: tx.hash,
          type: "ERC20",
          to: tokenRecipient,
          from: wallet.address,
          validated: tokenRecipient.toLowerCase() === ADMIN_WALLET_ADDRESS,
        },
        ...prev
      ]);
    } catch (err) {
      console.error("ERC20 Tx Error", err); // 👈 Check browser console
      alert("❌ ERC20 Transaction failed");
    }
  };

  return (
    <div style={styles.container}>
      <h1>🦜 Goan Wallet</h1>

      <section style={styles.section}>
        <input
          placeholder="🖊 Name to create wallet"
          value={walletName}
          onChange={(e) => setWalletName(e.target.value)}
          style={styles.input}
        />
        <button onClick={generateAndSaveWallet} style={styles.buttonPrimary}>✅ Generate & Save</button>
      </section>

      <section style={styles.section}>
        <input
          placeholder="🔍 Load wallet by name"
          value={searchName}
          onChange={(e) => setSearchName(e.target.value)}
          style={styles.input}
        />
        <button onClick={fetchWalletByName} style={styles.buttonSecondary}>📥 Load Wallet</button>
      </section>

      {walletData && (
        <div style={styles.walletBox}>
          <p><strong>👤 Name:</strong> {walletData.name}</p>
          <p><strong>🏦 Address:</strong> {walletData.address}</p>
          <p><strong>🔑 Private Key:</strong> {walletData.privateKey}</p>
          <p><strong>💰 BNB Balance:</strong> {balance !== null ? `${balance} BNB` : "Loading..."}</p>

          <h4>🚀 Send BNB</h4>
          <input
            placeholder="📤 Recipient Address"
            value={recipient}
            onChange={(e) => setRecipient(e.target.value)}
            style={styles.input}
          />
          <input
            placeholder="💸 Amount (BNB)"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            style={styles.input}
          />
          <button onClick={sendTokens} style={styles.buttonSecondary}>⚡ Send BNB</button>

          <hr />

          <h4>💎 ERC20 Token</h4>
          <input
            placeholder="🧾 Token Contract Address"
            value={tokenAddress}
            onChange={(e) => setTokenAddress(e.target.value)}
            onBlur={fetchTokenBalance}
            style={styles.input}
          />
          <p>💼 Token Balance: {tokenBalance}</p>
          <input
            placeholder="📤 Recipient Address"
            value={tokenRecipient}
            onChange={(e) => setTokenRecipient(e.target.value)}
            style={styles.input}
          />
          <input
            placeholder="💰 Amount (Token)"
            value={tokenAmount}
            onChange={(e) => setTokenAmount(e.target.value)}
            style={styles.input}
          />
          <button onClick={sendERC20} style={styles.buttonTertiary}>🚀 Send ERC20</button>

          <hr />
          <h4>📜 Transaction Logs</h4>
          {txLogs.length === 0 ? (
            <p>No transactions yet.</p>
          ) : (
            <ul style={{ listStyle: "none", paddingLeft: 0 }}>
              {txLogs.map((tx, i) => (
                <li key={i}>
                  ✅ <strong>{tx.type}</strong> → <a href={`https://testnet.bscscan.com/tx/${tx.hash}`} target="_blank" rel="noreferrer">{tx.hash.slice(0, 10)}...</a> → {tx.to}
                  {tx.validated && <span style={{ color: "lime" }}> ✔ Admin Validated</span>}
                </li>
              ))}
            </ul>
          )}
        </div>
      )}
    </div>
  );
}

