import React, { useState } from "react";
import Wallet from "./Wallet";

const App = () => {
  const [wallets, setWallets] = useState([{ id: 1 }]);

  const addWallet = () => {
    const newId = wallets.length > 0 ? wallets[wallets.length - 1].id + 1 : 1;
    setWallets([...wallets, { id: newId }]);
  };

  const removeWallet = (id) => {
    setWallets(wallets.filter((wallet) => wallet.id !== id));
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 p-4 text-white">
      <div className="max-w-7xl mx-auto space-y-8">
        <header className="text-center">
          <h1 className="text-4xl font-bold mb-2">ðŸª™ Multi-Wallet Manager</h1>
          <p className="text-lg">Create and manage multiple wallets independently.</p>
        </header>

        <div className="flex justify-center">
          <button
            onClick={addWallet}
            className="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded shadow"
          >
            âž• Add Wallet
          </button>
        </div>

        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          {wallets.map((wallet) => (
            <Wallet
              key={wallet.id}
              id={wallet.id}
              onRemove={() => removeWallet(wallet.id)}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
