import React, { useState, useEffect } from "react";
import {
  Wallet,
  isAddress,
  parseEther,
  formatEther,
  JsonRpcProvider,
  Contract,
  parseUnits,
  formatUnits,
} from "ethers";
import { QRCodeCanvas } from "qrcode.react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const ERC20_ABI = [
  "function balanceOf(address) view returns (uint256)",
  "function transfer(address to, uint256 value) returns (bool)",
  "function decimals() view returns (uint8)",
  "function symbol() view returns (string)",
];

const provider = new JsonRpcProvider("https://bsc-testnet-dataseed.bnbchain.org");

const TOKEN_ADDRESSES = {
  USDT: "0x787A697324dbA4AB965C58CD33c13ff5eeA6295F",
  USDC: "0x342e3aA1248AB77E319e3331C6fD3f1F2d4B36B1",
};

function App() {
  const [walletData, setWalletData] = useState(null);
  const [walletName, setWalletName] = useState("");
  const [searchName, setSearchName] = useState("");
  const [recipient, setRecipient] = useState("");
  const [amount, setAmount] = useState("");
  const [tokenType, setTokenType] = useState("BNB");
  const [balances, setBalances] = useState({ BNB: "Loading...", USDT: "N/A", USDC: "N/A" });
  const [txHashInput, setTxHashInput] = useState("");
  const [txDetails, setTxDetails] = useState(null);
  const [loading, setLoading] = useState({});
  const [showPrivate, setShowPrivate] = useState(false);

  const setLoader = (key, value) => setLoading((prev) => ({ ...prev, [key]: value }));

  const notify = (msg, type = "info") => toast[type](msg);

  const generateAndSaveWallet = async () => {
    if (!walletName.trim()) return notify("Enter a wallet name", "error");
    setLoader("creating", true);
    const wallet = Wallet.createRandom();
    const newWallet = {
      name: walletName,
      address: wallet.address,
      privateKey: wallet.privateKey,
      mnemonic: wallet.mnemonic.phrase,
    };

    try {
      const res = await fetch("http://localhost:5001/api/wallet", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newWallet),
      });

      if (res.ok) {
        notify("✅ Wallet saved!", "success");
        setWalletName("");
      } else notify("❌ Save failed", "error");
    } catch {
      notify("❌ Save error", "error");
    } finally {
      setLoader("creating", false);
    }
  };

  const fetchWalletByName = async () => {
    if (!searchName.trim()) return notify("Enter wallet name to fetch", "error");
    setLoader("fetching", true);
    try {
      const res = await fetch(`http://localhost:5001/api/wallet/${searchName}`);
      const data = await res.json();
      if (data.error) {
        notify("❌ Wallet not found", "error");
        setWalletData(null);
      } else {
        setWalletData(data);
        fetchAllBalances(data.privateKey, data.address);
      }
    } catch {
      notify("❌ Error fetching wallet", "error");
    } finally {
      setLoader("fetching", false);
    }
  };

  const fetchAllBalances = async (privateKey, address) => {
    setLoader("balance", true);
    try {
      const wallet = new Wallet(privateKey, provider);
      const bnb = await provider.getBalance(wallet.address);

      const tokenBalances = await Promise.all(
        Object.entries(TOKEN_ADDRESSES).map(async ([symbol, addr]) => {
          const token = new Contract(addr, ERC20_ABI, wallet);
          const decimals = await token.decimals();
          const bal = await token.balanceOf(wallet.address);
          return [symbol, formatUnits(bal, decimals)];
        })
      );

      const all = Object.fromEntries(tokenBalances);
      setBalances({
        BNB: formatEther(bnb),
        ...all,
      });
    } catch (e) {
      console.error(e);
      setBalances({ BNB: "Error", USDT: "N/A", USDC: "N/A" });
    } finally {
      setLoader("balance", false);
    }
  };

  const sendTokens = async () => {
    if (!walletData?.privateKey || !isAddress(recipient) || !amount || parseFloat(amount) <= 0)
      return notify("❌ Invalid inputs", "error");

    setLoader("sending", true);
    try {
      const wallet = new Wallet(walletData.privateKey, provider);
      const bnbBalance = await provider.getBalance(wallet.address);
      const bnbInEth = parseFloat(formatEther(bnbBalance));
      if (bnbInEth < 0.001) return notify("❌ Not enough BNB for gas", "error");

      let tx;
      if (tokenType === "BNB") {
        tx = await wallet.sendTransaction({ to: recipient, value: parseEther(amount) });
      } else {
        const token = new Contract(TOKEN_ADDRESSES[tokenType], ERC20_ABI, wallet);
        const decimals = await token.decimals();
        tx = await token.transfer(recipient, parseUnits(amount, decimals));
      }
      await tx.wait();
      notify(`✅ ${tokenType} Sent!\nTx: ${tx.hash}`, "success");
      navigator.clipboard.writeText(tx.hash);
      setAmount("");
      fetchAllBalances(walletData.privateKey, walletData.address);
    } catch (e) {
      console.error(e);
      notify("❌ Transaction failed", "error");
    } finally {
      setLoader("sending", false);
    }
  };

  const fetchTransactionDetails = async () => {
    if (!txHashInput) return;
    setLoader("tx", true);
    try {
      const tx = await provider.getTransaction(txHashInput);
      const receipt = await provider.getTransactionReceipt(txHashInput);
      setTxDetails({ ...tx, ...receipt });
    } catch (e) {
      notify("❌ Invalid or failed to fetch", "error");
    } finally {
      setLoader("tx", false);
    }
  };

  const copyAddress = () => {
    if (walletData?.address) {
      navigator.clipboard.writeText(walletData.address);
      notify("📋 Address copied", "info");
    }
  };

  return (
    <div style={styles.container}>
      <ToastContainer position="top-right" />
      <h1 style={styles.title}>🦜 Goan Wallet</h1>

      <section style={styles.section}>
        <input
          placeholder="🖊 Create wallet name"
          value={walletName}
          onChange={(e) => setWalletName(e.target.value)}
          style={styles.input}
        />
        <button onClick={generateAndSaveWallet} style={styles.buttonGreen}>
          {loading.creating ? "⏳ Saving..." : "✅ Generate & Save"}
        </button>
      </section>

      <section style={styles.section}>
        <input
          placeholder="🔍 Load wallet by name"
          value={searchName}
          onChange={(e) => setSearchName(e.target.value)}
          style={styles.input}
        />
        <button onClick={fetchWalletByName} style={styles.buttonBlue}>
          {loading.fetching ? "⏳ Loading..." : "📥 Load Wallet"}
        </button>
      </section>

      {walletData && (
        <div style={styles.walletBox}>
          <h2>{walletData.name}</h2>
          <p onClick={copyAddress} style={{ cursor: "pointer" }}>
            <strong>🏦 Address:</strong> {walletData.address} 📋
          </p>
          <p>
            <strong>🔑 Private Key:</strong>{" "}
            {showPrivate ? walletData.privateKey : "••••••••••••••••••••"}
            <button onClick={() => setShowPrivate(!showPrivate)} style={styles.toggle}>
              {showPrivate ? "🙈 Hide" : "👁 Show"}
            </button>
          </p>

          <h3>📊 Balances</h3>
          <ul style={{ listStyle: "none", padding: 0 }}>
            {Object.entries(balances).map(([symbol, value]) => (
              <li key={symbol}>
                {symbol === "BNB" ? "💰" : symbol === "USDT" ? "💵" : "💸"} {symbol}:{" "}
                <strong>{value}</strong>
              </li>
            ))}
          </ul>

          <div style={{ marginTop: "20px" }}>
            <QRCodeCanvas value={walletData.address} size={130} bgColor="#ffffff" fgColor="#000000" />
            <p>📱 Scan to pay</p>
          </div>

          <h3>🚀 Send Tokens</h3>
          <select value={tokenType} onChange={(e) => setTokenType(e.target.value)} style={styles.input}>
            <option value="BNB">BNB</option>
            <option value="USDT">USDT</option>
            <option value="USDC">USDC</option>
          </select>
          <input
            placeholder="📤 Recipient"
            value={recipient}
            onChange={(e) => setRecipient(e.target.value)}
            style={styles.input}
          />
          <input
            placeholder="💰 Amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            style={styles.input}
          />
          <button onClick={sendTokens} style={styles.buttonOrange}>
            {loading.sending ? "⏳ Sending..." : "🚀 Send"}
          </button>

          <h4 style={{ marginTop: "20px" }}>📦 Transaction Info</h4>
          <input
            placeholder="🔍 Tx Hash"
            value={txHashInput}
            onChange={(e) => setTxHashInput(e.target.value)}
            style={styles.input}
          />
          <button onClick={fetchTransactionDetails} style={styles.buttonPurple}>
            {loading.tx ? "⏳ Fetching..." : "🔍 View Tx Info"}
          </button>

          {txDetails && (
            <div style={styles.txBox}>
              <p><strong>From:</strong> {txDetails.from}</p>
              <p><strong>To:</strong> {txDetails.to}</p>
              <p><strong>Hash:</strong> <a href={`https://testnet.bscscan.com/tx/${txDetails.hash}`} target="_blank" rel="noopener noreferrer">{txDetails.hash}</a></p>
              <p><strong>Block:</strong> {txDetails.blockNumber}</p>
              <p><strong>Status:</strong> {txDetails.status === 1 ? "✅ Success" : "❌ Failed"}</p>
              <p><strong>Gas Used:</strong> {txDetails.gasUsed?.toString()}</p>
              <p><strong>Value:</strong> {txDetails.value ? `${formatEther(txDetails.value)} BNB` : "N/A"}</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

