import React, { useState, useEffect } from "react";
import {
  JsonRpcProvider,
  Wallet,
  isAddress,
  Contract,
  parseUnits,
  formatEther,
  formatUnits,
} from "ethers";
import QRCode from "qrcode.react";

const provider = new JsonRpcProvider("https://data-seed-prebsc-1-s1.binance.org:8545/");
const USDT_ADDRESS = "0xD9C8c4D67e50e0775C8Ff6275567aA18C82B9357";
const USDC_ADDRESS = "0x64544969ed7EBf5f083679233325356EbE738930";

const ERC20_ABI = [
  "function balanceOf(address) view returns (uint256)",
  "function transfer(address to, uint amount) returns (bool)",
  "function decimals() view returns (uint8)",
];

function App() {
  const [walletData, setWalletData] = useState(null);
  const [recipient, setRecipient] = useState("");
  const [amount, setAmount] = useState("");
  const [tokenType, setTokenType] = useState("BNB");
  const [balances, setBalances] = useState({ BNB: "-", USDT: "-", USDC: "-" });
  const [loader, setLoader] = useState({ sending: false });

  const notify = (msg, type = "info") => alert(`${type.toUpperCase()}: ${msg}`);

  const createWallet = () => {
    const wallet = Wallet.createRandom();
    setWalletData({
      address: wallet.address,
      privateKey: wallet.privateKey,
      mnemonic: wallet.mnemonic.phrase,
      wallet,
    });
    fetchAllBalances(wallet.address);
  };

  const fetchAllBalances = async (address) => {
    const bnb = await provider.getBalance(address);
    const usdt = await getERC20Balance(USDT_ADDRESS, address);
    const usdc = await getERC20Balance(USDC_ADDRESS, address);
    setBalances({
      BNB: formatEther(bnb),
      USDT: usdt,
      USDC: usdc,
    });
  };

  const getERC20Balance = async (tokenAddress, userAddress) => {
    const token = new Contract(tokenAddress, ERC20_ABI, provider);
    const balance = await token.balanceOf(userAddress);
    const decimals = await token.decimals();
    return formatUnits(balance, decimals);
  };

  const sendToken = async () => {
    if (!walletData || !isAddress(recipient)) {
      notify("Invalid wallet or recipient", "error");
      return;
    }

    setLoader({ sending: true });

    try {
      const signer = walletData.wallet.connect(provider);

      if (tokenType === "BNB") {
        const tx = await signer.sendTransaction({
          to: recipient,
          value: parseUnits(amount, "ether"),
        });
        await tx.wait();
      } else {
        const tokenAddress = tokenType === "USDT" ? USDT_ADDRESS : USDC_ADDRESS;
        const token = new Contract(tokenAddress, ERC20_ABI, signer);
        const decimals = await token.decimals();
        const tx = await token.transfer(recipient, parseUnits(amount, decimals));
        await tx.wait();
      }

      notify(`${tokenType} sent successfully`, "success");
      setAmount("");
      fetchAllBalances(walletData.address);
    } catch (err) {
      notify("Transfer failed: " + err.message, "error");
    }

    setLoader({ sending: false });
  };

  return (
    <div style={{ padding: 30, fontFamily: "Arial", background: "#f0f8ff" }}>
      <h1 style={{ color: "#2a2a72" }}>🌴 Goan Wallet</h1>
      {!walletData ? (
        <button onClick={createWallet}>Generate Wallet</button>
      ) : (
        <>
          <p><strong>Address:</strong> {walletData.address}</p>
          <p><strong>Private Key:</strong> {walletData.privateKey}</p>
          <p><strong>Mnemonic:</strong> {walletData.mnemonic}</p>

          <h3>QR Codes</h3>
          <div style={{ display: "flex", gap: "20px" }}>
            <div>
              <p>BNB</p>
              <QRCode value={walletData.address} size={128} />
            </div>
            <div>
              <p>USDT</p>
              <QRCode value={walletData.address} size={128} />
            </div>
            <div>
              <p>USDC</p>
              <QRCode value={walletData.address} size={128} />
            </div>
          </div>

          <h3>Balances</h3>
          <ul>
            <li>BNB: {balances.BNB}</li>
            <li>USDT: {balances.USDT}</li>
            <li>USDC: {balances.USDC}</li>
          </ul>

          <div>
            <h3>Send Token</h3>
            <select value={tokenType} onChange={(e) => setTokenType(e.target.value)}>
              <option value="BNB">BNB</option>
              <option value="USDT">USDT</option>
              <option value="USDC">USDC</option>
            </select>
            <input placeholder="Recipient" value={recipient} onChange={(e) => setRecipient(e.target.value)} />
            <input placeholder="Amount" value={amount} onChange={(e) => setAmount(e.target.value)} />
            <button onClick={sendToken} disabled={loader.sending}>
              {loader.sending ? "Sending..." : "Send"}
            </button>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
