import React, { useState, useEffect } from "react";
import { ethers } from "ethers";

function App() {
  const [walletName, setWalletName] = useState("");
  const [wallets, setWallets] = useState({});
  const [selectedWallet, setSelectedWallet] = useState(null);
  const [receiver, setReceiver] = useState("");
  const [amount, setAmount] = useState("");

  useEffect(() => {
    const storedWallets = localStorage.getItem("wallets");
    if (storedWallets) {
      setWallets(JSON.parse(storedWallets));
    }
  }, []);

  const generateWallet = () => {
    const wallet = ethers.Wallet.createRandom();
    const newWallet = {
      name: walletName,
      address: wallet.address,
      privateKey: wallet.privateKey,
      mnemonic: wallet.mnemonic.phrase,
    };

    const updatedWallets = { ...wallets, [walletName]: newWallet };
    setWallets(updatedWallets);
    localStorage.setItem("wallets", JSON.stringify(updatedWallets));
    setSelectedWallet(newWallet);
  };

  const fetchWallet = async () => {
    const wallet = wallets[walletName];
    if (!wallet) {
      alert("Wallet not found");
      return;
    }

    const provider = new ethers.providers.JsonRpcProvider("https://bsc-dataseed.binance.org/");
    const balance = await provider.getBalance(wallet.address);
    const formattedBalance = ethers.utils.formatEther(balance);

    setSelectedWallet({ ...wallet, balance: formattedBalance });
  };

  const sendBNB = async () => {
    const wallet = selectedWallet;
    const provider = new ethers.providers.JsonRpcProvider("https://bsc-dataseed.binance.org/");
    const signer = new ethers.Wallet(wallet.privateKey, provider);

    const tx = {
      to: receiver,
      value: ethers.utils.parseEther(amount),
    };

    try {
      const txResponse = await signer.sendTransaction(tx);
      await txResponse.wait();
      alert("Transaction Successful!");
    } catch (error) {
      alert("Transaction Failed: " + error.message);
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>🦊 <span style={{ color: "#f97316" }}>Mini Web3 Wallet</span></h1>

      <div style={styles.section}>
        <input
          type="text"
          placeholder="Name to create wallet"
          value={walletName}
          onChange={(e) => setWalletName(e.target.value)}
          style={styles.input}
        />
        <button onClick={generateWallet} style={styles.button}>Generate & Save</button>
        <button onClick={fetchWallet} style={styles.button}>Fetch Wallet</button>
      </div>

      {selectedWallet && (
        <div style={styles.walletBox}>
          <h3>🔐 Wallet Details</h3>
          <p><strong>Name:</strong> {selectedWallet.name}</p>
          <p><strong>Address:</strong> {selectedWallet.address}</p>
          <p><strong>Private Key:</strong> {selectedWallet.privateKey}</p>
          <p><strong>Mnemonic:</strong> {selectedWallet.mnemonic}</p>
          <p><strong>Balance:</strong> {selectedWallet.balance || "Fetching..."} BNB</p>

          <h4>🚀 Send BNB</h4>
          <input
            type="text"
            placeholder="Receiver Address"
            value={receiver}
            onChange={(e) => setReceiver(e.target.value)}
            style={{ ...styles.input, width: "100%", marginBottom: "10px" }}
          />
          <input
            type="text"
            placeholder="Amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            style={{ ...styles.input, width: "100%", marginBottom: "10px" }}
          />
          <button onClick={sendBNB} style={{ ...styles.button, width: "100%" }}>Send Tokens</button>
        </div>
      )}
    </div>
  );
}

const styles = {
  container: {
    padding: "40px 20px",
    fontFamily: "'Poppins', sans-serif",
    maxWidth: "800px",
    margin: "auto",
    textAlign: "center",
  },
  heading: {
    fontSize: "2.5rem",
    marginBottom: "30px",
    color: "#1f2937",
  },
  section: {
    marginBottom: "20px",
    display: "flex",
    justifyContent: "center",
    gap: "10px",
    flexWrap: "wrap",
  },
  input: {
    padding: "12px 16px",
    border: "1px solid #d1d5db",
    borderRadius: "8px",
    fontSize: "16px",
    minWidth: "250px",
  },
  button: {
    padding: "12px 20px",
    backgroundColor: "#3b82f6",
    color: "white",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    fontWeight: "600",
    transition: "background 0.3s ease",
  },
  walletBox: {
    marginTop: "30px",
    padding: "25px",
    borderRadius: "12px",
    backgroundColor: "#f3f4f6",
    boxShadow: "0 4px 12px rgba(0,0,0,0.05)",
    textAlign: "left",
  },
};

export default App;
