import React, { useState, useEffect } from "react";
import {
  JsonRpcProvider,
  Contract,
  formatEther,
  parseEther,
  formatUnits,
} from "ethers";
import QRCode from "qrcode.react";
import axios from "axios";

const provider = new JsonRpcProvider("https://data-seed-prebsc-1-s1.binance.org:8545/");

// Example ERC20 ABI (simplified)
const ERC20_ABI = [
  "function balanceOf(address) view returns (uint256)",
  "function transfer(address, uint256) returns (bool)",
  "function decimals() view returns (uint8)",
];

// Replace with your admin wallet
const ADMIN_WALLET = "0xAdminWalletAddressHere";

const tokenAddresses = {
  USDT: "0xYourUSDTAddress",
  USDC: "0xYourUSDCAddress",
};

function App() {
  const [wallet, setWallet] = useState({
    address: "",
    privateKey: "",
    balance: "",
    usdt: "",
    usdc: "",
  });
  const [recipient, setRecipient] = useState("");
  const [amount, setAmount] = useState("");
  const [selectedToken, setSelectedToken] = useState("BNB");
  const [txHash, setTxHash] = useState("");
  const [txDetails, setTxDetails] = useState(null);
  const [statusMsg, setStatusMsg] = useState("");

  useEffect(() => {
    if (wallet.address) {
      fetchBalances();
    }
  }, [wallet.address]);

  const fetchBalances = async () => {
    try {
      const balance = await provider.getBalance(wallet.address);
      const usdtContract = new Contract(tokenAddresses.USDT, ERC20_ABI, provider);
      const usdcContract = new Contract(tokenAddresses.USDC, ERC20_ABI, provider);

      const usdtBal = await usdtContract.balanceOf(wallet.address);
      const usdcBal = await usdcContract.balanceOf(wallet.address);

      setWallet((prev) => ({
        ...prev,
        balance: formatEther(balance),
        usdt: formatUnits(usdtBal, 18),
        usdc: formatUnits(usdcBal, 18),
      }));
    } catch (error) {
      setStatusMsg("Error fetching balances");
    }
  };

  const handleSend = async () => {
    try {
      const signer = new ethers.Wallet(wallet.privateKey, provider);

      if (selectedToken === "BNB") {
        const tx = await signer.sendTransaction({
          to: recipient,
          value: parseEther(amount),
        });
        await tx.wait();
        setTxHash(tx.hash);
        await verifyAndLog(tx.hash);
      } else {
        const tokenAddr = tokenAddresses[selectedToken];
        const contract = new Contract(tokenAddr, ERC20_ABI, signer);
        const decimals = await contract.decimals();
        const tx = await contract.transfer(recipient, parseUnits(amount, decimals));
        await tx.wait();
        setTxHash(tx.hash);
        await verifyAndLog(tx.hash);
      }

      setStatusMsg("Transaction successful!");
      fetchBalances();
    } catch (error) {
      console.error(error);
      setStatusMsg("Transaction failed!");
    }
  };

  const verifyAndLog = async (hash) => {
    const tx = await provider.getTransaction(hash);
    if (tx.to && tx.to.toLowerCase() === ADMIN_WALLET.toLowerCase()) {
      await axios.post("http://localhost:5000/api/transactions", {
        hash,
        from: tx.from,
        to: tx.to,
        amount: selectedToken === "BNB" ? formatEther(tx.value) : amount,
        token: selectedToken,
      });
    }
  };

  const handleTxLookup = async () => {
    try {
      const tx = await provider.getTransaction(txHash);
      setTxDetails(tx);
    } catch (error) {
      setTxDetails(null);
      setStatusMsg("Invalid transaction hash");
    }
  };

  return (
    <div style={{ padding: 20, background: "#f5f5dc", fontFamily: "Arial" }}>
      <h1 style={{ color: "#2e8b57" }}>ðŸŒ´ Goan Wallet</h1>

      <div style={{ marginBottom: 20 }}>
        <input
          placeholder="Wallet Address"
          value={wallet.address}
          onChange={(e) => setWallet({ ...wallet, address: e.target.value })}
        />
        <input
          placeholder="Private Key"
          value={wallet.privateKey}
          onChange={(e) => setWallet({ ...wallet, privateKey: e.target.value })}
        />
        <button onClick={fetchBalances} style={{ marginLeft: 10 }}>Fetch Balances</button>
      </div>

      <div>
        <p>ðŸ’° BNB: {wallet.balance}</p>
        <p>ðŸ’¸ USDT: {wallet.usdt}</p>
        <p>ðŸ’µ USDC: {wallet.usdc}</p>
      </div>

      <div style={{ marginTop: 20, background: "#e6f7ff", padding: 20, borderRadius: 8 }}>
        <h3>Send Tokens</h3>
        <select onChange={(e) => setSelectedToken(e.target.value)} value={selectedToken}>
          <option value="BNB">BNB</option>
          <option value="USDT">USDT</option>
          <option value="USDC">USDC</option>
        </select>
        <input
          placeholder="Recipient Address"
          value={recipient}
          onChange={(e) => setRecipient(e.target.value)}
        />
        <input
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
        />
        <button onClick={handleSend}>Send</button>
      </div>

      <div style={{ marginTop: 20 }}>
        <h3>QR Codes to Receive</h3>
        <div style={{ display: "flex", gap: 20 }}>
          <div>
            <p>BNB</p>
            <QRCode value={wallet.address} />
          </div>
          <div>
            <p>USDT</p>
            <QRCode value={wallet.address} />
          </div>
          <div>
            <p>USDC</p>
            <QRCode value={wallet.address} />
          </div>
        </div>
      </div>

      <div style={{ marginTop: 30 }}>
        <h3>ðŸ”Ž Check Transaction</h3>
        <input
          placeholder="Enter Transaction Hash"
          value={txHash}
          onChange={(e) => setTxHash(e.target.value)}
        />
        <button onClick={handleTxLookup}>Lookup</button>

        {txDetails && (
          <div style={{ marginTop: 10 }}>
            <p>From: {txDetails.from}</p>
            <p>To: {txDetails.to}</p>
            <p>Hash: {txDetails.hash}</p>
            <p>Value: {formatEther(txDetails.value)} BNB</p>
          </div>
        )}
      </div>

      {statusMsg && <p style={{ color: "red", marginTop: 20 }}>{statusMsg}</p>}
    </div>
  );
}

export default App;
