// App.js
import React, { useState } from "react";
import {
  Wallet,
  isAddress,
  parseEther,
  formatEther,
  JsonRpcProvider,
  Contract,
  parseUnits,
  formatUnits,
} from "ethers";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { QRCodeCanvas } from "qrcode.react";
import axios from "axios";

const provider = new JsonRpcProvider("https://bsc-testnet-dataseed.bnbchain.org");

const ERC20_ABI = [
  "function balanceOf(address) view returns (uint256)",
  "function transfer(address to, uint256 value) returns (bool)",
  "function decimals() view returns (uint8)",
  "function symbol() view returns (string)",
];

const TOKEN_ADDRESSES = {
  USDT: "0x787A697324dbA4AB965C58CD33c13ff5eeA6295F",
  USDC: "0x342e3aA1248AB77E319e3331C6fD3f1F2d4B36B1",
};

function App() {
  const [authMode, setAuthMode] = useState("login");
  const [authForm, setAuthForm] = useState({ username: "", email: "", password: "" });
  const [otpForm, setOtpForm] = useState({ email: "", otp: "", newPassword: "" });
  const [user, setUser] = useState(null);

  const notify = (msg, type = "info") => toast[type](msg);

  const handleAuth = async () => {
    try {
      const endpoint = authMode === "signup" ? "/api/auth/signup" : "/api/auth/login";
      const { data } = await axios.post(endpoint, authForm);
      setUser(data.user);
      notify(`${authMode} successful`, "success");
    } catch (err) {
      notify("Auth error: " + (err.response?.data?.message || "Check credentials"), "error");
    }
  };

  const requestOtp = async () => {
    try {
      await axios.post("/api/auth/request-otp", { email: otpForm.email });
      notify("OTP sent to your email", "info");
      setAuthMode("reset");
    } catch (err) {
      notify("Email not found", "error");
    }
  };

  const resetPassword = async () => {
    try {
      await axios.post("/api/auth/reset-password", otpForm);
      notify("Password reset successful", "success");
      setAuthMode("login");
    } catch (err) {
      notify("OTP invalid or expired", "error");
    }
  };

  return (
    <div style={{ padding: 40 }}>
      <ToastContainer />
      {!user ? (
        <div>
          <h1>{authMode === "login" ? "🔐 Login" : authMode === "signup" ? "📝 Signup" : "🔁 Reset Password"}</h1>
          {authMode !== "reset" ? (
            <>
              {authMode === "signup" && (
                <input
                  placeholder="📧 Email"
                  value={authForm.email}
                  onChange={(e) => setAuthForm({ ...authForm, email: e.target.value })}
                  style={{ display: "block", marginBottom: 10 }}
                />
              )}
              <input
                placeholder="👤 Username"
                value={authForm.username}
                onChange={(e) => setAuthForm({ ...authForm, username: e.target.value })}
                style={{ display: "block", marginBottom: 10 }}
              />
              <input
                type="password"
                placeholder="🔐 Password"
                value={authForm.password}
                onChange={(e) => setAuthForm({ ...authForm, password: e.target.value })}
                style={{ display: "block", marginBottom: 10 }}
              />
              <button onClick={handleAuth}>
                {authMode === "login" ? "Login" : "Signup"}
              </button>
              <p>
                {authMode === "login" ? (
                  <>
                    <span onClick={() => setAuthMode("signup")}>Create Account</span> |{' '}
                    <span onClick={() => setAuthMode("forgot")}>Forgot Password?</span>
                  </>
                ) : (
                  <span onClick={() => setAuthMode("login")}>Back to Login</span>
                )}
              </p>
            </>
          ) : authMode === "reset" ? (
            <>
              <input
                placeholder="📧 Email"
                value={otpForm.email}
                onChange={(e) => setOtpForm({ ...otpForm, email: e.target.value })}
                style={{ display: "block", marginBottom: 10 }}
              />
              <input
                placeholder="🔑 OTP"
                value={otpForm.otp}
                onChange={(e) => setOtpForm({ ...otpForm, otp: e.target.value })}
                style={{ display: "block", marginBottom: 10 }}
              />
              <input
                type="password"
                placeholder="🆕 New Password"
                value={otpForm.newPassword}
                onChange={(e) => setOtpForm({ ...otpForm, newPassword: e.target.value })}
                style={{ display: "block", marginBottom: 10 }}
              />
              <button onClick={resetPassword}>Reset Password</button>
              <p><span onClick={() => setAuthMode("login")}>Back to Login</span></p>
            </>
          ) : (
            <>
              <input
                placeholder="📧 Email"
                value={otpForm.email}
                onChange={(e) => setOtpForm({ ...otpForm, email: e.target.value })}
                style={{ display: "block", marginBottom: 10 }}
              />
              <button onClick={requestOtp}>Send OTP</button>
            </>
          )}
        </div>
      ) : (
        <div>
          <h1>👋 Welcome, {user.username}</h1>
          <button onClick={() => setUser(null)}>Logout</button>
          {/* Your wallet UI code goes here after authentication */}
        </div>
      )}
    </div>
  );
}

export default App;
