import React, { useState } from "react";
import {
  Wallet,
  isAddress,
  parseEther,
  JsonRpcProvider,
  formatEther,
  Contract,
  parseUnits
} from "ethers";

// ERC20 Minimal ABI
const erc20ABI = [
  "function balanceOf(address) view returns (uint256)",
  "function transfer(address to, uint256 amount) returns (bool)",
  "function decimals() view returns (uint8)"
];

const provider = new JsonRpcProvider("https://bsc-testnet-dataseed.bnbchain.org");

function App() {
  const [walletData, setWalletData] = useState(null);
  const [walletName, setWalletName] = useState("");
  const [searchName, setSearchName] = useState("");
  const [recipient, setRecipient] = useState("");
  const [amount, setAmount] = useState("");
  const [balance, setBalance] = useState(null);
  const [tokenAddress, setTokenAddress] = useState("");
  const [tokenBalance, setTokenBalance] = useState(null);

  const generateAndSaveWallet = async () => {
    if (!walletName.trim()) return alert("Enter wallet name");

    const wallet = Wallet.createRandom();
    const newWallet = {
      name: walletName,
      address: wallet.address,
      privateKey: wallet.privateKey,
      mnemonic: wallet.mnemonic?.phrase || "",
    };

    try {
      const res = await fetch("http://localhost:5001/api/wallet", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newWallet),
      });

      if (res.ok) {
        alert("‚úÖ Wallet saved!");
        setWalletName("");
      } else {
        alert("‚ùå Failed to save.");
      }
    } catch (err) {
      console.error(err);
      alert("‚ùå Error saving wallet.");
    }
  };

  const fetchWalletByName = async () => {
    if (!searchName.trim()) return alert("Enter wallet name to fetch");

    try {
      const res = await fetch(`http://localhost:5001/api/wallet/${searchName}`);
      const data = await res.json();

      if (data.error) {
        alert("‚ùå Wallet not found");
        setWalletData(null);
        setBalance(null);
        setTokenBalance(null);
      } else {
        setWalletData(data);
        fetchBNBBalance(data.privateKey);
        if (tokenAddress) fetchTokenBalance(data.privateKey, tokenAddress);
      }
    } catch (err) {
      console.error(err);
      alert("‚ùå Fetch error");
    }
  };

  const fetchBNBBalance = async (privateKey) => {
    try {
      const wallet = new Wallet(privateKey, provider);
      const bal = await provider.getBalance(wallet.address);
      setBalance(formatEther(bal));
    } catch (err) {
      console.error(err);
      setBalance(null);
    }
  };

  const sendBNB = async () => {
    if (!walletData?.privateKey || !isAddress(recipient) || !amount)
      return alert("‚ùå Invalid input");

    try {
      const wallet = new Wallet(walletData.privateKey, provider);
      const tx = await wallet.sendTransaction({
        to: recipient,
        value: parseEther(amount),
      });
      await tx.wait();
      alert(`‚úÖ BNB sent!\nHash: ${tx.hash}`);
      fetchBNBBalance(walletData.privateKey);
      setAmount("");
    } catch (err) {
      console.error(err);
      alert("‚ùå BNB send failed");
    }
  };

  const fetchTokenBalance = async (privateKey, tokenAddr) => {
    try {
      const wallet = new Wallet(privateKey, provider);
      const token = new Contract(tokenAddr, erc20ABI, provider);
      const decimals = await token.decimals();
      const bal = await token.balanceOf(wallet.address);
      setTokenBalance((Number(bal) / 10 ** decimals).toFixed(4));
    } catch (err) {
      console.error(err);
      setTokenBalance(null);
    }
  };

  const sendERC20 = async () => {
    if (!walletData?.privateKey || !isAddress(recipient) || !amount || !isAddress(tokenAddress))
      return alert("‚ùå Check inputs");

    try {
      const wallet = new Wallet(walletData.privateKey, provider);
      const token = new Contract(tokenAddress, erc20ABI, wallet);
      const decimals = await token.decimals();
      const tx = await token.transfer(recipient, parseUnits(amount, decimals));
      await tx.wait();
      alert(`‚úÖ Token sent!\nHash: ${tx.hash}`);
      fetchTokenBalance(walletData.privateKey, tokenAddress);
    } catch (err) {
      console.error(err);
      alert("‚ùå Token send failed");
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>ü¶ú Goan Wallet</h1>

      <section style={styles.section}>
        <input
          type="text"
          placeholder="üîß Name to create wallet"
          value={walletName}
          onChange={(e) => setWalletName(e.target.value)}
          style={styles.input}
        />
        <button onClick={generateAndSaveWallet} style={styles.buttonPrimary}>
          üî® Generate & Save
        </button>
      </section>

      <section style={styles.section}>
        <input
          type="text"
          placeholder="üîç Search wallet by name"
          value={searchName}
          onChange={(e) => setSearchName(e.target.value)}
          style={styles.input}
        />
        <button onClick={fetchWalletByName} style={styles.buttonSecondary}>
          üìÇ Load Wallet
        </button>
      </section>

      {walletData && (
        <div style={styles.walletBox}>
          <p><strong>üë§ Name:</strong> {walletData.name}</p>
          <p><strong>üè¶ Address:</strong> {walletData.address}</p>
          <p><strong>üîë Private Key:</strong> {walletData.privateKey}</p>
          <p><strong>üí∞ BNB Balance:</strong> {balance !== null ? `${balance} BNB` : "Loading..."}</p>

          <h3>üì§ Send BNB</h3>
          <input
            type="text"
            placeholder="üßæ Recipient Address"
            value={recipient}
            onChange={(e) => setRecipient(e.target.value)}
            style={styles.input}
          />
          <input
            type="text"
            placeholder="üí∏ Amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            style={styles.input}
          />
          <button onClick={sendBNB} style={styles.buttonTertiary}>‚úàÔ∏è Send BNB</button>

          <hr style={{ margin: "20px 0" }} />

          <h3>üí† ERC20 Token</h3>
          <input
            type="text"
            placeholder="ü™ô Token Contract Address"
            value={tokenAddress}
            onChange={(e) => {
              setTokenAddress(e.target.value);
              if (walletData) fetchTokenBalance(walletData.privateKey, e.target.value);
            }}
            style={styles.input}
          />
          <p><strong>üßæ Token Balance:</strong> {tokenBalance !== null ? tokenBalance : "N/A"}</p>
          <button onClick={sendERC20} style={styles.buttonSecondary}>üì§ Send ERC20</button>
        </div>
      )}
    </div>
  );
}

const styles = {
  container: {
    minHeight: "100vh",
    background: "#111",
    color: "#fff",
    padding: "40px 20px",
    textAlign: "center",
    fontFamily: "Arial, sans-serif",
  },
  heading: { fontSize: "2.4rem", marginBottom: "20px" },
  section: { marginBottom: "25px" },
  input: {
    padding: "12px",
    margin: "8px",
    width: "70%",
    maxWidth: "400px",
    borderRadius: "8px",
    border: "none",
    fontSize: "16px",
  },
  buttonPrimary: {
    padding: "10px 18px",
    background: "#16a34a",
    color: "#fff",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
  },
  buttonSecondary: {
    padding: "10px 18px",
    background: "#f97316",
    color: "#fff",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
  },
  buttonTertiary: {
    padding: "10px 18px",
    background: "#3b82f6",
    color: "#fff",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    marginTop: "10px",
  },
  walletBox: {
    background: "#222",
    borderRadius: "15px",
    padding: "25px",
    marginTop: "20px",
    textAlign: "left",
    maxWidth: "600px",
    marginInline: "auto",
    lineHeight: "1.7",
  },
};

export default App;
