// App.js (UPDATED with 40-second client-side pending status)

import React, { useState, useEffect, useRef } from "react";
import {
  Wallet,
  isAddress,
  parseEther,
  formatEther,
  JsonRpcProvider,
  Contract,
  parseUnits,
  formatUnits,
  Interface,
} from "ethers";
import { QRCodeCanvas } from "qrcode.react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const ERC20_ABI = [
  "function balanceOf(address) view returns (uint256)",
  "function transfer(address to, uint256 value) returns (bool)",
  "function decimals() view returns (uint8)",
  "function symbol() view returns (string)",
  "event Transfer(address indexed from, address indexed to, uint256 value)",
];
const provider = new JsonRpcProvider("https://bsc-testnet-dataseed.bnbchain.org");
const TOKEN_ADDRESSES = {
  USDT: "0x787A697324dbA4AB965C58CD33c13ff5eeA6295F",
  USDC: "0x342e3aA1248AB77E319e3331C6fD3f1F2d4B36B1",
};

// --- UPDATED: StatusIndicator now checks for the temporary client-side pending state ---
const StatusIndicator = ({ status, isNew }) => {
  // If it's a new transaction (within the 40s window), always show pending.
  if (isNew) {
    return <span style={{ color: '#f59e0b', fontWeight: 'bold' }}>‚è≥ Pending</span>;
  }
  // Otherwise, show the real status from the backend.
  if (status === 'confirmed') {
    return <span style={{ color: '#22c55e', fontWeight: 'bold' }}>‚úÖ Confirmed</span>;
  }
  if (status === 'failed') {
    return <span style={{ color: '#ef4444', fontWeight: 'bold' }}>‚ùå Failed</span>;
  }
  // This will show for transactions that are still pending on the backend after 40s.
  return <span style={{ color: '#f59e0b', fontWeight: 'bold' }}>‚è≥ Pending</span>;
};


function App() {
  // --- ADDED: State to track newly sent transactions for the 40s pending display ---
  const [newlySentTxHashes, setNewlySentTxHashes] = useState(new Set());

  // Existing state variables
  const [walletData, setWalletData] = useState(null);
  const [walletName, setWalletName] = useState("");
  const [searchName, setSearchName] = useState("");
  const [recipient, setRecipient] = useState("");
  const [amount, setAmount] = useState("");
  const [tokenType, setTokenType] = useState("BNB");
  const [balances, setBalances] = useState({ BNB: "Loading...", USDT: "N/A", USDC: "N/A" });
  const [txHashInput, setTxHashInput] = useState("");
  const [txDetails, setTxDetails] = useState(null);
  const [ledger, setLedger] = useState([]);
  const [loading, setLoading] = useState({});
  const [showPrivate, setShowPrivate] = useState(false);
  const [showQR, setShowQR] = useState(true);
  const qrRef = useRef();
  const [showHistory, setShowHistory] = useState(false);
  const [globalLedger, setGlobalLedger] = useState([]);

  const setLoader = (key, value) => setLoading((prev) => ({ ...prev, [key]: value }));
  const notify = (msg, type = "info") => toast[type](msg);
  const generateAndSaveWallet = async () => { /* ... no changes ... */
    if (!walletName.trim()) return notify("Enter a wallet name", "error");
    setLoader("creating", true);
    const wallet = Wallet.createRandom();
    const newWallet = { name: walletName, address: wallet.address, privateKey: wallet.privateKey, mnemonic: wallet.mnemonic.phrase };
    try {
      const res = await fetch("http://localhost:5001/api/wallet", {
        method: "POST", headers: { "Content-Type": "application/json" }, body: JSON.stringify(newWallet),
      });
      const data = await res.json();
      if (res.ok) { notify("‚úÖ Wallet saved!", "success"); setWalletName(""); } 
      else { notify(`‚ùå ${data.error || 'Save failed'}`, "error"); }
    } catch { notify("‚ùå Save error", "error"); } 
    finally { setLoader("creating", false); }
  };
  const fetchWalletByName = async () => { /* ... no changes ... */
    if (!searchName.trim()) return notify("Enter wallet name to fetch", "error");
    setLoader("fetching", true);
    setTxDetails(null); 
    setShowHistory(false); 
    try {
      const res = await fetch(`http://localhost:5001/api/wallet/${searchName}`);
      const data = await res.json();
      if (data.error) {
        notify("‚ùå Wallet not found", "error");
        setWalletData(null);
        setLedger([]);
      } else {
        setWalletData(data);
        fetchAllBalances(data.privateKey, data.address);
        fetchLedger(data.address);
      }
    } catch { notify("‚ùå Error fetching wallet", "error"); } 
    finally { setLoader("fetching", false); }
  };
  const fetchAllBalances = async (privateKey, address) => { /* ... no changes ... */
    setLoader("balance", true);
    try {
      const wallet = new Wallet(privateKey, provider);
      const bnb = await provider.getBalance(wallet.address);
      const tokenBalances = await Promise.all(
        Object.entries(TOKEN_ADDRESSES).map(async ([symbol, addr]) => {
          const tokenContract = new Contract(addr, ERC20_ABI, wallet);
          const decimals = await tokenContract.decimals();
          const bal = await tokenContract.balanceOf(wallet.address);
          return [symbol, formatUnits(bal, decimals)];
        })
      );
      const all = Object.fromEntries(tokenBalances);
      setBalances({ BNB: formatEther(bnb), ...all });
    } catch (e) {
      console.error(e);
      setBalances({ BNB: "Error", USDT: "N/A", USDC: "N/A" });
    } finally {
      setLoader("balance", false);
    }
  };

  // --- UPDATED: sendTokens now triggers the 40-second timer ---
  const sendTokens = async () => {
    if (!walletData?.privateKey || !isAddress(recipient) || !amount || parseFloat(amount) <= 0)
      return notify("‚ùå Invalid inputs", "error");
    setLoader("sending", true);
    try {
      const wallet = new Wallet(walletData.privateKey, provider);
      const bnbBalance = await provider.getBalance(wallet.address);
      if (parseFloat(formatEther(bnbBalance)) < 0.001) return notify("‚ùå Not enough BNB for gas", "error");

      let tx;
      if (tokenType === "BNB") {
        tx = await wallet.sendTransaction({ to: recipient, value: parseEther(amount) });
      } else {
        const tokenContract = new Contract(TOKEN_ADDRESSES[tokenType], ERC20_ABI, wallet);
        const decimals = await tokenContract.decimals();
        tx = await tokenContract.transfer(recipient, parseUnits(amount, decimals));
      }

      // --- ADDED: Logic to handle the 40-second pending state ---
      // 1. Add the hash to our temporary set to show "Pending"
      setNewlySentTxHashes(prev => new Set(prev).add(tx.hash));

      // 2. Set a timer to remove it after 40 seconds
      setTimeout(() => {
        setNewlySentTxHashes(prev => {
          const newSet = new Set(prev);
          newSet.delete(tx.hash);
          return newSet;
        });
      }, 40000); // 40 seconds

      await tx.wait(); // still wait for it to be mined before showing success
      notify(`‚úÖ ${tokenType} Sent!`, "success");
      navigator.clipboard.writeText(tx.hash);
      logTransaction(tx.hash, walletData.address, recipient, amount, tokenType);
      setRecipient("");
      setAmount("");
      fetchAllBalances(walletData.privateKey, walletData.address);
    } catch (e) {
      console.error(e);
      notify("‚ùå Transaction failed", "error");
    } finally {
      setLoader("sending", false);
    }
  };

  const logTransaction = async (hash, from, to, amount, token) => { /* ... no changes ... */
    try {
      await fetch("http://localhost:5001/api/ledger", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ walletAddress: from, hash, from, to, amount, token }),
      });
      fetchLedger(from); 
      fetchGlobalLedger();
    } catch (error) {
      console.error("Failed to log transaction:", error);
      notify("Could not save Tx to ledger", "warn");
    }
  };
  const fetchLedger = async (address) => { /* ... no changes ... */
    if (!address) return;
    setLoader("ledger", true);
    try {
      const res = await fetch(`http://localhost:5001/api/ledger/${address}`);
      const data = await res.json();
      setLedger(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error("Failed to fetch ledger:", error);
      setLedger([]);
    } finally {
      setLoader("ledger", false);
    }
  };
  const fetchTransactionDetails = async () => { /* ... no changes ... */
    if (!txHashInput) return;
    setLoader("tx", true);
    setTxDetails(null);
    try {
        const tx = await provider.getTransaction(txHashInput);
        const receipt = await provider.getTransactionReceipt(txHashInput);
        if (!tx || !receipt) throw new Error("Transaction not found or not yet mined.");
        const iface = new Interface(ERC20_ABI);
        const decodedLogs = receipt.logs.map(log => {
            try {
                const tokenSymbol = Object.keys(TOKEN_ADDRESSES).find(key => TOKEN_ADDRESSES[key].toLowerCase() === log.address.toLowerCase());
                if (!tokenSymbol) return null;
                const parsedLog = iface.parseLog(log);
                return { ...parsedLog, tokenSymbol, formattedAmount: formatUnits(parsedLog.args.value, 18) };
            } catch { return null; }
        }).filter(log => log !== null);
        setTxDetails({ ...tx, ...receipt, decodedLogs });
    } catch (e) {
        console.error(e);
        notify(`‚ùå ${e.message || "Invalid or failed to fetch"}`, "error");
    } finally {
        setLoader("tx", false);
    }
  };
  const fetchGlobalLedger = async () => { /* ... no changes ... */
    setLoader("globalLedger", true);
    try {
      const res = await fetch("http://localhost:5001/api/ledger");
      const data = await res.json();
      setGlobalLedger(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error("Failed to fetch global ledger:", error);
      setGlobalLedger([]);
    } finally {
      setLoader("globalLedger", false);
    }
  };
  useEffect(() => { /* ... no changes ... */
    fetchGlobalLedger();
  }, []);
  useEffect(() => { /* ... no changes ... */
    const interval = setInterval(() => {
        console.log("Auto-refreshing transaction data...");
        fetchGlobalLedger();
        if (walletData) {
            fetchLedger(walletData.address);
        }
    }, 30000); // 30 seconds
    return () => clearInterval(interval);
  }, [walletData]);
  const copyAddress = () => { /* ... no changes ... */
    if (walletData?.address) {
      navigator.clipboard.writeText(walletData.address);
      notify("üìã Address copied", "info");
    }
  };
  const downloadQR = () => { /* ... no changes ... */
    const canvas = qrRef.current.querySelector("canvas");
    const pngUrl = canvas.toDataURL("image/png");
    const a = document.createElement("a");
    a.href = pngUrl;
    a.download = `${walletData.name}_qr.png`;
    a.click();
  };

  return (
    <div style={styles.container}>
      <ToastContainer position="top-right" autoClose={3000} hideProgressBar={false} />
      <h1 style={styles.title}>ü¶ú Goan Wallet</h1>

      {/* --- No changes to Create/Load/Wallet Details sections --- */}
      <section style={styles.section}>
        <input placeholder="üñä Create wallet name" value={walletName} onChange={(e) => setWalletName(e.target.value)} style={styles.input} />
        <button onClick={generateAndSaveWallet} style={styles.buttonGreen}>{loading.creating ? "‚è≥ Saving..." : "‚úÖ Generate & Save"}</button>
      </section>
      <section style={styles.section}>
        <input placeholder="üîç Load wallet by name" value={searchName} onChange={(e) => setSearchName(e.target.value)} style={styles.input} />
        <button onClick={fetchWalletByName} style={styles.buttonBlue}>{loading.fetching ? "‚è≥ Loading..." : "üì• Load Wallet"}</button>
      </section>

      {walletData && (
        <div style={styles.walletBox}>
          <h2>{walletData.name}</h2>
          <p onClick={copyAddress} style={{ cursor: "pointer" }}><strong>üè¶ Address:</strong> {walletData.address} üìã</p>
          <p><strong>üîë Private Key:</strong>{" "}{showPrivate ? walletData.privateKey : "‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"}
            <button onClick={() => setShowPrivate(!showPrivate)} style={styles.toggle}>{showPrivate ? "üôà Hide" : "üëÅ Show"}</button>
          </p>
          <h3>üìä Balances</h3>
          <ul style={{ listStyle: "none", padding: 0 }}>
            {Object.entries(balances).map(([symbol, value]) => (<li key={symbol}>{symbol === "BNB" ? "üí∞" : symbol === "USDT" ? "üíµ" : "üí∏"} {symbol}:{" "}<strong>{value}</strong></li>))}
          </ul>
          <div style={{ marginTop: "20px" }}>
            <button onClick={() => setShowQR(!showQR)} style={styles.toggle}>{showQR ? "üôà Hide QR" : "üì± Show QR"}</button>
            {showQR && (<div ref={qrRef} style={{ marginTop: "10px" }}><QRCodeCanvas value={walletData.address} size={130} bgColor="#ffffff" fgColor="#000000" /><p>üì± Scan to pay</p><button onClick={downloadQR} style={styles.buttonBlue}>‚¨áÔ∏è Download QR</button></div>)}
          </div>
          <h3>üöÄ Send Tokens</h3>
          <select value={tokenType} onChange={(e) => setTokenType(e.target.value)} style={styles.input}>
            <option value="BNB">BNB</option><option value="USDT">USDT</option><option value="USDC">USDC</option>
          </select>
          <input placeholder="üì§ Recipient" value={recipient} onChange={(e) => setRecipient(e.target.value)} style={styles.input} />
          <input placeholder="üí∞ Amount" value={amount} onChange={(e) => setAmount(e.target.value)} style={styles.input} />
          <button onClick={sendTokens} style={styles.buttonOrange}>{loading.sending ? "‚è≥ Sending..." : "üöÄ Send"}</button>

          <div style={{display: 'flex', alignItems: 'center', marginTop: "20px"}}>
            <h3>üìú Transaction Ledger</h3>
            <button onClick={() => setShowHistory(!showHistory)} style={styles.toggle}>
                {showHistory ? 'Hide History' : 'Show History'}
            </button>
          </div>

          {showHistory && (
              loading.ledger ? <p>Loading ledger...</p> : (
              <div style={styles.ledgerContainer}>
                {ledger.length > 0 ? (
                  ledger.map((tx) => {
                    const isSent = tx.from.toLowerCase() === walletData.address.toLowerCase();
                    return (
                      <div key={tx.hash} style={styles.ledgerTx}>
                          <p><strong>{isSent ? <span style={{color: '#f97316'}}>[OUT]</span> : <span style={{color: '#16a34a'}}>[IN]</span>}</strong>
                            <a href={`https://testnet.bscscan.com/tx/${tx.hash}`} target="_blank" rel="noopener noreferrer">{`${tx.hash.substring(0, 10)}...`}</a>
                          </p>
                          <p>{isSent ? `To: ${tx.to.substring(0, 10)}...` : `From: ${tx.from.substring(0, 10)}...`}</p>
                          <p><strong>Amount:</strong> {tx.amount} {tx.token}</p>
                          {/* --- UPDATED: Pass the isNew prop to the StatusIndicator --- */}
                          <p><strong>Status:</strong> <StatusIndicator status={tx.status} isNew={newlySentTxHashes.has(tx.hash)} /></p> 
                          <p><strong>Date:</strong> {new Date(tx.createdAt).toLocaleString()}</p>
                      </div>
                    )
                  })
                ) : ( <p>No transactions recorded for this wallet.</p> )}
              </div>
            )
          )}

          {/* --- No changes to the rest of the component --- */}
          <h4 style={{ marginTop: "20px" }}>üì¶ Transaction Info</h4>
          <input placeholder="üîç Tx Hash" value={txHashInput} onChange={(e) => setTxHashInput(e.target.value)} style={styles.input}/>
          <button onClick={fetchTransactionDetails} style={styles.buttonPurple}>{loading.tx ? "‚è≥ Fetching..." : "üîç View Tx Info"}</button>
          {txDetails && ( /* ... no changes ... */
            <div style={styles.txBox}>
              <p><strong>Hash:</strong> <a href={`https://testnet.bscscan.com/tx/${txDetails.hash}`} target="_blank" rel="noopener noreferrer">{txDetails.hash}</a></p>
              <p><strong>Status:</strong> {txDetails.status === 1 ? "‚úÖ Success" : "‚ùå Failed"}</p>
              <p><strong>Block:</strong> {txDetails.blockNumber}</p>
              <p><strong>From:</strong> {txDetails.from}</p>
              <p><strong>To:</strong> {txDetails.to}</p>
              {txDetails.decodedLogs?.length > 0 && (
                <div style={{marginTop: '15px', borderTop: '1px solid #374151', paddingTop: '10px'}}>
                  <h5 style={{margin: '0 0 10px 0'}}><strong>Token Transfers Detected:</strong></h5>
                  {txDetails.decodedLogs.map((log, index) => (
                    <div key={index} style={{background: '#000', padding: '10px', borderRadius: '5px', marginBottom: '5px'}}>
                      <p><strong>From:</strong> {log.args.from}</p>
                      <p><strong>To:</strong> {log.args.to}</p>
                      <p><strong>Amount:</strong> {log.formattedAmount} {log.tokenSymbol}</p>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}
        </div>
      )}

      <div style={{...styles.walletBox, marginTop: "30px"}}>
        <h3>üåç Global Transaction Ledger</h3>
        <p>Showing all transactions from all wallets.</p>
        {loading.globalLedger ? <p>Loading global ledger...</p> : (
              <div style={styles.ledgerContainer}>
                {globalLedger.length > 0 ? (
                  globalLedger.map((tx) => (
                      <div key={tx.hash} style={styles.ledgerTx}>
                          <p><strong>Hash: </strong><a href={`https://testnet.bscscan.com/tx/${tx.hash}`} target="_blank" rel="noopener noreferrer">{`${tx.hash.substring(0, 10)}...`}</a></p>
                          <p><strong>From:</strong> {`${tx.from.substring(0, 12)}...`}</p>
                          <p><strong>To:</strong> {`${tx.to.substring(0, 12)}...`}</p>
                          <p><strong>Amount:</strong> {tx.amount} {tx.token}</p>
                           {/* --- UPDATED: Pass the isNew prop to the StatusIndicator --- */}
                          <p><strong>Status:</strong> <StatusIndicator status={tx.status} isNew={newlySentTxHashes.has(tx.hash)} /></p>
                          <p><strong>Date:</strong> {new Date(tx.createdAt).toLocaleString()}</p>
                      </div>
                  ))
                ) : (
                  <p>No transactions recorded in the global ledger yet.</p>
                )}
              </div>
            )}
      </div>
    </div>
  );
}

