import React, { useState, useEffect, useRef } from "react";
import {
  Wallet,
  isAddress,
  parseEther,
  formatEther,
  JsonRpcProvider,
  Contract,
  parseUnits,
  formatUnits,
} from "ethers";
import { QRCodeCanvas } from "qrcode.react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const ERC20_ABI = [
  "function balanceOf(address) view returns (uint256)",
  "function transfer(address to, uint256 value) returns (bool)",
  "function decimals() view returns (uint8)",
  "function symbol() view returns (string)",
];

const provider = new JsonRpcProvider("https://bsc-testnet-dataseed.bnbchain.org");

const TOKEN_ADDRESSES = {
  USDT: "0x787A697324dbA4AB965C58CD33c13ff5eeA6295F",
  USDC: "0x342e3aA1248AB77E319e3331C6fD3f1F2d4B36B1",
};

function App() {
  const [walletData, setWalletData] = useState(null);
  const [walletName, setWalletName] = useState("");
  const [searchName, setSearchName] = useState("");
  const [recipient, setRecipient] = useState("");
  const [amount, setAmount] = useState("");
  const [tokenType, setTokenType] = useState("BNB");
  const [balances, setBalances] = useState({ BNB: "...", USDT: "...", USDC: "..." });
  const [txHashInput, setTxHashInput] = useState("");
  const [txDetails, setTxDetails] = useState(null);
  const [ledger, setLedger] = useState([]);
  const [loading, setLoading] = useState({});
  const [showPrivate, setShowPrivate] = useState(false);
  const [showQR, setShowQR] = useState(true);
  const qrRef = useRef();

  useEffect(() => {
    const lastWalletData = localStorage.getItem("currentWallet");
    if (lastWalletData) {
      const parsedData = JSON.parse(lastWalletData);
      setWalletData(parsedData);
      setSearchName(parsedData.name);
      handleRefresh(parsedData); // Refresh data on load
    }
  }, []);

  const setLoader = (key, value) => setLoading((prev) => ({ ...prev, [key]: value }));

  const notify = (msg, type = "info") => toast[type](msg, { theme: "dark" });

  const handleRefresh = async (currentWallet = walletData) => {
    if (!currentWallet) return;
    notify("ğŸ”„ Refreshing data...", "info");
    setLoader("refreshing", true);
    await Promise.all([
      fetchAllBalances(currentWallet.privateKey, currentWallet.address),
      fetchLedger(currentWallet.address),
    ]);
    setLoader("refreshing", false);
  };

  const generateAndSaveWallet = async () => {
    if (!walletName.trim()) return notify("Enter a wallet name", "error");
    setLoader("creating", true);
    const wallet = Wallet.createRandom();
    const newWallet = {
      name: walletName,
      address: wallet.address,
      privateKey: wallet.privateKey,
      mnemonic: wallet.mnemonic.phrase,
    };

    try {
      const res = await fetch("http://localhost:5001/api/wallet", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newWallet),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.error || 'Save failed');
      notify("âœ… Wallet saved!", "success");
      setWalletName("");
    } catch (err) {
      notify(`âŒ ${err.message}`, "error");
    } finally {
      setLoader("creating", false);
    }
  };

  const fetchWalletByName = async () => {
    if (!searchName.trim()) return notify("Enter wallet name to fetch", "error");
    setLoader("fetching", true);
    setWalletData(null); // Clear previous wallet data
    localStorage.removeItem("currentWallet");
    localStorage.removeItem("currentLedger");
    try {
      const res = await fetch(`http://localhost:5001/api/wallet/${searchName}`);
      const data = await res.json();
      if (!res.ok) throw new Error(data.error);
      setWalletData(data);
      localStorage.setItem("currentWallet", JSON.stringify(data));
      await handleRefresh(data);
    } catch (err) {
      notify(`âŒ ${err.message}`, "error");
    } finally {
      setLoader("fetching", false);
    }
  };

  const fetchAllBalances = async (privateKey, address) => {
    setLoader("balance", true);
    try {
      const wallet = new Wallet(privateKey, provider);
      const bnbPromise = provider.getBalance(wallet.address);
      const tokenPromises = Object.entries(TOKEN_ADDRESSES).map(async ([symbol, addr]) => {
        const token = new Contract(addr, ERC20_ABI, wallet);
        const [decimals, bal] = await Promise.all([token.decimals(), token.balanceOf(wallet.address)]);
        return [symbol, formatUnits(bal, decimals)];
      });

      const [bnb, tokenBalances] = await Promise.all([bnbPromise, Promise.all(tokenPromises)]);

      setBalances({ BNB: formatEther(bnb), ...Object.fromEntries(tokenBalances) });
    } catch (e) {
      console.error("Balance fetch error:", e);
      setBalances({ BNB: "Error", USDT: "Error", USDC: "Error" });
    } finally {
      setLoader("balance", false);
    }
  };

  const sendTokens = async () => {
    if (!walletData?.privateKey || !isAddress(recipient) || !amount || parseFloat(amount) <= 0)
      return notify("âŒ Invalid inputs", "error");

    setLoader("sending", true);
    let txResponse;
    try {
      const wallet = new Wallet(walletData.privateKey, provider);
      if (parseFloat(formatEther(await provider.getBalance(wallet.address))) < 0.001) 
        throw new Error("Not enough BNB for gas fees");

      if (tokenType === "BNB") {
        txResponse = await wallet.sendTransaction({ to: recipient, value: parseEther(amount) });
      } else {
        const token = new Contract(TOKEN_ADDRESSES[tokenType], ERC20_ABI, wallet);
        const decimals = await token.decimals();
        txResponse = await token.transfer(recipient, parseUnits(amount, decimals));
      }

      notify("â³ Transaction sent, awaiting confirmation...", "info");

      const receipt = await txResponse.wait(); // Wait for transaction to be mined

      if (receipt.status === 1) {
          notify(`âœ… ${tokenType} Sent! Hash: ${receipt.hash.substring(0,12)}...`, "success");
          // Log to backend AFTER it is confirmed
          await logTransaction(receipt.hash, walletData.address, recipient, amount, tokenType);
      } else {
          throw new Error("Transaction failed on-chain");
      }

      setRecipient("");
      setAmount("");
      await handleRefresh(); // Refresh balances and ledger
    } catch (e) {
      console.error("Send error:", e);
      notify(`âŒ ${e.message}`, "error");
    } finally {
      setLoader("sending", false);
    }
  };

  const logTransaction = async (hash, from, to, amount, token) => {
    try {
      const res = await fetch("http://localhost:5001/api/ledger", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ walletAddress: from, hash, from, to, amount, token }),
      });
      if (!res.ok) throw new Error("Backend logging failed");
    } catch (error) {
      console.error("Failed to log transaction:", error);
      notify("âš ï¸ Tx confirmed but failed to save in ledger", "warn");
    }
  };

  const fetchLedger = async (address) => {
    setLoader("ledger", true);
    try {
      const res = await fetch(`http://localhost:5001/api/ledger/${address}`);
      const data = await res.json();
      if (Array.isArray(data)) {
        setLedger(data);
        localStorage.setItem("currentLedger", JSON.stringify(data));
      } else throw new Error("Invalid ledger data");
    } catch (error) {
      console.error("Failed to fetch ledger:", error);
      setLedger([]);
      localStorage.removeItem("currentLedger");
    } finally {
      setLoader("ledger", false);
    }
  };

  const fetchTransactionDetails = async () => {
    if (!txHashInput) return;
    setLoader("tx", true);
    try {
      const tx = await provider.getTransaction(txHashInput);
      const receipt = await provider.getTransactionReceipt(txHashInput);
      setTxDetails({ ...tx, ...receipt });
    } catch (e) {
      notify("âŒ Invalid hash or failed to fetch", "error");
    } finally {
      setLoader("tx", false);
    }
  };

  const copyToClipboard = (text, type) => {
    navigator.clipboard.writeText(text);
    notify(`ğŸ“‹ ${type} copied!`, "info");
  };

  const downloadQR = () => {
    const canvas = qrRef.current.querySelector("canvas");
    const pngUrl = canvas.toDataURL("image/png").replace("image/png", "image/octet-stream");
    const a = document.createElement("a");
    a.href = pngUrl;
    a.download = `${walletData.name}_address_qr.png`;
    a.click();
  };

  return (
    <div style={styles.container}>
      <ToastContainer position="top-right" autoClose={4000} hideProgressBar={false} />
      <h1 style={styles.title}>ğŸ¦œ Goan Wallet</h1>

      <section style={styles.section}>
        <input
          placeholder="ğŸ–Š Create wallet name"
          value={walletName}
          onChange={(e) => setWalletName(e.target.value)}
          style={styles.input}
        />
        <button onClick={generateAndSaveWallet} disabled={loading.creating} style={styles.buttonGreen}>
          {loading.creating ? "â³ Saving..." : "âœ… Generate & Save"}
        </button>
      </section>

      <section style={styles.section}>
        <input
          placeholder="ğŸ” Load wallet by name"
          value={searchName}
          onChange={(e) => setSearchName(e.target.value)}
          style={styles.input}
        />
        <button onClick={fetchWalletByName} disabled={loading.fetching} style={styles.buttonBlue}>
          {loading.fetching ? "â³ Loading..." : "ğŸ“¥ Load Wallet"}
        </button>
      </section>

      {walletData && (
        <div style={styles.walletBox}>
          <h2>{walletData.name}</h2>
          <p style={{ cursor: "pointer" }} onClick={() => copyToClipboard(walletData.address, 'Address')}>
            <strong>ğŸ¦ Address:</strong> {`${walletData.address.substring(0,10)}...${walletData.address.substring(32)}`} ğŸ“‹
          </p>
          <p>
            <strong>ğŸ”‘ Private Key:</strong>{" "}
            {showPrivate ? walletData.privateKey : "â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"}
            <button onClick={() => setShowPrivate(!showPrivate)} style={styles.toggle}>
              {showPrivate ? "ğŸ™ˆ Hide" : "ğŸ‘ Show"}
            </button>
          </p>

          <div style={styles.flexHeader}>
            <h3>ğŸ“Š Balances</h3>
            <button onClick={() => handleRefresh()} style={styles.buttonRefresh} disabled={loading.refreshing}>
                {loading.refreshing ? 'â³' : 'ğŸ”„'} Refresh
            </button>
          </div>
          <ul style={{ listStyle: "none", padding: 0 }}>
            {Object.entries(balances).map(([symbol, value]) => (
              <li key={symbol}>
                {symbol === "BNB" ? "ğŸ’°" : symbol === "USDT" ? "ğŸ’µ" : "ğŸ’¸"} {symbol}:{" "}
                <strong>{value}</strong>
              </li>
            ))}
          </ul>

          <div style={{ marginTop: "20px" }}>
            <button onClick={() => setShowQR(!showQR)} style={styles.toggle}>
              {showQR ? "ğŸ™ˆ Hide QR" : "ğŸ“± Show QR"}
            </button>
            {showQR && (
              <div ref={qrRef} style={{ marginTop: "10px" }}>
                <QRCodeCanvas value={walletData.address} size={130} bgColor="#ffffff" fgColor="#000000" />
                <p>ğŸ“± Scan to pay me</p>
                <button onClick={downloadQR} style={styles.buttonSmallBlue}>â¬‡ï¸ Download</button>
              </div>
            )}
          </div>

          <h3>ğŸš€ Send Tokens</h3>
          <select value={tokenType} onChange={(e) => setTokenType(e.target.value)} style={styles.input}>
            <option value="BNB">BNB</option>
            <option value="USDT">USDT</option>
            <option value="USDC">USDC</option>
          </select>
          <input
            placeholder="ğŸ“¤ Recipient Address"
            value={recipient}
            onChange={(e) => setRecipient(e.target.value)}
            style={styles.input}
          />
          <input
            type="number"
            placeholder="ğŸ’° Amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            style={styles.input}
          />
          <button onClick={sendTokens} disabled={loading.sending} style={styles.buttonOrange}>
            {loading.sending ? "â³ Sending..." : "ğŸš€ Send"}
          </button>

          <div style={styles.flexHeader}>
             <h3 style={{ marginTop: "20px" }}>ğŸ“œ Transaction Ledger</h3>
          </div>
          {loading.ledger ? <p>Loading ledger...</p> : (
            <div style={styles.ledgerContainer}>
              {ledger.length > 0 ? (
                ledger.map((tx) => (
                  <div key={tx.hash} style={styles.ledgerTx}>
                    <div style={styles.ledgerRow}>
                      <span><strong>Hash:</strong> <a href={`https://testnet.bscscan.com/tx/${tx.hash}`} target="_blank" rel="noopener noreferrer">{`${tx.hash.substring(0, 10)}...`}</a></span>
                      <button onClick={() => copyToClipboard(tx.hash, 'Hash')} style={styles.copyButton}>ğŸ“‹ Copy</button>
                    </div>
                    <div style={styles.ledgerRow}><span><strong>To:</strong> {`${tx.to.substring(0, 10)}...`}</span></div>
                    <div style={styles.ledgerRow}><span><strong>Amount:</strong> {tx.amount} {tx.token}</span></div>
                    <div style={styles.ledgerRow}><span><strong>Date:</strong> {new Date(tx.createdAt).toLocaleString()}</span></div>
                  </div>
                ))
              ) : (
                <p>No transactions recorded in the ledger.</p>
              )}
            </div>
          )}

          <h4 style={{ marginTop: "20px" }}>ğŸ“¦ Fetch On-Chain Tx Info</h4>
          <input
            placeholder="ğŸ” Paste any Tx Hash"
            value={txHashInput}
            onChange={(e) => setTxHashInput(e.target.value)}
            style={styles.input}
          />
          <button onClick={fetchTransactionDetails} disabled={loading.tx} style={styles.buttonPurple}>
            {loading.tx ? "â³ Fetching..." : "ğŸ” View Tx"}
          </button>

          {txDetails && (
            <div style={styles.txBox}>
              <p><strong>Status:</strong> {txDetails.status === 1 ? "âœ… Success" : "âŒ Failed"}</p>
              <p><strong>Hash:</strong> <a href={`https://testnet.bscscan.com/tx/${txDetails.hash}`} target="_blank" rel="noopener noreferrer">{txDetails.hash}</a></p>
              <p><strong>Block:</strong> {txDetails.blockNumber}</p>
              <p><strong>From:</strong> {txDetails.from}</p>
              <p><strong>To:</strong> {txDetails.to}</p>
              <p><strong>Value:</strong> {txDetails.value ? `${formatEther(txDetails.value)} BNB` : "N/A"}</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
}


