  container: {
    minHeight: "100vh",
    background: "linear-gradient(to right, #3b82f6, #9333ea)",
    padding: "40px 20px",
    fontFamily: "Segoe UI, sans-serif",
    color: "#fff",
    textAlign: "center",
  },
  title: { fontSize: "2.5rem", marginBottom: "30px" },
  section: { marginBottom: "25px" },
  input: {
    padding: "12px",
    margin: "10px",
    width: "60%",
    maxWidth: "400px",
    borderRadius: "10px",
    border: "none",
    fontSize: "16px",
    backgroundColor: "#f0f0f0",
    color: "#111",
  },
  buttonGreen: { ...baseButton, backgroundColor: "#16a34a" },
  buttonBlue: { ...baseButton, backgroundColor: "#2563eb", marginTop: "10px" },
  buttonSmallBlue: { ...baseButton, backgroundColor: "#2563eb", padding: '8px 16px', fontSize: '14px'},
  buttonOrange: { ...baseButton, backgroundColor: "#f97316", marginTop: "10px" },
  buttonPurple: { ...baseButton, backgroundColor: "#9333ea" },
  buttonRefresh: { ...baseButton, backgroundColor: "#06b6d4", padding: '8px 16px', fontSize: '14px'},
  walletBox: {
    backgroundColor: "#1e293b",
    padding: "30px",
