import React, { useState } from 'react';
import { ethers } from 'ethers';

// ‚úÖ Inline minimal ERC20 ABI
const ERC20_ABI = [
  "function balanceOf(address owner) view returns (uint256)",
  "function transfer(address to, uint amount) returns (bool)",
  "function decimals() view returns (uint8)"
];

export default function App() {
  const [wallets, setWallets] = useState({});
  const [walletName, setWalletName] = useState('');
  const [selectedWallet, setSelectedWallet] = useState('');
  const [bnbBalance, setBNBBalance] = useState('');
  const [tokenBalance, setTokenBalance] = useState('');
  const [txHash, setTxHash] = useState('');
  const [tokenType, setTokenType] = useState('ERC20');
  const [recipient, setRecipient] = useState('');
  const [amount, setAmount] = useState('');

  // ‚úÖ Replace this with your ERC20 token contract address on BSC Testnet
  const ERC20_ADDRESS = '0x787A697324dbA4AB965C58CD33c13ff5eeA6295F'; // YOUR TOKEN HERE

  const provider = new ethers.JsonRpcProvider('https://data-seed-prebsc-1-s1.binance.org:8545');

  const generateWallet = () => {
    const wallet = ethers.Wallet.createRandom();
    const newWallets = {
      ...wallets,
      [walletName]: {
        address: wallet.address,
        privateKey: wallet.privateKey,
        mnemonic: wallet.mnemonic.phrase
      }
    };
    setWallets(newWallets);
    alert(`Wallet generated for ${walletName}`);
  };

  const getSigner = () => {
    if (!selectedWallet || !wallets[selectedWallet]) {
      alert('Select a valid wallet!');
      return null;
    }
    return new ethers.Wallet(wallets[selectedWallet].privateKey, provider);
  };

  const loadWallet = () => {
    if (!wallets[walletName]) {
      alert('Wallet not found');
      return;
    }
    setSelectedWallet(walletName);
    setBNBBalance('');
    setTokenBalance('');
    setTxHash('');
  };

  const refreshBNBBalance = async () => {
    const address = wallets[selectedWallet]?.address;
    if (!address) return;
    const balance = await provider.getBalance(address);
    setBNBBalance(ethers.formatEther(balance));
  };

  const fetchTokenBalance = async () => {
    if (!ERC20_ADDRESS) return;
    const wallet = getSigner();
    if (!wallet) return;
    const contract = new ethers.Contract(ERC20_ADDRESS, ERC20_ABI, wallet);
    const balance = await contract.balanceOf(wallet.address);
    const decimals = await contract.decimals();
    setTokenBalance(ethers.formatUnits(balance, decimals));
  };

  const sendToken = async () => {
    if (!ERC20_ADDRESS || !recipient || !amount) {
      alert('Fill all token fields');
      return;
    }

    const signer = getSigner();
    if (!signer) return;

    const contract = new ethers.Contract(ERC20_ADDRESS, ERC20_ABI, signer);
    try {
      const decimals = await contract.decimals();
      const tx = await contract.transfer(recipient, ethers.parseUnits(amount, decimals));
      await tx.wait();
      setTxHash(tx.hash);
      alert('‚úÖ Transaction Successful!');
      fetchTokenBalance();
    } catch (err) {
      console.error(err);
      alert(`‚ùå Transaction failed\n\n${err.message}`);
    }
  };

  const viewTransaction = async () => {
    if (!txHash) return;
    const tx = await provider.getTransaction(txHash);
    alert(JSON.stringify(tx, null, 2));
  };

  return (
    <div style={{ padding: 20, fontFamily: 'monospace', backgroundColor: '#111', color: '#fff', minHeight: '100vh' }}>
      <h2>ü™ô Goan Wallet</h2>

      <input placeholder="Wallet name" value={walletName} onChange={e => setWalletName(e.target.value)} />
      <button onClick={generateWallet}>Generate Wallet</button>
      <button onClick={loadWallet}>Load Wallet</button>

      {selectedWallet && (
        <div style={{ marginTop: 20 }}>
          <h3>üìå {selectedWallet}</h3>
          <p>üîë Address: {wallets[selectedWallet].address}</p>
          <p>üîê Private Key: {wallets[selectedWallet].privateKey}</p>
          <p>üß† Mnemonic: {wallets[selectedWallet].mnemonic}</p>
          <p>BNB: {bnbBalance}</p>
          <button onClick={refreshBNBBalance}>Refresh BNB</button>
        </div>
      )}

      <div style={{ marginTop: 30 }}>
        <h3>üöÄ Send Tokens</h3>
        <select value={tokenType} onChange={e => setTokenType(e.target.value)}>
          <option value="ERC20">ERC20</option>
        </select>
        <p>üéØ Token: {ERC20_ADDRESS}</p>
        <button onClick={fetchTokenBalance}>Check Token Balance</button>
        <p>Balance: {tokenBalance}</p>
        <input placeholder="Recipient address" value={recipient} onChange={e => setRecipient(e.target.value)} />
        <input placeholder="Amount" value={amount} onChange={e => setAmount(e.target.value)} />
        <button onClick={sendToken}>Send</button>
      </div>

      <div style={{ marginTop: 30 }}>
        <h3>üîç View Tx Details</h3>
        <input placeholder="Transaction Hash" value={txHash} onChange={e => setTxHash(e.target.value)} />
        <button onClick={viewTransaction}>View</button>
      </div>
    </div>
  );
}
