import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import ERC20ABI from './ERC20ABI.json';

const provider = new ethers.JsonRpcProvider('https://data-seed-prebsc-1-s1.binance.org:8545/');

// Define token contract addresses
const ERC20_ADDRESS = '0xYourERC20TokenAddress';
const USDT_ADDRESS = '0xYourUSDTTokenAddress';

export default function App() {
  const [wallets, setWallets] = useState({});
  const [walletName, setWalletName] = useState('');
  const [activeWallet, setActiveWallet] = useState('');
  const [tokenType, setTokenType] = useState('ERC20');
  const [tokenContract, setTokenContract] = useState(null);
  const [balance, setBalance] = useState('0');
  const [recipient, setRecipient] = useState('');
  const [amount, setAmount] = useState('');
  const [bnbBalance, setBnbBalance] = useState('');
  const [txHash, setTxHash] = useState('');
  const [txDetails, setTxDetails] = useState(null);

  useEffect(() => {
    if (tokenType && activeWallet) {
      getTokenBalance();
    }
  }, [tokenType, activeWallet]);

  const generateWallet = () => {
    const wallet = ethers.Wallet.createRandom();
    const walletData = {
      address: wallet.address,
      privateKey: wallet.privateKey,
      mnemonic: wallet.mnemonic.phrase,
    };
    setWallets({ ...wallets, [walletName]: walletData });
    setActiveWallet(walletName);
  };

  const loadWallet = () => {
    setActiveWallet(walletName);
  };

  const getWallet = () => {
    if (!wallets[activeWallet]) return null;
    return new ethers.Wallet(wallets[activeWallet].privateKey, provider);
  };

  const getBnbBalance = async () => {
    const wallet = getWallet();
    if (!wallet) return;
    const balance = await provider.getBalance(wallet.address);
    setBnbBalance(ethers.formatEther(balance));
  };

  const getTokenAddress = () => {
    if (tokenType === 'ERC20') return ERC20_ADDRESS;
    if (tokenType === 'USDT') return USDT_ADDRESS;
    return null;
  };

  const getTokenBalance = async () => {
    const wallet = getWallet();
    if (!wallet) return;
    const address = getTokenAddress();
    if (!address) return;
    const contract = new ethers.Contract(address, ERC20ABI, wallet);
    const bal = await contract.balanceOf(wallet.address);
    setBalance(ethers.formatUnits(bal, 18));
    setTokenContract(contract);
  };

  const sendToken = async () => {
    try {
      const wallet = getWallet();
      if (!wallet || !tokenContract) return alert('Wallet not loaded');

      const tx = await tokenContract.transfer(
        recipient,
        ethers.parseUnits(amount, 18)
      );
      await tx.wait();
      alert(`‚úÖ Token sent!\nTx Hash: ${tx.hash}`);
    } catch (err) {
      alert(`‚ùå Transaction failed\n${err.message}`);
    }
  };

  const checkTransaction = async () => {
    try {
      const tx = await provider.getTransactionReceipt(txHash);
      if (!tx) return alert('Transaction not found.');
      setTxDetails(tx);
    } catch (err) {
      alert('‚ùå Error fetching tx: ' + err.message);
    }
  };

  return (
    <div style={{ padding: '2rem', background: '#111', color: '#eee', fontFamily: 'Arial' }}>
      <h2>ü™ô Goan Wallet</h2>

      <input placeholder="Wallet name" onChange={e => setWalletName(e.target.value)} />
      <button onClick={generateWallet}>Generate Wallet</button>
      <button onClick={loadWallet}>Load Wallet</button>

      {activeWallet && wallets[activeWallet] && (
        <div>
          <h3>üë§ {activeWallet}</h3>
          <p>üì¨ {wallets[activeWallet].address}</p>
          <p>üîë {wallets[activeWallet].privateKey}</p>
          <p>üìù {wallets[activeWallet].mnemonic}</p>

          <h4>BNB: {bnbBalance}</h4>
          <button onClick={getBnbBalance}>Refresh BNB</button>

          <hr />
          <h3>üöÄ Send Tokens</h3>
          <select onChange={e => setTokenType(e.target.value)} value={tokenType}>
            <option value="ERC20">ERC20</option>
            <option value="USDT">USDT</option>
          </select>
          <p>üì¶ Balance: {balance}</p>
          <button onClick={getTokenBalance}>Refresh Token Balance</button>

          <input placeholder="Recipient" onChange={e => setRecipient(e.target.value)} />
          <input placeholder="Amount" onChange={e => setAmount(e.target.value)} />
          <button onClick={sendToken}>Send</button>

          <hr />
          <h3>üîç View Tx Details</h3>
          <input placeholder="Transaction Hash" onChange={e => setTxHash(e.target.value)} />
          <button onClick={checkTransaction}>View</button>
          {txDetails && (
            <pre style={{ background: '#222', padding: '1rem', marginTop: '1rem' }}>
              {JSON.stringify(txDetails, null, 2)}
            </pre>
          )}
        </div>
      )}
    </div>
  );
}
