import React, { useState } from "react";
import {
  Wallet,
  isAddress,
  parseEther,
  formatEther,
  JsonRpcProvider,
  Contract,
  parseUnits,
  formatUnits,
} from "ethers";

const ERC20_ABI = [
  "function balanceOf(address) view returns (uint256)",
  "function transfer(address to, uint256 value) returns (bool)",
  "function decimals() view returns (uint8)",
  "function symbol() view returns (string)"
];

const provider = new JsonRpcProvider("https://bsc-testnet.publicnode.com");
const USDT_ADDRESS = "0x7ef95a0Fe8fE1fBf9fcdAE4D9b62b2d0D8959B8A";

function App() {
  const [walletData, setWalletData] = useState(null);
  const [walletName, setWalletName] = useState("");
  const [searchName, setSearchName] = useState("");
  const [balance, setBalance] = useState(null);

  const [recipient, setRecipient] = useState("");
  const [amount, setAmount] = useState("");

  const [tokenType, setTokenType] = useState("BNB");
  const [tokenAddress, setTokenAddress] = useState("");
  const [tokenBalance, setTokenBalance] = useState("N/A");

  const [txHashInput, setTxHashInput] = useState("");
  const [txDetails, setTxDetails] = useState(null);

  const generateAndSaveWallet = async () => {
    if (!walletName.trim()) return alert("Enter wallet name");

    const wallet = Wallet.createRandom();
    const newWallet = {
      name: walletName,
      address: wallet.address,
      privateKey: wallet.privateKey,
      mnemonic: wallet.mnemonic.phrase,
    };

    const res = await fetch("http://localhost:5001/api/wallet", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newWallet),
    });

    if (res.ok) {
      alert("✅ Wallet saved!");
      setWalletName("");
    } else {
      alert("❌ Failed to save wallet");
    }
  };

  const fetchWalletByName = async () => {
    if (!searchName.trim()) return alert("Enter wallet name to load");

    const res = await fetch(`http://localhost:5001/api/wallet/${searchName}`);
    const data = await res.json();

    if (data.error) {
      alert("❌ Wallet not found");
      setWalletData(null);
    } else {
      setWalletData(data);
      fetchBalance(data.privateKey);
    }
  };

  const fetchBalance = async (privateKey = walletData?.privateKey) => {
    if (!privateKey) return;

    const wallet = new Wallet(privateKey, provider);
    const bnb = await provider.getBalance(wallet.address);
    setBalance(formatEther(bnb));
  };

  const fetchTokenBalance = async () => {
    if (!walletData) return;

    try {
      const wallet = new Wallet(walletData.privateKey, provider);
      const address = tokenType === "USDT" ? USDT_ADDRESS : tokenAddress;
      const contract = new Contract(address, ERC20_ABI, wallet);
      const decimals = await contract.decimals();
      const balance = await contract.balanceOf(wallet.address);
      setTokenBalance(formatUnits(balance, decimals));
    } catch (err) {
      console.error("❌ Error fetching token balance:", err);
      setTokenBalance("N/A");
    }
  };

  const sendTokens = async () => {
    if (!walletData?.privateKey || !isAddress(recipient) || !amount || parseFloat(amount) <= 0) {
      return alert("❌ Invalid inputs");
    }

    try {
      const wallet = new Wallet(walletData.privateKey, provider);

      if (tokenType === "BNB") {
        const tx = await wallet.sendTransaction({
          to: recipient,
          value: parseEther(amount),
        });
        await tx.wait();
        alert(`✅ BNB Sent!\nTx Hash: ${tx.hash}`);
      } else {
        const contractAddress = tokenType === "USDT" ? USDT_ADDRESS : tokenAddress;
        const contract = new Contract(contractAddress, ERC20_ABI, wallet);

        const decimals = await contract.decimals();
        const value = parseUnits(amount, decimals);

        const tx = await contract.transfer(recipient, value, {
          gasLimit: 100_000, // ensure gasLimit is set
        });

        await tx.wait();
        alert(`✅ Token Sent!\nTx Hash: ${tx.hash}`);
        fetchTokenBalance();
      }

      fetchBalance(wallet.privateKey);
      setAmount("");
    } catch (err) {
      console.error("❌ Tx Error:", err);
      if (err?.reason) alert(`❌ Tx failed: ${err.reason}`);
      else if (err?.message) alert(`❌ Tx failed: ${err.message}`);
      else alert("❌ Transaction failed");
    }
  };

  const fetchTransactionDetails = async () => {
    if (!txHashInput) return;

    try {
      const tx = await provider.getTransaction(txHashInput);
      const receipt = await provider.getTransactionReceipt(txHashInput);
      setTxDetails({ ...tx, ...receipt });
    } catch (err) {
      console.error("❌ Error fetching tx:", err);
      alert("❌ Invalid transaction hash or fetch failed");
    }
  };

  return (
    <div style={styles.container}>
      <h1>🦜 Goan Wallet</h1>

      {/* Wallet Creation & Loading */}
      <div style={styles.section}>
        <input
          placeholder="Enter Wallet Name"
          value={walletName}
          onChange={(e) => setWalletName(e.target.value)}
          style={styles.input}
        />
        <button onClick={generateAndSaveWallet} style={styles.buttonPrimary}>Generate & Save</button>
      </div>

      <div style={styles.section}>
        <input
          placeholder="Search Wallet"
          value={searchName}
          onChange={(e) => setSearchName(e.target.value)}
          style={styles.input}
        />
        <button onClick={fetchWalletByName} style={styles.buttonSecondary}>Load Wallet</button>
      </div>

      {/* Wallet Info */}
      {walletData && (
        <div style={styles.walletBox}>
          <p>👤 Name: {walletData.name}</p>
          <p>📬 Address: {walletData.address}</p>
          <p>🔐 Private Key: {walletData.privateKey}</p>
          <p>💰 BNB Balance: {balance ?? "Loading..."}</p>
          <button onClick={() => fetchBalance(walletData.privateKey)} style={styles.buttonSecondary}>🔁 Refresh BNB</button>

          <h4>🚀 Send Tokens</h4>
          <select value={tokenType} onChange={(e) => setTokenType(e.target.value)} style={styles.input}>
            <option value="BNB">BNB</option>
            <option value="ERC20">ERC20</option>
            <option value="USDT">USDT</option>
          </select>

          {tokenType === "ERC20" && (
            <input
              placeholder="ERC20 Token Address"
              value={tokenAddress}
              onChange={(e) => setTokenAddress(e.target.value)}
              style={styles.input}
            />
          )}

          {(tokenType === "ERC20" || tokenType === "USDT") && (
            <>
              <p>🔢 Token Balance: {tokenBalance}</p>
              <button onClick={fetchTokenBalance} style={styles.buttonSecondary}>🔁 Refresh Token Balance</button>
            </>
          )}

          <input
            placeholder="Recipient Address"
            value={recipient}
            onChange={(e) => setRecipient(e.target.value)}
            style={styles.input}
          />
          <input
            placeholder="Amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            style={styles.input}
          />
          <button onClick={sendTokens} style={styles.buttonTertiary}>Send</button>

          <hr />

          <h4>📦 View Transaction</h4>
          <input
            placeholder="Enter Tx Hash"
            value={txHashInput}
            onChange={(e) => setTxHashInput(e.target.value)}
            style={styles.input}
          />
          <button onClick={fetchTransactionDetails} style={styles.buttonPrimary}>View Tx Info</button>

          {txDetails && (
            <div style={{ marginTop: "15px", fontSize: "14px", background: "#333", padding: "15px", borderRadius: "8px" }}>
              <p><strong>From:</strong> {txDetails.from}</p>
              <p><strong>To:</strong> {txDetails.to}</p>
              <p><strong>Hash:</strong> {txDetails.hash}</p>
              <p><strong>Status:</strong> {txDetails.status === 1 ? "✅ Success" : "❌ Failed"}</p>
              <p><strong>Block:</strong> {txDetails.blockNumber}</p>
              <p><strong>Gas Used:</strong> {txDetails.gasUsed?.toString()}</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

