import React, { useState } from "react";
import {
  Wallet,
  isAddress,
  parseEther,
  formatEther,
  JsonRpcProvider,
  Contract,
  parseUnits,
  formatUnits
} from "ethers";

const ERC20_ABI = [
  "function balanceOf(address) view returns (uint256)",
  "function transfer(address to, uint256 amount) returns (bool)",
  "function decimals() view returns (uint8)",
  "function symbol() view returns (string)"
];

const provider = new JsonRpcProvider("https://bsc-testnet.publicnode.com");
const USDT_ADDRESS = "0x7ef95a0Fe8fE1fBf9fcdAE4D9b62b2d0D8959B8A"; // BSC testnet USDT

function App() {
  const [walletName, setWalletName] = useState("");
  const [searchName, setSearchName] = useState("");
  const [walletData, setWalletData] = useState(null);
  const [balance, setBalance] = useState(null);
  const [recipient, setRecipient] = useState("");
  const [amount, setAmount] = useState("");
  const [tokenType, setTokenType] = useState("BNB");
  const [tokenAddress, setTokenAddress] = useState("");
  const [tokenDecimals, setTokenDecimals] = useState(18);
  const [tokenBalance, setTokenBalance] = useState("N/A");
  const [txHashInput, setTxHashInput] = useState("");
  const [txDetails, setTxDetails] = useState(null);

  const generateWallet = async () => {
    if (!walletName.trim()) return alert("Enter wallet name");
    const wallet = Wallet.createRandom();
    const newWallet = {
      name: walletName,
      address: wallet.address,
      privateKey: wallet.privateKey,
      mnemonic: wallet.mnemonic.phrase,
    };

    try {
      await fetch("http://localhost:5001/api/wallet", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newWallet),
      });
      alert("‚úÖ Wallet created & saved");
      setWalletName("");
    } catch (err) {
      console.error(err);
      alert("‚ùå Wallet save failed");
    }
  };

  const loadWallet = async () => {
    try {
      const res = await fetch(`http://localhost:5001/api/wallet/${searchName}`);
      const data = await res.json();
      if (data.error) return alert("‚ùå Wallet not found");
      setWalletData(data);
      fetchBNBBalance(data.privateKey);
    } catch (err) {
      alert("‚ùå Failed to load wallet");
    }
  };

  const fetchBNBBalance = async (pk) => {
    try {
      const wallet = new Wallet(pk, provider);
      const bal = await provider.getBalance(wallet.address);
      setBalance(formatEther(bal));
    } catch (err) {
      setBalance("Error");
    }
  };

  const fetchTokenBalance = async () => {
    if (!walletData) return;
    try {
      const address = tokenType === "USDT" ? USDT_ADDRESS : tokenAddress;
      const wallet = new Wallet(walletData.privateKey, provider);
      const contract = new Contract(address, ERC20_ABI, wallet);

      const decimals = await contract.decimals();
      const bal = await contract.balanceOf(wallet.address);
      setTokenDecimals(decimals);
      setTokenBalance(formatUnits(bal, decimals));
    } catch (err) {
      console.error("Fetch token balance failed:", err);
      setTokenBalance("N/A");
    }
  };

  const sendTokens = async () => {
    if (!walletData || !recipient || !amount) return alert("Missing fields");

    try {
      const wallet = new Wallet(walletData.privateKey, provider);

      if (tokenType === "BNB") {
        const tx = await wallet.sendTransaction({
          to: recipient,
          value: parseEther(amount),
        });
        await tx.wait();
        alert("‚úÖ BNB Sent!");
      } else {
        const address = tokenType === "USDT" ? USDT_ADDRESS : tokenAddress;
        const contract = new Contract(address, ERC20_ABI, wallet);
        const decimals = await contract.decimals();

        const parsedAmount = parseUnits(amount.replace(/^0+/, ""), decimals);
        const tx = await contract.transfer(recipient, parsedAmount);
        await tx.wait();

        alert("‚úÖ Token Sent!");
      }

      fetchBNBBalance(wallet.privateKey);
      fetchTokenBalance();
    } catch (err) {
      console.error("Transaction failed:", err);
      alert("‚ùå Transaction failed\n" + (err?.reason || err?.message || "Unknown error"));
    }
  };

  const viewTransaction = async () => {
    try {
      const tx = await provider.getTransaction(txHashInput);
      const receipt = await provider.getTransactionReceipt(txHashInput);
      setTxDetails({ ...tx, ...receipt });
    } catch (err) {
      alert("‚ùå Failed to fetch tx details");
    }
  };

  return (
    <div style={{ background: "#111", color: "#fff", padding: "40px", fontFamily: "Arial" }}>
      <h2>ü¶ú Goan Wallet</h2>

      <div style={{ marginBottom: "20px" }}>
        <input placeholder="Wallet name" value={walletName} onChange={(e) => setWalletName(e.target.value)} />
        <button onClick={generateWallet}>Generate Wallet</button>
      </div>

      <div style={{ marginBottom: "20px" }}>
        <input placeholder="Search wallet name" value={searchName} onChange={(e) => setSearchName(e.target.value)} />
        <button onClick={loadWallet}>Load Wallet</button>
      </div>

      {walletData && (
        <>
          <div>
            <p>üë§ <b>{walletData.name}</b></p>
            <p>üîë {walletData.privateKey}</p>
            <p>üè¶ {walletData.address}</p>
            <p>üí∞ BNB: {balance ?? "Loading..."}</p>
            <button onClick={() => fetchBNBBalance(walletData.privateKey)}>Refresh BNB</button>
          </div>

          <hr />
          <h3>üöÄ Send Tokens</h3>
          <select value={tokenType} onChange={(e) => setTokenType(e.target.value)}>
            <option value="BNB">BNB</option>
            <option value="ERC20">ERC20</option>
            <option value="USDT">USDT</option>
          </select>

          {tokenType !== "BNB" && (
            <>
              <input
                placeholder="Token address"
                value={tokenType === "ERC20" ? tokenAddress : USDT_ADDRESS}
                onChange={(e) => setTokenAddress(e.target.value)}
                onBlur={fetchTokenBalance}
              />
              <p>üéØ Balance: {tokenBalance}</p>
            </>
          )}

          <input placeholder="Recipient" value={recipient} onChange={(e) => setRecipient(e.target.value)} />
          <input placeholder="Amount" value={amount} onChange={(e) => setAmount(e.target.value)} />
          <button onClick={sendTokens}>Send</button>

          <hr />
          <h3>üîç View Tx Details</h3>
          <input placeholder="Transaction Hash" value={txHashInput} onChange={(e) => setTxHashInput(e.target.value)} />
          <button onClick={viewTransaction}>View</button>

          {txDetails && (
            <div style={{ marginTop: "10px", fontSize: "14px" }}>
              <p><strong>From:</strong> {txDetails.from}</p>
              <p><strong>To:</strong> {txDetails.to}</p>
              <p><strong>Hash:</strong> {txDetails.hash}</p>
              <p><strong>Status:</strong> {txDetails.status === 1 ? "‚úÖ Success" : "‚ùå Failed"}</p>
              <p><strong>Block:</strong> {txDetails.blockNumber}</p>
              <p><strong>Gas:</strong> {txDetails.gasUsed?.toString()}</p>
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default App;
