import React, { useState } from "react";
import {
  Wallet,
  isAddress,
  parseEther,
  formatEther,
  JsonRpcProvider,
  Contract,
  parseUnits,
  formatUnits
} from "ethers";

const ERC20_ABI = [
  "function balanceOf(address) view returns (uint256)",
  "function transfer(address to, uint256 value) returns (bool)",
  "function decimals() view returns (uint8)",
  "function symbol() view returns (string)"
];

const provider = new JsonRpcProvider("https://bsc-testnet-dataseed.bnbchain.org");

// USDC on BSC Testnet
const USDC_ADDRESS = "0x64544969ed7EBf5f083679233325356EbE738930";

function App() {
  const [walletData, setWalletData] = useState(null);
  const [walletName, setWalletName] = useState("");
  const [searchName, setSearchName] = useState("");
  const [recipient, setRecipient] = useState("");
  const [amount, setAmount] = useState("");
  const [balance, setBalance] = useState(null);
  const [tokenType, setTokenType] = useState("BNB");

  const [tokenBalance, setTokenBalance] = useState("N/A");
  const [tokenDecimals, setTokenDecimals] = useState(18);
  const [txHashInput, setTxHashInput] = useState("");
  const [txDetails, setTxDetails] = useState(null);

  const generateAndSaveWallet = async () => {
    if (!walletName.trim()) return alert("Enter a wallet name");

    const wallet = Wallet.createRandom();
    const newWallet = {
      name: walletName,
      address: wallet.address,
      privateKey: wallet.privateKey,
      mnemonic: wallet.mnemonic.phrase,
    };

    try {
      const res = await fetch("http://localhost:5001/api/wallet", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newWallet),
      });

      if (res.ok) {
        alert("‚úÖ Wallet saved!");
        setWalletName("");
      } else {
        alert("‚ùå Save failed");
      }
    } catch (err) {
      alert("‚ùå Save error");
    }
  };

  const fetchWalletByName = async () => {
    if (!searchName.trim()) return alert("Enter wallet name to fetch");

    try {
      const res = await fetch(`http://localhost:5001/api/wallet/${searchName}`);
      const data = await res.json();

      if (data.error) {
        alert("‚ùå Wallet not found");
        setWalletData(null);
        setBalance(null);
      } else {
        setWalletData(data);
        fetchBalance(data.privateKey);
      }
    } catch (err) {
      alert("‚ùå Error fetching wallet");
    }
  };

  const fetchBalance = async (privateKey = walletData?.privateKey) => {
    if (!privateKey) return;

    try {
      const wallet = new Wallet(privateKey, provider);
      const bal = await provider.getBalance(wallet.address);
      setBalance(formatEther(bal));
    } catch {
      setBalance(null);
    }
  };

  const sendTokens = async () => {
    if (!walletData?.privateKey || !isAddress(recipient) || !amount || parseFloat(amount) <= 0) {
      return alert("‚ùå Invalid inputs");
    }

    try {
      const wallet = new Wallet(walletData.privateKey, provider);

      if (tokenType === "BNB") {
        const tx = await wallet.sendTransaction({
          to: recipient,
          value: parseEther(amount),
        });
        await tx.wait();
        alert(`‚úÖ BNB Sent!\nTx: ${tx.hash}`);
      } else {
        const contract = new Contract(USDC_ADDRESS, ERC20_ABI, wallet);
        const decimals = await contract.decimals();
        const tx = await contract.transfer(recipient, parseUnits(amount, decimals));
        await tx.wait();
        alert(`‚úÖ USDC Sent!\nTx: ${tx.hash}`);
      }

      setAmount("");
      fetchBalance(walletData.privateKey);
    } catch (err) {
      console.error("Tx Error:", err);
      alert("‚ùå Transaction failed");
    }
  };

  const fetchTokenBalance = async () => {
    if (!walletData) return;

    try {
      const wallet = new Wallet(walletData.privateKey, provider);
      const token = new Contract(USDC_ADDRESS, ERC20_ABI, wallet);

      const decimals = await token.decimals();
      const balance = await token.balanceOf(wallet.address);

      setTokenDecimals(decimals);
      setTokenBalance(formatUnits(balance, decimals));
    } catch (err) {
      console.error("Token balance error", err);
      setTokenBalance("N/A");
    }
  };

  const fetchTransactionDetails = async () => {
    if (!txHashInput) return;
    try {
      const tx = await provider.getTransaction(txHashInput);
      const receipt = await provider.getTransactionReceipt(txHashInput);
      setTxDetails({ ...tx, ...receipt });
    } catch (err) {
      alert("‚ùå Invalid transaction hash or failed to fetch");
    }
  };

  return (
    <div style={styles.container}>
      <h1>ü¶ú Goan Wallet</h1>

      <section style={styles.section}>
        <input
          placeholder="üñä Name to create wallet"
          value={walletName}
          onChange={(e) => setWalletName(e.target.value)}
          style={styles.input}
        />
        <button onClick={generateAndSaveWallet} style={styles.buttonPrimary}>‚úÖ Generate & Save</button>
      </section>

      <section style={styles.section}>
        <input
          placeholder="üîç Load wallet by name"
          value={searchName}
          onChange={(e) => setSearchName(e.target.value)}
          style={styles.input}
        />
        <button onClick={fetchWalletByName} style={styles.buttonSecondary}>üì• Load Wallet</button>
      </section>

      {walletData && (
        <div style={styles.walletBox}>
          <p><strong>üë§ Name:</strong> {walletData.name}</p>
          <p><strong>üè¶ Address:</strong> {walletData.address}</p>
          <p><strong>üîë Private Key:</strong> {walletData.privateKey}</p>
          <p><strong>üí∞ BNB Balance:</strong> {balance !== null ? `${balance} BNB` : "Loading..."}</p>

          <button onClick={() => fetchBalance(walletData.privateKey)} style={styles.buttonSecondary}>üîÑ Refresh Balance</button>

          <h4>üöÄ Send Tokens</h4>
          <select value={tokenType} onChange={(e) => { setTokenType(e.target.value); setTokenBalance("N/A"); }} style={styles.input}>
            <option value="BNB">BNB</option>
            <option value="USDC">USDC</option>
          </select>

          {tokenType === "USDC" && (
            <>
              <p>üíº USDC Balance: {tokenBalance}</p>
              <button onClick={fetchTokenBalance} style={styles.buttonSecondary}>üîç Check USDC Balance</button>
            </>
          )}

          <input
            placeholder="üì§ Recipient Address"
            value={recipient}
            onChange={(e) => setRecipient(e.target.value)}
