          />
          <button onClick={sendTokens} style={styles.buttonTertiary}>üöÄ Send</button>

          <hr />

          <h4>üì¶ Transaction Details</h4>
          <input
            placeholder="üîç Enter Tx Hash"
            value={txHashInput}
            onChange={(e) => setTxHashInput(e.target.value)}
            style={styles.input}
          />
          <button onClick={fetchTransactionDetails} style={styles.buttonPrimary}>üîç View Tx Info</button>

          {txDetails && (
            <div style={{ marginTop: "15px", fontSize: "14px", background: "#333", padding: "15px", borderRadius: "8px" }}>
              <p><strong>From:</strong> {txDetails.from}</p>
              <p><strong>To:</strong> {txDetails.to}</p>
              <p><strong>Hash:</strong> {txDetails.hash}</p>
              <p><strong>Block:</strong> {txDetails.blockNumber}</p>
              <p><strong>Status:</strong> {txDetails.status === 1 ? "‚úÖ Success" : "‚ùå Failed"}</p>
              <p><strong>Gas Used:</strong> {txDetails.gasUsed?.toString()}</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
