// App.js (Your code, updated with friend's features and your UI)

import React, { useState, useEffect, useRef } from "react";
import {
  Wallet,
  isAddress,
  parseEther,
  formatEther,
  JsonRpcProvider,
  Contract,
  parseUnits,
  formatUnits,
  Interface, // <-- ADDED: For decoding transaction data
} from "ethers";
import { QRCodeCanvas } from "qrcode.react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

// Your existing constants - no changes
const ERC20_ABI = [
  "function balanceOf(address) view returns (uint256)",
  "function transfer(address to, uint256 value) returns (bool)",
  "function decimals() view returns (uint8)",
  "function symbol() view returns (string)",
  "event Transfer(address indexed from, address indexed to, uint256 value)", // <-- ADDED: Event for decoding
];
const provider = new JsonRpcProvider("https://bsc-testnet-dataseed.bnbchain.org");
const TOKEN_ADDRESSES = {
  USDT: "0x787A697324dbA4AB965C58CD33c13ff5eeA6295F",
  USDC: "0x342e3aA1248AB77E319e3331C6fD3f1F2d4B36B1",
};

function App() {
  // Your existing state variables - no changes
  const [walletData, setWalletData] = useState(null);
  const [walletName, setWalletName] = useState("");
  const [searchName, setSearchName] = useState("");
  const [recipient, setRecipient] = useState("");
  const [amount, setAmount] = useState("");
  const [tokenType, setTokenType] = useState("BNB");
  const [balances, setBalances] = useState({ BNB: "Loading...", USDT: "N/A", USDC: "N/A" });
  const [txHashInput, setTxHashInput] = useState("");
  const [txDetails, setTxDetails] = useState(null);
  const [ledger, setLedger] = useState([]);
  const [loading, setLoading] = useState({});
  const [showPrivate, setShowPrivate] = useState(false);
  const [showQR, setShowQR] = useState(true);
  const qrRef = useRef();

  // --- ADDED: State for new features from friend's code ---
  const [showHistory, setShowHistory] = useState(false);
  const [globalLedger, setGlobalLedger] = useState([]);


  // Your existing helper functions - no changes
  const setLoader = (key, value) => setLoading((prev) => ({ ...prev, [key]: value }));
  const notify = (msg, type = "info") => toast[type](msg);

  // Your existing wallet functions - no changes needed
  const generateAndSaveWallet = async () => {
    if (!walletName.trim()) return notify("Enter a wallet name", "error");
    setLoader("creating", true);
    const wallet = Wallet.createRandom();
    const newWallet = { name: walletName, address: wallet.address, privateKey: wallet.privateKey, mnemonic: wallet.mnemonic.phrase };
    try {
      const res = await fetch("http://localhost:5001/api/wallet", {
        method: "POST", headers: { "Content-Type": "application/json" }, body: JSON.stringify(newWallet),
      });
      const data = await res.json();
      if (res.ok) { notify("âœ… Wallet saved!", "success"); setWalletName(""); } 
      else { notify(`âŒ ${data.error || 'Save failed'}`, "error"); }
    } catch { notify("âŒ Save error", "error"); } 
    finally { setLoader("creating", false); }
  };

  const fetchWalletByName = async () => {
    if (!searchName.trim()) return notify("Enter wallet name to fetch", "error");
    setLoader("fetching", true);
    setTxDetails(null); // Clear previous tx details
    setShowHistory(false); // Hide history on new wallet load
    try {
      const res = await fetch(`http://localhost:5001/api/wallet/${searchName}`);
      const data = await res.json();
      if (data.error) {
        notify("âŒ Wallet not found", "error");
        setWalletData(null);
        setLedger([]);
      } else {
        setWalletData(data);
        fetchAllBalances(data.privateKey, data.address);
        fetchLedger(data.address);
      }
    } catch { notify("âŒ Error fetching wallet", "error"); } 
    finally { setLoader("fetching", false); }
  };

  const fetchAllBalances = async (privateKey, address) => {
    // ... no changes to this function ...
    setLoader("balance", true);
    try {
      const wallet = new Wallet(privateKey, provider);
      const bnb = await provider.getBalance(wallet.address);
      const tokenBalances = await Promise.all(
        Object.entries(TOKEN_ADDRESSES).map(async ([symbol, addr]) => {
          const tokenContract = new Contract(addr, ERC20_ABI, wallet);
          const decimals = await tokenContract.decimals();
          const bal = await tokenContract.balanceOf(wallet.address);
          return [symbol, formatUnits(bal, decimals)];
        })
      );
      const all = Object.fromEntries(tokenBalances);
      setBalances({ BNB: formatEther(bnb), ...all });
    } catch (e) {
      console.error(e);
      setBalances({ BNB: "Error", USDT: "N/A", USDC: "N/A" });
    } finally {
      setLoader("balance", false);
    }
  };

  const sendTokens = async () => {
    // ... no changes to this function ...
    if (!walletData?.privateKey || !isAddress(recipient) || !amount || parseFloat(amount) <= 0)
      return notify("âŒ Invalid inputs", "error");
    setLoader("sending", true);
    try {
      const wallet = new Wallet(walletData.privateKey, provider);
      const bnbBalance = await provider.getBalance(wallet.address);
      if (parseFloat(formatEther(bnbBalance)) < 0.001) return notify("âŒ Not enough BNB for gas", "error");
      let tx;
      if (tokenType === "BNB") {
        tx = await wallet.sendTransaction({ to: recipient, value: parseEther(amount) });
      } else {
        const tokenContract = new Contract(TOKEN_ADDRESSES[tokenType], ERC20_ABI, wallet);
        const decimals = await tokenContract.decimals();
        tx = await tokenContract.transfer(recipient, parseUnits(amount, decimals));
      }
      await tx.wait();
      notify(`âœ… ${tokenType} Sent!`, "success");
      navigator.clipboard.writeText(tx.hash);
      logTransaction(tx.hash, walletData.address, recipient, amount, tokenType);
      setRecipient("");
      setAmount("");
      fetchAllBalances(walletData.privateKey, walletData.address);
    } catch (e) {
      console.error(e);
      notify("âŒ Transaction failed", "error");
    } finally {
      setLoader("sending", false);
    }
  };

  const logTransaction = async (hash, from, to, amount, token) => {
    // ... no changes to this function ...
    try {
      await fetch("http://localhost:5001/api/ledger", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ walletAddress: from, hash, from, to, amount, token }),
      });
      fetchLedger(from); // Refresh personal ledger
      fetchGlobalLedger(); // Refresh global ledger
    } catch (error) {
      console.error("Failed to log transaction:", error);
      notify("Could not save Tx to ledger", "warn");
    }
  };

  const fetchLedger = async (address) => {
    // ... no changes to this function ...
    setLoader("ledger", true);
    try {
      const res = await fetch(`http://localhost:5001/api/ledger/${address}`);
      const data = await res.json();
      setLedger(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error("Failed to fetch ledger:", error);
      setLedger([]);
    } finally {
      setLoader("ledger", false);
    }
  };

  // --- UPDATED: This function is now much more powerful, like your friend's ---
  const fetchTransactionDetails = async () => {
    if (!txHashInput) return;
    setLoader("tx", true);
    setTxDetails(null);
    try {
        const tx = await provider.getTransaction(txHashInput);
        const receipt = await provider.getTransactionReceipt(txHashInput);
        if (!tx || !receipt) throw new Error("Transaction not found or not yet mined.");

        // This is the powerful decoding logic from your friend's code
        const iface = new Interface(ERC20_ABI);
        const decodedLogs = receipt.logs.map(log => {
            try {
                // Find which token this log belongs to
                const tokenSymbol = Object.keys(TOKEN_ADDRESSES).find(
                    key => TOKEN_ADDRESSES[key].toLowerCase() === log.address.toLowerCase()
                );
                if (!tokenSymbol) return null;

                const parsedLog = iface.parseLog(log);
                return {
                    ...parsedLog,
                    tokenSymbol,
                    // We need decimals for formatting, let's hardcode them based on our tokens
                    // A better approach would be fetching them, but this is fine.
                    formattedAmount: formatUnits(parsedLog.args.value, 18) 
                };
            } catch { return null; }
        }).filter(log => log !== null);

        setTxDetails({ ...tx, ...receipt, decodedLogs });

    } catch (e) {
        console.error(e);
        notify(`âŒ ${e.message || "Invalid or failed to fetch"}`, "error");
    } finally {
        setLoader("tx", false);
    }
  };

  // --- ADDED: New function to get the global ledger ---
  const fetchGlobalLedger = async () => {
    setLoader("globalLedger", true);
    try {
      const res = await fetch("http://localhost:5001/api/ledger");
      const data = await res.json();
      setGlobalLedger(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error("Failed to fetch global ledger:", error);
      setGlobalLedger([]);
    } finally {
      setLoader("globalLedger", false);
    }
  };

  // ADDED: Fetch global ledger on initial page load
  useEffect(() => {
    fetchGlobalLedger();
  }, []);

  // Your existing copy and download functions - no changes
  const copyAddress = () => {
    if (walletData?.address) {
      navigator.clipboard.writeText(walletData.address);
      notify("ðŸ“‹ Address copied", "info");
    }
  };
  const downloadQR = () => {
    const canvas = qrRef.current.querySelector("canvas");
    const pngUrl = canvas.toDataURL("image/png");
    const a = document.createElement("a");
    a.href = pngUrl;
    a.download = `${walletData.name}_qr.png`;
    a.click();
  };


  return (
    <div style={styles.container}>
      <ToastContainer position="top-right" autoClose={3000} hideProgressBar={false} />
      <h1 style={styles.title}>ðŸ¦œ Goan Wallet</h1>

      {/* --- Your existing Create and Load sections - no changes --- */}
      <section style={styles.section}>
        <input placeholder="ðŸ–Š Create wallet name" value={walletName} onChange={(e) => setWalletName(e.target.value)} style={styles.input} />
        <button onClick={generateAndSaveWallet} style={styles.buttonGreen}>{loading.creating ? "â³ Saving..." : "âœ… Generate & Save"}</button>
      </section>
      <section style={styles.section}>
        <input placeholder="ðŸ” Load wallet by name" value={searchName} onChange={(e) => setSearchName(e.target.value)} style={styles.input} />
        <button onClick={fetchWalletByName} style={styles.buttonBlue}>{loading.fetching ? "â³ Loading..." : "ðŸ“¥ Load Wallet"}</button>
      </section>

      {/* --- Your existing Wallet Details section - with updates --- */}
      {walletData && (
        <div style={styles.walletBox}>
          {/* ... your existing name, address, private key display ... */}
          <h2>{walletData.name}</h2>
          <p onClick={copyAddress} style={{ cursor: "pointer" }}><strong>ðŸ¦ Address:</strong> {walletData.address} ðŸ“‹</p>
          <p><strong>ðŸ”‘ Private Key:</strong>{" "}{showPrivate ? walletData.privateKey : "â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"}
            <button onClick={() => setShowPrivate(!showPrivate)} style={styles.toggle}>{showPrivate ? "ðŸ™ˆ Hide" : "ðŸ‘ Show"}</button>
          </p>

          {/* ... your existing balances display ... */}
          <h3>ðŸ“Š Balances</h3>
          <ul style={{ listStyle: "none", padding: 0 }}>
