import React, { useState } from "react";
import {
  Wallet,
  isAddress,
  JsonRpcProvider,
  Contract,
  formatEther,
  parseUnits,
} from "ethers";
import { QRCodeCanvas } from "qrcode.react";
import axios from "axios";

const provider = new JsonRpcProvider("https://data-seed-prebsc-1-s1.binance.org:8545/");

const ERC20_ABI = [
  "function balanceOf(address) view returns (uint)",
  "function transfer(address to, uint amount) returns (bool)",
  "function decimals() view returns (uint8)",
];

const tokens = {
  BNB: { name: "BNB", address: null },
  USDT: { name: "USDT", address: "0x9ac64cc6e4415144c455bd8e4837fea55603e5c3" }, // testnet USDT
  USDC: { name: "USDC", address: "0x64544969ed7EBf5f083679233325356EbE738930" }, // testnet USDC
};

export default function App() {
  const [walletData, setWalletData] = useState(null);
  const [recipient, setRecipient] = useState("");
  const [amount, setAmount] = useState("");
  const [tokenType, setTokenType] = useState("BNB");
  const [loader, setLoader] = useState(false);
  const [balances, setBalances] = useState({ BNB: "0", USDT: "0", USDC: "0" });

  const notify = (msg) => alert(msg);

  const generateWallet = async () => {
    const wallet = Wallet.createRandom();
    setWalletData(wallet);
    await fetchAllBalances(wallet.address);
  };

  const fetchAllBalances = async (address) => {
    const bnbBalance = await provider.getBalance(address);
    const balances = { BNB: formatEther(bnbBalance) };

    for (const [key, token] of Object.entries(tokens)) {
      if (!token.address) continue;
      const contract = new Contract(token.address, ERC20_ABI, provider);
      const decimals = await contract.decimals();
      const bal = await contract.balanceOf(address);
      balances[key] = Number(bal) / 10 ** decimals;
    }

    setBalances(balances);
  };

  const handleTransfer = async () => {
    if (!walletData || !isAddress(recipient)) return notify("Invalid input");

    try {
      setLoader(true);
      const wallet = walletData.connect(provider);

      let tx;
      if (tokenType === "BNB") {
        tx = await wallet.sendTransaction({
          to: recipient,
          value: parseUnits(amount, "ether"),
        });
      } else {
        const token = tokens[tokenType];
        const contract = new Contract(token.address, ERC20_ABI, wallet);
        const decimals = await contract.decimals();
        tx = await contract.transfer(recipient, parseUnits(amount, decimals));
      }

      const receipt = await tx.wait();
      notify(`${tokenType} Sent! TX Hash: ${receipt.transactionHash}`);

      await axios.post("http://localhost:5000/log", {
        sender: walletData.address,
        recipient,
        token: tokenType,
        amount,
        txHash: receipt.transactionHash,
      });

      setAmount("");
      await fetchAllBalances(walletData.address);
    } catch (err) {
      console.error(err);
      notify("Transfer Failed");
    } finally {
      setLoader(false);
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial", backgroundColor: "#f0f8ff", minHeight: "100vh" }}>
      <h2 style={{ color: "#333" }}>🌴 Goan Wallet</h2>
      {!walletData ? (
        <button onClick={generateWallet} style={{ padding: "10px 20px" }}>
          Generate Wallet
        </button>
      ) : (
        <>
          <p><strong>Address:</strong> {walletData.address}</p>
          <p><strong>Private Key:</strong> {walletData.privateKey}</p>
          <div style={{ display: "flex", gap: "40px", marginTop: "20px" }}>
            <div>
              <h4>BNB QR</h4>
              <QRCodeCanvas value={walletData.address} />
              <p>{balances.BNB} BNB</p>
            </div>
            <div>
              <h4>USDT QR</h4>
              <QRCodeCanvas value={walletData.address} />
              <p>{balances.USDT} USDT</p>
            </div>
            <div>
              <h4>USDC QR</h4>
              <QRCodeCanvas value={walletData.address} />
              <p>{balances.USDC} USDC</p>
            </div>
          </div>

          <hr />
          <h3>Transfer</h3>
          <select onChange={(e) => setTokenType(e.target.value)} value={tokenType}>
            {Object.keys(tokens).map((t) => (
              <option key={t} value={t}>{t}</option>
            ))}
          </select>
          <input
            placeholder="Recipient Address"
            value={recipient}
            onChange={(e) => setRecipient(e.target.value)}
            style={{ display: "block", margin: "10px 0", width: "300px" }}
          />
          <input
            placeholder="Amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            style={{ display: "block", marginBottom: "10px", width: "300px" }}
          />
          <button onClick={handleTransfer} disabled={loader}>
            {loader ? "Sending..." : "Send"}
          </button>
        </>
      )}
    </div>
  );
}
