import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import tokenABI from './tokenABI.json'; // Make sure this file contains the standard ERC20 ABI

const App = () => {
  const [walletName, setWalletName] = useState('');
  const [wallets, setWallets] = useState({});
  const [activeWallet, setActiveWallet] = useState(null);
  const [bnbBalance, setBnbBalance] = useState('0');
  const [tokenBalance, setTokenBalance] = useState('0');
  const [recipient, setRecipient] = useState('');
  const [amount, setAmount] = useState('');
  const [transactionHash, setTransactionHash] = useState('');
  const [txDetails, setTxDetails] = useState(null);
  const [selectedToken, setSelectedToken] = useState('0x787A697324db4AAB'); // Replace with your ERC20

  const provider = new ethers.JsonRpcProvider("https://data-seed-prebsc-1-s1.binance.org:8545");

  // 🔐 Load from localStorage
  useEffect(() => {
    const savedWallets = localStorage.getItem('wallets');
    if (savedWallets) setWallets(JSON.parse(savedWallets));
  }, []);

  // 🏦 Generate wallet
  const generateWallet = async () => {
    const wallet = ethers.Wallet.createRandom();
    const walletData = {
      address: wallet.address,
      privateKey: wallet.privateKey,
      mnemonic: wallet.mnemonic.phrase,
    };

    const updated = { ...wallets, [walletName]: walletData };
    setWallets(updated);
    localStorage.setItem('wallets', JSON.stringify(updated));
    alert(`Wallet "${walletName}" created.`);
  };

  // 📥 Load selected wallet
  const loadWallet = async () => {
    const data = wallets[walletName];
    if (!data) return alert('Wallet not found');
    const wallet = new ethers.Wallet(data.privateKey, provider);
    setActiveWallet(wallet);
    fetchBNBBalance(wallet.address);
    fetchTokenBalance(wallet);
  };

  const fetchBNBBalance = async (address) => {
    const balance = await provider.getBalance(address);
    setBnbBalance(ethers.formatEther(balance));
  };

  const fetchTokenBalance = async (wallet) => {
    const token = new ethers.Contract(selectedToken, tokenABI, provider);
    const bal = await token.balanceOf(wallet.address);
    setTokenBalance(ethers.formatUnits(bal, 18));
  };

  // 🚀 Token Transfer
  const sendTokens = async () => {
    if (!activeWallet) return alert("Load a wallet first");
    try {
      const token = new ethers.Contract(selectedToken, tokenABI, activeWallet);
      const tx = await token.transfer(recipient, ethers.parseUnits(amount, 18));
      await tx.wait();
      alert("✅ Token sent!");
      setTransactionHash(tx.hash);
    } catch (err) {
      console.error(err);
      alert(`❌ Transaction failed\n${err.message}`);
    }
  };

  // 🔎 View transaction
  const viewTransaction = async () => {
    try {
      const tx = await provider.getTransaction(transactionHash);
      setTxDetails(tx);
    } catch (err) {
      console.error(err);
      alert('Invalid transaction hash');
    }
  };

  return (
    <div style={{ padding: 20, background: '#111', color: 'white', fontFamily: 'monospace' }}>
      <h2>🪙 Goan Wallet</h2>
      <input placeholder="Wallet name" onChange={(e) => setWalletName(e.target.value)} />
      <button onClick={generateWallet}>Generate Wallet</button>
      <button onClick={loadWallet}>Load Wallet</button>

      {activeWallet && (
        <div style={{ marginTop: 20 }}>
          <h4>📛 {walletName}</h4>
          <p>📮 {activeWallet.address}</p>
          <p>💰 BNB: {bnbBalance}</p>
          <button onClick={() => fetchBNBBalance(activeWallet.address)}>Refresh BNB</button>

          <hr />
          <h4>🚀 Send Tokens</h4>
          <select onChange={(e) => setSelectedToken(e.target.value)} value={selectedToken}>
            <option value="0x787A697324db4AAB">ERC20</option>
            {/* Add more token addresses if needed */}
          </select>
          <p>Balance: {tokenBalance}</p>
          <input placeholder="Recipient" onChange={(e) => setRecipient(e.target.value)} />
          <input placeholder="Amount" onChange={(e) => setAmount(e.target.value)} />
          <button onClick={sendTokens}>Send</button>

          <hr />
          <h4>📜 View Tx Details</h4>
          <input placeholder="Transaction Hash" onChange={(e) => setTransactionHash(e.target.value)} />
          <button onClick={viewTransaction}>View</button>

          {txDetails && (
            <pre>{JSON.stringify(txDetails, null, 2)}</pre>
          )}
        </div>
      )}
    </div>
  );
};

export default App;
