import React, { useState, useEffect } from "react";

function App() {
  const [account, setAccount] = useState(null);
  const [error, setError] = useState("");

  // Request account access
  const connectWallet = async () => {
    if (window.ethereum) {
      try {
        const accounts = await window.ethereum.request({
          method: "eth_requestAccounts",
        });
        setAccount(accounts[0]);
        setError("");
      } catch (err) {
        setError("ðŸ›‘ User rejected connection");
      }
    } else {
      setError("ðŸ¦Š MetaMask not installed!");
    }
  };

  // Watch for account change
  useEffect(() => {
    if (window.ethereum) {
      window.ethereum.on("accountsChanged", (accounts) => {
        setAccount(accounts[0]);
      });
    }
  }, []);

  return (
    <div style={{ padding: 30, background: "#111", color: "#fff", fontFamily: "sans-serif" }}>
      <h2>ðŸ¦œ Goan Wallet</h2>

      <button onClick={connectWallet} style={{ padding: "10px 20px", background: "#f6851b", border: "none", color: "#000", cursor: "pointer" }}>
        Connect MetaMask
      </button>

      <div style={{ marginTop: 20 }}>
        {account ? (
          <p>ðŸŸ¢ Connected Wallet: <strong>{account}</strong></p>
        ) : (
          <p>ðŸ”´ Not Connected</p>
        )}

        {error && <p style={{ color: "red" }}>{error}</p>}
      </div>
    </div>
  );
}

export default App;
